css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.xda-developers.com") {
p {
 color: #444 !important;
}
* {
 border: 0 !important;
}
a {
 color: #A44 !important;
 font-weight: 600;
}
.categorybox {
 padding: 0 !important;
}
img[title=report] {
 display: none !important; 
}
font[size="7"] {
  font-size: 19px !important;
}
blockquote {
 border: 5px solid #999 !important;
 background-color: #DEDEDE !important;
}
.carrier,
.flag,
.thread-options,
.post-info,
.post-options-retweet {
 visibility: hidden ;
}
.avatar {
 visibility: visible;
 opacity: 0.4;
min-height: 150px;
min-width: 150px;
z-index: -2;
margin-top: -80px !important;
}

.forumbox-header {
 background: none;
}
.posts, .categorybox, .container {
 width: 100% !important;
}
.post-author a, .post-author a:visited {
 font-size: 25px !important;
 color: #222 !important;
opacity: 0.4;
}
.post {
 background-color: white !important;
 margin: 0 !important;
 padding: 0 !important;
}
.post-content {
 min-height: 80px !important;
}
.post-content div {
 display: none !important;
}
.post-content .post-message {
 display: block !important;
}
.post-header {
 background-color: #FFF !important;
border-top: #AAA 2px solid !important;
}
.post-date {
 padding-left: 280px !important;
 font-size: 1.3em !important;
 opacity: 0.5;
}
}
