css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://gmod.de"), url-prefix("https://gmod.de"), domain("http://gmod.de"),domain("https://gmod.de") 
{

.toplinks .logindetails {
background: none repeat scroll 0 0 #5B5B5B!important;
}

.blockhead{
background: url("http://gmod.de/images/buttons/newbtn_middle.png") repeat-x scroll 0 0 #5B5B5B !important;
    border: 1px solid #424242 !important;

}

.posthead {
    background: url("http://gmod.de/images/buttons/newbtn_middle.png") repeat-x scroll 0 0 #5B5B5B !important;
    border: 1px solid #424242 !important;
}

.forumhead {
    background: url("http://gmod.de/images/buttons/newbtn_middle.png") repeat-x scroll 0 0 #424242 !important;
    border: 1px solid #424242 !important;
}

.newcontent_textcontrol{
    background: url("http://gmod.de/images/buttons/newbtn_middle.png") repeat-x scroll left top #0088D6 !important;
-webkit-transition: all 0.5s ease-in-out; 
-moz-transition: all 0.5s ease-in-out; 
-o-transition: all 0.5s ease-in-out;
opacity:0.5;
}

.newcontent_textcontrol:hover{
    background: url("http://gmod.de/images/buttons/newbtn_middle.png") repeat-x scroll left top #0088D6 !important;
opacity:1;
}

.postuseravatar img{
-webkit-transition: all 0.5s ease-in-out; 
-moz-transition: all 0.5s ease-in-out; 
-o-transition: all 0.5s ease-in-out;

}

.postuseravatar img:hover{
    -webkit-transform: rotate(7deg) scale(0.8); 
    -moz-transform: rotate(7deg) scale(0.8); 
    -o-transform: rotate(7deg) scale(0.8); 
}

#quick_reply{
-webkit-transition: all 0.2s ease-in-out; 
-moz-transition: all 0.2s ease-in-out; 
-o-transition: all 0.2s ease-in-out;
opacity:0.5;
}
#quick_reply:hover{
opacity:1;
}

.textcontrols{
-webkit-transition: all 0.2s ease-in-out; 
-moz-transition: all 0.2s ease-in-out; 
-o-transition: all 0.2s ease-in-out;
opacity:0.5;
}

.textcontrols:hover{
opacity:1;
}

.pagination span a{
-webkit-transition: all 0.2s ease-in-out; 
-moz-transition: all 0.2s ease-in-out; 
-o-transition: all 0.2s ease-in-out;
opacity:0.5;
border: 1px solid #2982c1!important;
color: #626262!important;
}

.pagination span a:hover{
opacity:1.0;
}


.pagination span.selected a {
background:  0 0 #2982c1!important;

color: #FFFFFF!important;
}



.toplinks ul.isuser li a{
-webkit-transition: all 0.2s ease-in-out; 
-moz-transition: all 0.2s ease-in-out; 
-o-transition: all 0.2s ease-in-out;

}


.toplinks ul.isuser li a:hover{
background: none repeat scroll 0 0 #727272!important;
}


.navtabs li a.navtab:hover {
-webkit-transition: all 0.2s ease-in-out; 
-moz-transition: all 0.2s ease-in-out; 
-o-transition: all 0.2s ease-in-out;

}

.navtabs li a:hover {
-webkit-transition: all 0.1s ease-in-out; 
-moz-transition: all 0.1s ease-in-out; 
-o-transition: all 0.1s ease-in-out;
}

.navtabs .popupbody {
-webkit-transition: all 0.1s ease-in-out; 
-moz-transition: all 0.1s ease-in-out; 
-o-transition: all 0.1s ease-in-out;
background: none repeat scroll 0 0 #009bdb!important;
    border: 1px solid #009bdb!important;
    width: auto;
}

.navtabs li.selected li:hover a.popupctrl.active, .navtabs li.selected li a.popupctrl.active, .navtabs li.selected li a.popupctrl:hover, .toplinks .notifications a.popupctrl, .toplinks .notifications a.popupctrl:hover, .toplinks .nonotifications a.popupctrl:hover, .toplinks .nonotifications a.popupctrl.active, .toplinks .notifications a.popupctrl.active, .navtabs li.selected li:hover a.popupctrl {

background: url("http://gmod.de/images/misc/arrow.png") no-repeat scroll right center #009bdb!important;
border-color: #009bdb!important;
}

.navtabs .popupbody li {
border-top: 1px solid #a0a0a0!important;

}

.threadbit .sticky{
background: url("http://gmod.de/images/gradients/grey-up.png") repeat-x scroll left bottom #E8F4FF!important;

}

#vsa_fh_stats{
-webkit-transition: all 0.1s ease-in-out; 
-moz-transition: all 0.1s ease-in-out; 
-o-transition: all 0.1s ease-in-out;
}

.postbitlegacy .postdetails, .eventbit .eventdetails {

background:-moz-radial-gradient(left, #fafafa ,#fff2e7)!important;; 

}

.postbitlegacy .userinfo{
background:none!important;
}

}
