css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('www.karachan.org')

{

html, body {
        overflow-x: hidden; 
    
	font-family: "Times New Roman" !important;
	background: url('http://i52.tinypic.com/23kviq9.png') no-repeat fixed !important; 
	background-position: bottom right !important;

	color: #3B4990 !important;
	background-color: #f0e0f0 !important;

}

a {
        text-decoration: none !important; 
	color:#9900CC !important;
}

.reply, blockquote {
        padding: 10px !important;
        border: 0px !important;
        border-radius: 10px !important;
	background-color: rgba(230,196,230,1) !important;
        color: #000000 !important;
}

.postername {
	color:#3B4990 !important;
}

.postblock {
        text-align: center;
	background-color:#c77ac7 !important;
	color:#3B4990 !important;

}

.reflinkpreview { 
    border: 0 !important;
    background-color: #f0e0f0 !important; 
    border-radius: 10px !important;
} 

.unkfunc {
        text-decoration: none !important; 
	color:#9900CC !important;
}

.filesize {
        text-decoration: none !important; 
	background:inherit !important;
	font-size:15px !important;
	font-family:serif !important;
	color:#3B4990 !important;
	font-weight:800 !important;
}

.replymode {
	background-color:#c77ac7 !important;
}

.highlight {
	background: rgba(230,196,230,1) !important;
	border: 2px solid #9900CC !important;
        border-radius: 10px !important;
	color: #3B4990 !important;

}

#watchedthreads { 
	background: rgba(230,196,230,1) !important;
        border: 0px !important;
        border-radius: 10px !important;
}

limg.thumb {
	box-shadow: 8px 6px 10px rgba(230,196,230,1) !important;
}

hr {
  border: 1px !important;
  height: 2px !important; 
  	background-color:#c77ac7 !important;
	background: none;
}

div.navbar {
   font-size: 13px !important;
}

}
