css:
@namespace url(http://www.w3.org/1999/xhtml);
     
@-moz-document domain('www.karachan.org')
     
{
     
    html, body {
            font-size:13px !important;
            font-family:"Tahoma" !important;
            background: url('http://i55.tinypic.com/35852d3.png') no-repeat fixed !important;
            background-position: bottom right !important;
     
            color: #000000 !important;
            background-color: #FFFFC8 !important;
     
    }
     
    a {
        text-decoration: none !important;
            font-weight: bold !important;
            color:#DB00DB !important;
    }
     
    .reply {
            padding: 10px !important;
            border: 2px solid #DB7BAC !important;
            border-radius: 10px !important;
                    background-color: #F8B9CE !important;
            color: #000000 !important;
    }
     
    .postername {
            color:#DB00DB !important;
    }
     
    .postblock {
            background-color:#DB7BAC !important;
            color:#000000 !important;
            border-radius: 5px !important;
    }
     
    .reflinkpreview {
        border: 0 !important;
        background-color: #FFFFC8 !important;
        border-radius: 10px !important;
    }
     
    .unkfunc {
            font-weight: bold !important;
            color:#FF4AFF !important;
    }
     
    .filesize {
        font-family:"Tahoma" !important;
       
            background:inherit !important;
            font-size:15px !important;
            color:#872187 !important;
            font-weight:800 !important;
    }
     
    .replymode {
            background-color:#DB7BAC !important;
    }
     
    .highlight {
            background: #F8B9CE !important;
            border: 4px dotted #DB7BAC !important;
            border-radius: 10px !important;
            color: #000000 !important;
     
    }
     
    #watchedthreads {
            background: #F8B9CE !important;
            border: 0px !important;
            border-radius: 10px !important;
    }
     
    div.navbar {
            font-size: 11px !important;
    }
     
    hr {
            border: 0 !important;
            height: 18px;
            background: url('http://i55.tinypic.com/245h9q9.png') scroll;
    }
}
