}
.chip, .ca, dd [class^="evt-lk"], [class*="evt-lk"] {
font: 11px Segoe UI !important;
font-weight: 500 !important;
}
.evt-lk:hover {
text-decoration: none !important;
}
div.resizer {opacity:0.1 !important;}
div.mask {display:none !important;}

.st-ad-ml, .st-ad-mr {
