css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document url-prefix(http://hup.hu) {
  body,
  #all,
  #top-nav,
  #top-nav a,
  .sidebar .block .content,
  #footer,
  .node .links {
    font-size: 12px !important;
  }
  .main-content a:link,
  .main-content a:visited { 
    border-bottom: 1px dotted #999 !important;
    text-decoration: none !important;
  }
  .main-content a:hover {
    border-bottom: 1px solid #006699 !important;
    text-decoration: none !important;
  }
  .main-content .content td a:link,
  .main-content .content td a:visited,
  .main-content .content td a:hover  {
    border-bottom: 0 !important;
  }
  .main-content code, 
 .main-content pre {
/* this is a background image */
   background: #D9D9D9 url(data:image/png;base64,) repeat scroll 0 0 !important;
    color: #000;
    border: 1px solid #A5A5A5;
    font-family: Monaco, monospace !important;
    font-size: 13px;
    padding: 3px !important;
  }
  .main-content blockquote,
  .main-content q {
    font-style: italic !important;
  }
  .comment {
    max-width: 100% !important;
  }
  .block-button, .block-move-button {
    top: 2px !important;
  }
  .pager .pager-current,
  .pager a,
  .pager a:link,
  .pager a:visited { 
    background-color:#F0F0E4 !important;
    display:inline-block !important;
    border: 1px solid #999 !important;
    margin-right: 1em !important;
    margin-top: 1em !important;
    padding: 2px 5px !important;
  }
  .pager a:hover {
     background-color: #D4D4CA !important;
     color: #000 !important;
     border-color: #000 !important;
   }
}
