css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.forocoches.com") {

/* Menu lateral */
td[width="152"] { display: none!important; }

/* Basurilla y espaciados generales*/
body > table { display: none!important; }

/* Publicidad superior de foros */
table#table5 { display: none!important; }
table[style="margin-bottom:10px"] { display: none!important; }

/* Muestro barra navegacion */
body > table[class="cajasprin"]:nth-of-type(3) { display: block !important;} 

/* ***************** Fuentes******************* */

body, .navbar, .smallfont, td, th, p, li, b {
    /*font: 11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;*/
    font-family: 'Segoe UI','Lucida Grande',Verdana,Arial,Helvetica,sans-serif !important;
}

/* ***************** Colores ******************* */

/* Lista de Hilos */
#threadslist a:link {color: #666666 !important;}
#threadslist a:hover {color: #4895c7 !important;}

/* Color Fondo Lista de Hilos */
.alt2, .alt2Active {background-color: #E8E8E8 !important;}

/* Barra de navegación */
.navbar a:link, .navbar_notice a:link {color: #4895c7 !important;}
.navbar a:hover, .navbar_notice a:hover {color: #4895c7 !important;}

/* Links Inicio Sesión */
a:link, body_alink {color: #4895c7 !important;}
a:hover, a:active, body_ahover {color: #4895c7 !important;}

/* Ver primero sin leer */
.tcat a:link {color: #666666 !important;}
.tcat a:hover {color: #4895c7 !important;}

/* Navegador de páginas */
.pagenav a:link {color: #4895c7 !important;}
.pagenav a:hover {color: #4895c7 !important;}

/* Usuario */
a.bigusername:link {color: #4895c7 !important; font-weight: bold!important;}
a.bigusername:hover {color: #4895c7 !important; text-decoration:none !important;}

/* ***************** Bloques ******************* */

/* Color de bordes */
.tborder {
    border: none !important;
}

/* Barra de registro, Herramientas de navegacion de pagina e hilos */
.vbmenu_control {background: #4895c7 !important;}
.vbmenu_control a:link {color: #fff !important;}
.vbmenu_control a:hover {color: #fff!important;}

/* Ocultar título del foro */
#table3 { background: none !important; }
#table3 td { padding: 0 !important; margin: 0 !important;}
.cmega2 {font-size: 0pt !important;}

/* Ocultar creador post y reducir tamaño de imágenes */
span[style="cursor:pointer"] {display: none !important;}

/* ********************************************* */
}
