css:
/**********************************/
/* Hertha BSC          v1.0 skin  */
/* Date: 06/07/2011               */
/* Made by: Gizzly                */
/**********************************/

@-moz-document domain("hattrick.org") {

#page
{
	margin-top: 10px !important;
}
#top
{
	height: 0px !important;
	position: relative !important;
	z-index: 50 !important;
    background: #000000 !important;
}
#footer a
{
        color: white!important;
        font-weight: bold !important;
}
#ticker
{
	/*position: absolute !important;   
	top: 30px !important;
	left: 360px !important;*/
	width: 350px !important;
	margin-top: 20px !important;
	margin-left: 20px !important;
}
#ticker div a
{
	padding-bottom: 2px !important;
	color: white !important;
	text-decoration: none !important;
	line-height: 1.1em !important;
}
#shortcuts
{
	margin-top: 10px !important;
	margin-left:0px !important;
}
#teamLinks
{
    color: white !important;
    margin-top: 50px !important;
	margin-left: 180px !important;
}
div#teamLinks a
{
     color: white !important;
}
#ft_rapidid {  margin-top:50px !important; }
#ft_rapidid a { color: white !important;}
#ft_rapidid_form {margin-top:-5px !important; }
/*--Users online - week - date - time ----------------------------*/
div[id="online"] {color: white !important; margin-top: 50px !important;}
div[id="time"] {color: white !important; margin-top: 50px !important;}

/*--Page background-----------------------------------------------*/
body 
{
background: #005ba8 !important; /* Old browsers */
background: -moz-linear-gradient(top, #005ba8 0%, #ffffff 100%) !important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#005ba8), color-stop(100%,#ffffff)) !important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, #005ba8 0%,#ffffff 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, #005ba8 0%,#ffffff 100%) !important; /* Opera11.10+ */
background: -ms-linear-gradient(top, #005ba8 0%,#ffffff 100%) !important; /* IE10+ */
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#005ba8', endColorstr='#ffffff',GradientType=0 ) !important; /* IE6-9 */
background: linear-gradient(top, #005ba8 0%,#ffffff 100%) !important; /* W3C */

}

/*----centre title-------("Offizielle Hattrick-Mitteilungen")--------------*/
h1 
{
        color: #005ba8 !important;
        border-bottom: solid thin #005ba8!important;
}
/*-----all boxHead--------------------*/
h2 
{
        color: #fefefe !important;
        background-color: #005ba8 !important;
        border-radius: 4px 4px 0 0 !important;
}
/*-----centre news--------------------*/
.boxBody h2 
{ 
        color: #005ba8 !important;
        background: none !important;
        border-bottom: solid 1px #005ba8 !important;
}
/*-----------boxHeads----------------------------------------------------------------------------------------*/

.sidebarBox .boxHead .boxLeft, .subMenuBox .boxHead .boxLeft, .main .boxHead .boxLeft
{
        background: none !important;
}
.sidebarBox .boxHead, .subMenuBox .boxHead, .main .boxHead { background: none !important;}
.sidebarBox .boxFooter .boxLeft,.subMenuBox .boxFooter .boxLeft,.main .boxFooter .boxLeft{ background: none !important; }
.sidebarBox .boxBody,.subMenuBox .boxBody,.main .boxBody{ border-left: none !important; border-right: none !important; }

/*Left menu*/
.subMenuBox .boxHead
{
        color: black !important;
	border-radius: 5px 5px 0 0 !important;
}
.subMenuBox .boxHead .boxLeft
{
        color: black !important;
        }
/*Centre box*/
.main .boxHead h2 
{
        color: black !important;
}
/*Right menu*/
.sidebarBox h2 
{
        color: #fefefe !important;
}

.boxFooter
{
        background: white !important;
        border-radius: 0 0 4px 4px !important;
}
/*----- Press, GB, Team events ------------------------------------------------------------------------------*/
.main .mainBox h2 
{
	font-size: 14px !important;
	font-weight: bold !important;
	padding: 1px !important;
}
/*Central header paths*/
h2 a 
{
        color: #fefefe !important;
}
h2 a:hover 
{
        color: white !important;
}
/*News and team events date*/
.date
{
	color: #808080 !important;
	padding-right: 8px;
	font-weight: 400;
}
/*Left Menu titles*/
h3 
{
        color: #005ba8 !important;
        border-bottom: solid thin #005ba8 !important;
}
/*General Links*/
a
{
    color: #005ba8 !important;
	/*text-decoration: none !important;*/
}
/*----subMenu and sidebar links------------------------------------------------------------------------------*/
.subMenuBox a, .subMenuBox a
{
    /*text-decoration: none !important;*/
	color: black !important;
}
.sidebarBox a 
{
        text-decoration: underline !important;
	color: black !important;
}
/*Bot teams link*/
div a[class="shy"] 
{
        color: gray !important;
}
/*Hide Hattrick logo*/
#logo
{
        background: none !important;
}
/*Hide ticker's stars*/
#ticker div
{
        /*background: none !important;*/
}
/* central page background */
#content
{
	background: #ffffff !important; /* Old browsers */
background: -moz-linear-gradient(top, #ffffff 0%, #005ba8 100%) !important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffff), color-stop(100%,#005ba8)) !important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, #ffffff 0%,#005ba8 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, #ffffff 0%,#005ba8 100%) !important; /* Opera11.10+ */
background: -ms-linear-gradient(top, #ffffff 0%,#005ba8 100%) !important; /* IE10+ */
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#ffffff', endColorstr='#005ba8',GradientType=0 ) !important; /* IE6-9 */
background: linear-gradient(top, #ffffff 0%,#005ba8 100%) !important; /* W3C */
}
/*Image header (banner)*/
#header 
{
	height: 142px !important;
	/*padding-top: 20px !important;*/
	background: url("http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/HerthaBSC/header.png") repeat scroll 0 -15px transparent  !important;
	border-radius: 20px 20px 0 0 !important;
}
/*--------Top menu settings----------------------------------------------------------------------------------*/
#menu
{
	height: 30px !important;
	width: 981px !important;
	margin-top: 0px !important;
	margin-left: -14px !important;
	margin-bottom: -7px !important;
	padding-left: 0px !important;
	background: #ffffff !important;
	position: absolute !important;
}
#menu a
{
	line-height: 30px !important;  
    color: #005ba8!important;
}
div#menu a:hover 
{
        color: #ffffff!important; 
        background: #005ba8 !important;
}
a:hover
{
	color: black !important;
}
/*bottom white line*/
div[id="bottom"]
{
    background: #005ba8 !important;
	border-radius: 0 0 20px 20px !important;
}

/*---Aufstellung---------------------------------------------------------*/
div[class="name"] a       {color: #000000 !important;}
div[class="name"] a:hover {color: #005ba8 !important;}

/*---Forum---------------------------------------------------------------*/
div[class="boxBody"]   {background: white !important;}
div[class="message"] a {color: #005ba8 !important;}
div[class="signature"] a {color: #005ba8 !important;}

.cfHeader
{
	background: none repeat scroll 0 0 #005ba8 !important;
}
#cfPollHeader
{
	background: black !important;
	padding: 5px 10px;
	font-weight: bold;
        color: #ffffff;
}
.cfWrapper
{
	border: solid 1px #005ba8 !important;
	margin: 0 0 12px 0;
}
.cfWrapper a
{
        color: #fefefe !important;
}
.cfUserInfo a
{
        color: #005ba8 !important;
}
.folderHeader a:link 
{
	color: #D11E1A !important;
	font-weight: bold !important;
}
.cfFooter a
{ 
        color: black !important;
}
.folderItem div.ignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore:hover
{

}
/*Ads section*/
div[class="insertHeader"]
{

}
div[class="insertFooter"]
{
        background: #ffffff !important;
}
/*-------------Formatting buttons----------------------------------------------------------------------------*/

/*------Foxtrick popup-------*/
ul.ft-popup-list {background-color: black !important; border: solid 1px white !important; }
ul.ft-popup-list > li > a,  ul.ft-popup-list > li > span {	color: white !important; }
ul.ft-popup-list > li > a:hover {color: #005ba8 !important; background-color: #fefefe !important; }
ul.ft-popup-list > li:hover {background-color: #fefefe !important;}

/*------lineup-order-----*/
#order_tabs a {color:#fefefe !important;}
#order_tabs a:hover {color:#005ba8 !important;}
#orders .pos_abbr { color: #ffffff !important;}
#orders .position {border: 2px solid #FFFFFF !important;}
#orders .position .player, .player.ui-draggable-dragging {border: 2px solid #FFFFFF !important;}
/*#orders #players .player.used {background: none repeat scroll 0 0 #005BA8 !important;}*/
/*#orders #players .player.bench {background: none repeat scroll 0 0 #3e8fd4 !important;}*/
/*#orders .player {background: none repeat scroll 0 0 #005BA8 !important;}*/
#orders .helptext { color: #ffffff !important;}
.field
{
    background-image: url("http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/field_blue.png")  !important;
}

#tab_order 
{
    background-image: url("http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/field_blue.png")  !important;
}

#orders .position, #formations 
{
    background-color:transparent!important;
}


/*------MyOffice---------*/
.eventCalendarDate.selectedEventDate {background-color:#fefefe !important; border-color: #005ba8 !important; color: #005ba8 !important;}
.eventItem { background-color:#fefefe !important; border:1px solid #005ba8 !important;}
.eventItem > .eventItemText {color: #005ba8 !important;}


.alert, .edit, .kitEdit, .alert_top  {border: 1px solid #005ba8 !important; }
/*shortcuts*/
img.scNewMail 
{

}
}
