css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zahlungsverkehrsfragen.de") {
	* {
		font-family: helvetica, sans-serif;
	}
	body {
		background: #FFEEDD !important;
	}
	h1, h2, h3, h4, h5, h6 {
		color: white;
		background: navy;
		border: outset navy;
		padding-left: 1em;
		padding-right: 1em;
		padding-top: 0.5em;
		padding-bottom: 0.5em;

	}
}

@-moz-document url-prefix('http://www.zahlungsverkehrsfragen.de/menue') {
	body {
		background: navy !important;
		color: white !important;
	}
	h1, h2, h3, h4, h5, h6 {
		color: white;
		background: #AA0000;
	}
	a {
		color: yellow !important;
		text-decoration: none !important;
		font-weight: bold;
	}
}
