css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ubuntuforums.org")

{

html, body, tbody, tr, td, .vbclean_top *, .vbclean_topfg, .page, body_alink, .alt1, .alt1Active, .vbclean_navbar_bg, .smallfont, strong, .alt2, .alt2Active, .vblcean_navbar3, .tcat, .tborder, .vblcean_navbar *, .forumhome_stats, .vbclean_fhomecat *, .time, td.thead, th.thead, div.thead, .tfoot, .vbclean_bottom *, .vbclean_bottomfg, .vbclean_fhomecat_top *, .vbclean_forumdesc *, .stickybg, .vbclean_announce *, .pagenav td, .vbmenu_control, .vbclean_sticky *, .vbclean_postbitleg *, .vbclean_postbitmsg *, .alt4, .alt3, .vbclean_welcomemsg *, .vbmenu_option, .vbmenu_hilite, .panelsurround, .panel, .vbclean_postbitlegfg, .vbclean_postbitmsgfg, .vbclean_msgtext, .vbclean_newthread *, .vbclean_newthreadfg, .vBulletin_editor, .vbclean_fhomecat_topfg, .vbclean_fhomecatfg, .vbclean_announcefg, .vbclean_stickyfg, .pagebody, .posttop, #navbar, .posttext, legend

{
background: black !important;
border: black !important;
color: white !important;
}

select, button, h4.thead, ul.thead

{
background: black !important;
}

div, span, a

{
background-color: black !important;
}

legend, select, input, span, code, textarea, pre, label, div, dd

{
color: white !important;
}

/*links*/

a, a:link

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlightbackground*/


textarea, pre, input, code, .ubuntu_quotebackground, .vbclean_noticefg, .vbclean_notice *, .vbclean_welcomemsgfg


{
background: #333333 !important;
}

}
