css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hobius.com") {
input.bb, textarea.bb.new-comment-input.goog-textarea, textarea.bb.new-comment-input, span.button button, button.new-comment-button, div.tb.t2 input, textarea, input { border: 1px solid #aab!important;}
input:focus, textarea:focus {border: 1px solid #FF7B00!important;}
.misc { width: 144px!important; }
a.logo { visibility: hidden!important;}
span.button button, button.new-comment-button { cursor: pointer!important;}
span.button button:active, button.new-comment-button:active { font-size: 0.9em!important;}
span.star { color: #3a3!important; font-weight: bold!important;}
span.share.flink { color: #33a!important; font-weight: bold!important;}
span.flink.dislike { color: #a33!important; font-weight: bold!important;}
div.text p { font-size: 12px!important;}
img, entry, object { -webkit-box-shadow: 10px 10px 5px #AAA!important; -moz-box-shadow: 10px 10px 5px #AAA!important; box-shadow: 10px 10px 5px #AAA!important; margin-bottom: 12px!important; margin-right: 12px!important; position: relative!important; -webkit-transition: all 0.2s ease-in-out!important; -moz-transition: all 0.2s ease-in-out!important; -o-transition: all 0.2s ease-in-out!important; -ms-transition: all 0.2s ease-in-out!important; transition: all 0.2s ease-in-out!important;}
/*[[hoveract]]*/
em.drag { visibility: visible!important;}
div.main { width: 495px!important;}
div.beta, div.suggest div { display: none!important;}
.entry .poll-res em em { background: #DDF!important;}
button { -webkit-box-shadow: 3px 3px 2px #AAA!important; -moz-box-shadow: 3px 3px 2px #AAA!important; box-shadow: 3px 3px 2px #AAA!important; transition: all 0.2s ease-in-out!important; border-radius: 2px!important; background: #DDF!important;}
button:active { -webkit-box-shadow: 1px 1px 1px #AAA!important; -moz-box-shadow: 1px 1px 1px #AAA!important; box-shadow: 1px 1px 1px #AAA!important;}
span.button { height: 29px!important;}
.like b { color: #06C!important;}
.beta, .suggest, #vkshare0, .misc iframe{ display: none!important;}
.misc{left: 0!important;position: absolute!important;top: -25px!important;width:144px!important;}
.body{position:relative!important;width:777px!important;}
.footer{width:777px!important;}
#nav .active a, #nav .active a:hover{background-color:#4B8BCC!important}
}
