css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {
#navtop, #navbot {
    background :url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAGQCAYAAABvWArbAAAAQ0lEQVQ4je3PsQmAYBCD0eS7abRw%2F6luBS08BMFe%2BEnzSIoU0bYfJ5IUQliW7p4EDLZFVQ1gAa96JyP8LP4%2FE0L44gL5Awe6aDut%2FgAAAABJRU5ErkJggg%3D%3D) repeat-x scroll 50% 50% #121417 !important;
    color: #404040 !important;
    font-size: 8pt !important;
    height: 12pt;
    text-align: center;
    width: 100%;
}
#navtop *, #navbot * {
    
    color: #EEEEEE !important;
    display: inline;

}
#navtop a:hover, #navbot a:hover {
    background: none repeat scroll 0 0 #555555;
}
html, body {
    background: none repeat scroll 0 0 #182631 !important;
    color: #E2E2E2 !important;
    font-family: Tahoma !important;
    font-size: 11px !important;
}
td.reply {
    background-color: #071520 !important;
    border: 0 solid #8A2BE2 !important;
    color: #E2E2E2 !important;
    float: left !important;
    padding: 0.2em !important;
    position: relative !important;
    width: 300px !important;
}
td.replyhl {
    background-color: #555555 !important;
    border: 0 solid #555555 !important;
    border-radius: 3px 3px 3px 3px !important;
    color: #FFFFFF !important;
    padding: 0.2em !important;
    width: 100% !important;
}
a {
    color: #ACACAC !important;
    text-decoration: none !important;
}
label {
    color: #65718C !important;
}
.quotelink {
    color: #36D6D0 !important;
    font-size: 11px;
    font-weight: bold !important;
    text-decoration: none !important;
}
.postername, .commentpostername, .omittedposts, .abbr {
    color: #9CA9C5 !important;
}
.filetitle, .replytitle {
    color: #876ED7 !important;
    font-size: large;
}
.postertrip, .linkmail {
    color: #DADAA1 !important;
    text-decoration: none !important;
}
.filesize {
    color: #65718C !important;
}
.filesize a {
    color: #65718C !important;
    font-weight: bold !important;
    text-decoration: none !important;
}
.boxbar {
    background: none repeat scroll 0 0 #232327 !important;
    
    color: #dddddd !important;
    
}
.quotejs a {
    color: #232327 !important;
    font-size: 10px !important;
}
#navtop a {
    color: #D7FEFD !important;
}
#navtop {
    font-size: 11px;
    padding: 0.4em !important;
    position: fixed !important;
    text-align: center !important;
    top: 0;
    width: 100% !important;
}
.quotejs {
    color: #36D6D0 !important;
    font-size: 10px !important;
    font-weight: bold !important;
}
a:hover {
    color: #FFFFFF !important;
}
.reflink a:hover {
    font-weight: bold;
}
.adminbar {
    clear: both;
    float: right;
    text-align: right;
}
.logo {
    clear: both;
    color: #FFFFFF !important;
    font-size: 2em;
    text-align: center;
    width: 100%;
}
.replymode {
    background: none repeat scroll 0 0 #094CA8;
    color: #FFFFFF !important;
    padding: 2px;
    text-align: center;
    width: 100%;
}
.catalogmode {
    background: none repeat scroll 0 0 #0040E0;
    color: #FFFFFF !important;
    padding: 2px;
    text-align: center;
    width: 100%;
}
.rules {
    font-family: sans-serif;
    font-size: 10px;
    width: 468px;
}
.rules li {
    margin-left: 1em;
}
.postblock {
    background: none repeat scroll 0 0 #294052 !important;
    color: #EEEEEE !important;
    font-weight: 800;
    border:0px solid #000000 !important;
}
.footer {
    font-family: serif;
    font-size: 12px;
    text-align: center;
}
.passvalid {
    background: none repeat scroll 0 0 #EEAA88;
    color: #FFFFFF;
    text-align: center;
    width: 100%;
}
.dellist {
    font-weight: bold;
    text-align: center;
}
.delbuttons {
    padding-bottom: 4px;
    text-align: center;
}
.managehead {
    background: none repeat scroll 0 0 #AAAA66;
    color: #400000;
    padding: 0;
}
.postlists {
    background: none repeat scroll 0 0 #FFFFFF;
    color: #800000;
    padding: 0;
    width: 100%;
}
.row1 {
    background: none repeat scroll 0 0 #EEEECC;
    color: #800000;
}
.row2 {
    background: none repeat scroll 0 0 #DDDDAA;
    color: #800000;
}
.unkfunc {
    background: inherit;
    color: #789922;
}
.filesize {
    text-decoration: none;
}
.filetitle {
    background: inherit;
    font-size: 1.2em;
    font-weight: 800;
}
.postername {
    font-weight: bold;
}
.postertrip {
    color: #228854 !important;
}
.oldpost {
    color: #CC1105;
    font-weight: 800;
}
.omittedposts {
    color: #707070;
}
.reply {
    background: none repeat scroll 0 0 #6688AA !important;
}
.replyhl {
    background: none repeat scroll 0 0 #F0C0B0 !important;
}
.doubledash {
    clear: both;
    float: left;
    vertical-align: top;
}
.replytitle {
    font-size: 1.2em;
    font-weight: 800;
}
.commentpostername {
    font-weight: 800;
}
.thumbnailmsg {
    color: #800000;
    font-size: small;
}
.abbrev {
    color: #707070;
}
.highlight {
    background: none repeat scroll 0 0 #F0E0D6;
    border: 2px dashed #EEAA88;
    color: #800000;
}
#watchedthreads {
    background-color: #F0E0D6 !important;
}
.reflinkpreview {
    background-color: #F0E0D6 !important;
    z-index: 1000;
}
.doubledash {
    visibility: hidden !important;
}
.unkfunc {
    color: #36D6D0 !important;
}
img[id^="4chan_ext_"], img[class^="4chan_ext_"], span[id^="4chan_ext_"], div#twi_table_div {
    opacity: 0.75 !important;
}
.deletebuttons {
    color: #BFCFFE !important;
}
th {
    background: none repeat scroll 0 0 #555555 !important;
    font-size: small;
    padding: 0.2em !important;
    width: 200px !important;
}
#hd {
    display: none;
}
.box-outer {
    background: none repeat scroll 0 0 #203040 !important;
    border: 1px solid #505050 !important;
    border-radius: 6px 6px 6px 6px;
    margin-bottom: 1.0em !important;
    padding-bottom: 1.3em !important;
    padding-top: 0.5em !important;
}

.box-inner {
    background: none repeat scroll 0 0 #000000 !important;
    color: #65718C !important;

}
#filter-button, #option-button {
    display: none;
}
#ft {
    display: none;
}
.pages, .pages a {
    background: none repeat scroll 0 0 transparent !important;
    border: medium none !important;
    color: #65718C !important;
}
.pages td, .pages td b {
    color: #BFE3FE !important;
    font-weight: bold !important;
}
#recent-images li {
    background: none repeat scroll 0 0 #161717 !important;
    border-radius: 4px 4px 4px 4px;
    overflow:hidden;
   
}
#footer {
    display: none !important;
}

.boxcontent{
 padding-bottom:4px !important;
 padding-top:34px !important;
 
}

#preview-tooltip-ws h2 {
    background: none repeat scroll 0 0 #4b4b4e !important;
    color: #FFFFFF;
    font-size: 108%;
    height: 1.5em;
    line-height: 1.5em;
    max-width: 500px;
    padding-left: 0.5em;
}

#preview-tooltip-nws {
    background: none repeat scroll 0 0 #202020;
    border-color: #880000;
    padding: 0;
}
#preview-tooltip-ws {
    background: none repeat scroll 0 0 #101010;
    border-color: #000066;
    padding: 0;
    color: #ffffff;
}

.border, .hborder {
    background-color: #071520 !important;
    border: 0px solid gray !important;
    margin-bottom: 1em;
    margin-left: 2.5%;
    margin-right: 2.5%;
    padding: 2px;
    width: 95%;
}

.newthread h2 {
    color: #ffffff !important;
}

li {
    color: #65718C !important;
    font-family: "Tahoma",Arial,sans-serif;
    list-style: square inside none !important;
}

td.reply {
    background-image:none !important;
    background-color: #071520 !important;
    border: 0 solid #8A2BE2 !important;
    color: #E2E2E2 !important;
    float: left !important;
    padding: 0.2em !important;
    position: relative !important;
    width: 300px !important;
}
}
