css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {
#navtop, #navbot {
    background :url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAGQCAYAAABvWArbAAAAQ0lEQVQ4je3PsQmAYBCD0eS7abRw%2F6luBS08BMFe%2BEnzSIoU0bYfJ5IUQliW7p4EDLZFVQ1gAa96JyP8LP4%2FE0L44gL5Awe6aDut%2FgAAAABJRU5ErkJggg%3D%3D") repeat scroll 50% 50% #121417 !important;
    color: #000000 !important;
    font-size: 7pt !important;
    height: 12pt;
    text-align: center;
    width: 100%;
}
#navtop *, #navbot * {
    color: #EEEEEE;
    display: inline;
    font-size: 7pt;
}
#navtop a:hover, #navbot a:hover {
    background: none repeat scroll 0 0 #555555;
}
html, body {
    background: none repeat scroll 0 0 #222222 !important;
    color: #E6C06E !important;
    font-family: Tahoma !important;
    font-size: 11px !important;
}
a {
    color: #808768 !important;
    text-decoration: none !important;
}
#navtop a {
    color: #EDE993 !important;
}

.quotejs {
    color: #808768 !important;
    font-size: 10px !important;
    font-weight: bold !important;
}
.filesize {
    color: #808768 !important;
}
.filesize a {
    color: #808768 !important;
    font-weight: bold !important;
    text-decoration: none !important;
}
.quotejs a {
    color: #808768 !important;
    font-size: 10px !important;
}
a:hover {
    color: #FFFFFF;
}
.reflink a:hover {
    font-weight: bold;
}
.adminbar {
    clear: both;
    float: right;
    text-align: right;
}
.logo {
    clear: both;
    color: #FFFFFF;
    font-size: 2em;
    text-align: center;
    width: 100%;
}
.replymode {
    background: none repeat scroll 0 0 #E04000;
    color: #FFFFFF;
    padding: 2px;
    text-align: center;
    width: 100%;
}
.catalogmode {
    background: none repeat scroll 0 0 #0040E0;
    color: #FFFFFF;
    padding: 2px;
    text-align: center;
    width: 100%;
}
.rules {
    font-family: sans-serif;
    font-size: 10px;
    width: 468px;
}
.rules li {
    margin-left: 1em;
}
.postblock {
    color: #EEEEEE;
    font-weight: 800;
}
.footer {
    font-family: serif;
    font-size: 12px;
    text-align: center;
}
.passvalid {
    background: none repeat scroll 0 0 #EEAA88;
    color: #FFFFFF;
    text-align: center;
    width: 100%;
}
.dellist {
    font-weight: bold;
    text-align: center;
}
.delbuttons {
    padding-bottom: 4px;
    text-align: center;
}
.managehead {
    background: none repeat scroll 0 0 #AAAA66;
    color: #400000;
    padding: 0;
}
.postlists {
    background: none repeat scroll 0 0 #FFFFFF;
    color: #800000;
    padding: 0;
    width: 100%;
}
.row1 {
    background: none repeat scroll 0 0 #EEEECC;
    color: #800000;
}
.row2 {
    background: none repeat scroll 0 0 #DDDDAA;
    color: #800000;
}
.unkfunc {
    background: inherit;
    color: #789922;
}
.filesize {
    text-decoration: none;
}
.filetitle {
    background: inherit;
    color: #CC1105;
    font-size: 1.2em;
    font-weight: 800;
}
.postername {
    color: #117743;
    font-weight: bold;
}
.postertrip {
    color: #228854;
}
.oldpost {
    color: #CC1105;
    font-weight: 800;
}
.omittedposts {
    color: #707070;
}
.reply {
    background: none repeat scroll 0 0 #6688AA;
    color: #222222;
}
.replyhl {
    background: none repeat scroll 0 0 #F0C0B0;
    color: #EEEEEE;
}
.doubledash {
    clear: both;
    float: left;
    vertical-align: top;
}
.replytitle {
    color: #CC1105;
    font-size: 1.2em;
    font-weight: 800;
}
.commentpostername {
    color: #117743;
    font-weight: 800;
}
.thumbnailmsg {
    color: #800000;
    font-size: small;
}
.abbrev {
    color: #707070;
}
.highlight {
    background: none repeat scroll 0 0 #F0E0D6;
    border: 2px dashed #EEAA88;
    color: #800000;
}
#watchedthreads {
    background-color: #F0E0D6 !important;
}
.reflinkpreview {
    background-color: #F0E0D6 !important;
    z-index: 1000;
}
td.reply {
    background-color: #111111 !important;
    border: 0 solid #808768 !important;
    color: #808768 !important;
    float: left !important;
    padding: 0.2em !important;
    position: relative !important;
    width: 300px !important;
}
td.replyhl {
    background-color: #555555 !important;
    border: 0 solid #555555 !important;
    border-radius: 3px 3px 3px 3px !important;
    color: #FFFFFF !important;
    padding: 0.2em !important;
    width: 100% !important;
}
label {
    color: #808768 !important;
}
.quotelink {
    color: #808768 !important;
    font-size: 11px;
    font-weight: bold !important;
    text-decoration: none !important;
}
.postername, .commentpostername, .omittedposts, .abbr {
    color: #E6C06E !important;
}
.filetitle, .replytitle {
    color: #E6C06E !important;
    font-size: large;
}
.postertrip, .linkmail {
    color: #D49090 !important;
    text-decoration: none !important;
}
.doubledash {
    visibility: hidden !important;
}
.unkfunc {
    color: #8DA971 !important;
}
img[id^="4chan_ext_"], img[class^="4chan_ext_"], span[id^="4chan_ext_"], div#twi_table_div {
    opacity: 0.75 !important;
}
.deletebuttons {
    color: #61664F !important;
}
th {
    background: none repeat scroll 0 0 #555555 !important;
    font-size: small;
    padding: 0.2em !important;
    width: 200px !important;
}
#hd {
    display: none;
}
.boxbar {
    background: none repeat scroll 0 0 #343928 !important;
    color: #808768 !important;
}
.box-outer {
    background: none repeat scroll 0 0 #61664F !important;
    border: medium none !important;
    color: #808768 !important;
}
.box-inner {
    background: none repeat scroll 0 0 #111111 !important;
    color: #808768 !important;
}
#filter-button, #option-button {
    display: none;
}
#ft {
    display: none;
}
.pages, .pages a {
    background: none repeat scroll 0 0 transparent !important;
    border: medium none !important;
    color: #808768 !important;
}
.pages td, .pages td b {
    color: #61664F !important;
    font-weight: bold !important;
}
#recent-images li {
    background: none repeat scroll 0 0 #314746 !important;
}
#footer {
    display: none !important;
}
.postblock {
    background: none repeat scroll 0 0 #111111 !important;
    color: #aaaaaa !important;
    font-weight: 800 !important;
}

.postblock {
    border: 1px solid #000000 !important;
    font-size: 10pt !important;
    line-height: 20px !important;
    padding: 0 5px !important;
}

.logo b span {
    font-family: tahoma;
}

.logo span {
    font-size: 28px;
    letter-spacing: 2px !important;
}

.logo {
    color: #E6C06E !important;
    font-size: 24pt !important;
    text-align: center;
}

.border, .hborder {
    background: none repeat scroll 0 0 #111111 !important;
    border: 0px solid #000000 !important;
    margin-bottom: 1em;
    margin-left: 2.5%;
    margin-right: 2.5%;
    padding: 6px;
    width: 95%;
}
}
