css:
@-moz-document domain('userstyles.org') {

body:not([class]) {
    background-image: -webkit-repeating-linear-gradient(150deg, #3d3d3d, #3d3d3d 48px, #333333 48px, #333333 96px) !important;
    background-image: -moz-repeating-linear-gradient(150deg, #3d3d3d, #3d3d3d 48px, #333333 48px, #333333 96px) !important;
    background-image: repeating-linear-gradient(150deg, #3d3d3d, #3d3d3d 48px, #333333 48px, #333333 96px) !important;
    font-family: Verdana !important;
}

body.Vanilla, body.Dashboard, body.Conversations {
    background: whitesmoke !important;
    font-family: Verdana !important;
}

header {
    background-color: #38ABE3 !important;
    margin: 0px !important;
}

header h1 {
    color: white !important;
    font-family: "Helvetica Neue", Helvetica, arial, sans-serif !important;
    font-size: 24px !important;
    margin-left: 15% !important;
}

header h1 a {
    color: #E3F4FF !important;
}

header h1 a:hover {
    color: white !important;
}

nav {
    margin-top: 20px !important;
    margin-bottom: 20px !important;
    border-radius: 3px !important;
    padding: 5px !important;
    width: 155px !important;
    margin-left: 10px !important;
    background-color: #333333 !important;
    color: white !important;
    border: 1px solid #3D3D3D !important;
}

nav li {
    margin-left: 5px !important;
    margin-right: 5px !important;
    margin-bottom: 10px !important;
    font-size: 14px !important;
    font-weight: bold !important;
}

nav li a {
    color: gainsboro !important;
    text-decoration: none !important;
}

nav li a:hover {
    color: white !important;
}

nav:hover {
    border: 1px solid dimgray !important;
}

#left-sidebar .ad {
    display: none !important;
}

#content-wrapper article {
    width: 70% !important;
    padding: 10px 10px 20px 10px !important;
    background-color: white !important;
    border-radius: 5px !important;
    min-height: 700px !important;
}

#content-wrapper article section {
    margin-bottom: 10px !important;
}

h2 {
    font-weight: normal !important;
    font-style: normal !important;
}

#front-page-best, #front-page-newest {
    width: 50% !important;
}

#install {
    border-radius: 5px !important;
    border: 1px solid #D6D6D6 !important;
    background-color: #ACE1AF !important;
    width: 94.333% !important;
}

#related {
    border-radius: 5px !important;
    padding: 5px !important;
    width: 155px !important;
    margin-left: 10px !important;
    background-color: #333333 !important;
    color: white !important;
    border: 1px solid #3D3D3D !important;
}

#related:hover {
    border: 1px solid dimgray !important;
}

#related ul {
    margin-top: 10px !important;
}

#related li {
    margin-bottom: 15px !important;
}

#related li a {
    color: gainsboro !important;
    text-decoration: none !important;
}

#related li a:hover {
    color: white !important;
}

#screenshots {
    position: relative !important;
    margin-right: 5px !important;
}

#screenshots img {
    max-width: 300px !important;
    max-height: 200px !important;
}

#content-wrapper li {
    margin-bottom: 2px !important;
}

#content-wrapper #style-table {
    width: 96% !important;
}

#content-wrapper article .pagination {
    padding-top: 10px !important;
    font-size: 14px !important;
}

footer p {
    padding-top: 20px !important;
    margin-left: 15% !important;
}

#LikePluginPagelet, #facebook {
    background: white !important;
}

#long-description {
    margin-bottom: 20px !important;
    line-height: 1.5em !important;
}

#content-wrapper {
    margin-top: 20px !important;
}

#search-terms {
    opacity: 0.916 !important;
}

#search-submit {
    opacity: 0.916 !important;
}

:hover> #search-terms {
    opacity: 1 !important;
}

:hover> #search-submit {
    opacity: 1 !important;
}

#search-terms:focus {
    opacity: 1 !important;
}

#search-terms:focus + #search-submit {
    opacity: 1 !important;
}

footer>p {
    margin-bottom: 8px !important;
}

a[href="/contact"] {
    background-color: white !important;
    border-top-left-radius: 5px !important;
    border-top-right-radius: 5px !important;
    padding: 1ex !important;
    padding-bottom: 8px !important;
}

div[style="width: 300px; height: 262px;"] {
    width: auto !important;;
    height: auto !important;;
}
}
