css:
@-moz-document domain('twitter.com'){
body, body .dashboard {
    background: white;
}

body .hovered-stream-item, .stream-item:hover {
    background: #dedede;
}

#top-bar-bg {
    background: #2d2d2d;
    border-bottom: 1px solid #e5e5e5;
    box-shadow: none;
    -webkit-box-shadow: none;
    height: 29px;
}

#logo {
    height: 29px;
    margin-top: 6px;
    width: 60px;
}

#logo:before {
    content: "Twitter";
    color: white;
    height: 29px;
    line-height: 10px;
    font-weight: bold;
}

#logo a {
    height: 29px;
    margin: 0;
    padding: 0;
    display: none;
}

#global-nav li a {
    background: transparent;
    padding: 10px;
    margin-top: 2px;
    line-height: 5px;
}

#global-nav li.active a {
    border-top: 2px solid red;
    background: transparent;
    margin-top: 0;
}

a#new-tweet {
    margin: -5px 0 0 0;
}

.dropdown {
    margin: -11px 0 0 0;
}

#search-form {
    position: absolute;
    top: 35px;
    left: 725px;
    width: 190px;
}

#search-form #search-query, .main-tweet-box textarea.twitter-anywhere-tweet-box-editor {
    background-color: white;
    border: 1px solid #d9d9d9;
    color: black;
    font-size: 16px;
    height: 25px;
    line-height: 25px;
    padding: 1px 7px;
    box-shadow: none;
    -webkit-box-shadow: none;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
}

.condensed textarea.twitter-anywhere-tweet-box-editor, .main-tweet-box textarea.twitter-anywhere-tweet-box-editor {
    width: 500px;
    margin: -31px 0 0 185px;
    float: left;
}

#top-bar .vr {
    border-right: 1px solid #eee;
    border-left: none;
    height: 19px;
}

body.narrow #session {
    padding: 5px 10px 5px 5px;
}

#page-outer:before {
    content: "";
    position: absolute;
    top: 25px;
    left: 0;
    width: 100%;
    background: whiteSmoke;
    border-bottom: 1px solid #e5e5e5;
    display: block;
    height: 55px;
}

#page-container {
    margin-top: 40px;
    overflow: visible;
}

.main-tweet-box {
    position: absolute;
    top: -70px;
    left: 0px;
    z-index: 10;
}

.tweet-box-title {
    float: left;
    margin-top: 4px;
}

.tweet-box-title h2 {
    color: whiteSmoke;
    text-shadow: none;
}

.condensed .tweet-box-title h2 {
    color: #999;
}

.main-tweet-box .geo-control {
    margin-top: -26px;
    width: 180px;
}

.page-header {
    background: transparent;
    border-radius: 0;
    -webkit-border-radius: 0;
}

.main-content {
    box-shadow: none;
    -webkit-box-shadow: none;
}

.page-header .stream-tabs {
    margin-top: 0;
}

.stream-tabs, .stream-subtabs, .stream-tab, .stream-tab:hover, .stream-tab.active, .stream-tab a.tab-text, .stream-tabs .stream-tab.active .tab-text, .stream-tabs .stream-tab.active .tab-text:hover, .stream-tabs .stream-tab .tab-text:hover {
    background: transparent;
    border: 0;
    color: #333;
    font-size: 15px;
    font-weight: normal;
    border-radius: 0;
    -webkit-border-radius: 0;
}

.stream-tabs .stream-tab.active .tab-text, .stream-tabs .stream-tab.active .tab-text:hover {
    color: #D14836;
}

.stream-tabs li.stream-tab:hover {
    background: whiteSmoke;
}

.details-pane {
    margin-top: 50px;
}

.tweet-button {
    margin-top: 1px;
}

#session.loggedin {
    padding-top: 5px;
}
}
