css:
@-moz-document domain('picasaweb.google.com') {
  td.gphoto-logo {
    padding-left: 100px;
    vertical-align: middle;
  }

  .gphoto-logo div {
    background: url(http://img202.imageshack.us/img202/3523/googlephotos.png) no-repeat; 
    height: 39px;
    width: 200px;
  }

  .gphoto-logo a {
    display: block;
    width: 200px;
    height: 39px;
  }

  #lhid_logo {
    display: none;
  }

  table.gphoto-topnav {
    height: 60px;
  }

  div.gphoto-topnav-tab-inactive a[href$='/home'] {
    background-image: url(https://ssl.gstatic.com/s2/oz/images/sprites/common-full-2fd6df831c52ccf15a88279322948103.png);
    background-position: 0 -90px;
  }

  div.gphoto-topnav-tab-active a[href$='/home'],
  div.gphoto-topnav-tab-inactive a[href$='/home']:hover {
    background-image: url(https://ssl.gstatic.com/s2/oz/images/sprites/common-full-2fd6df831c52ccf15a88279322948103.png);
    background-position: 0 -137px;
  }

  div.gphoto-topnav-tab-inactive a[href$='/myphotos'] {
    background-image: url(https://ssl.gstatic.com/s2/oz/images/sprites/common-full-2fd6df831c52ccf15a88279322948103.png);
    background-position: 0 -33px
  }

  div.gphoto-topnav-tab-active a[href$='/myphotos'],
  div.gphoto-topnav-tab-inactive a[href$='/myphotos']:hover {
    background-image: url(https://ssl.gstatic.com/s2/oz/images/sprites/common-full-2fd6df831c52ccf15a88279322948103.png);
    background-position: 0 -156px
  }

  div.gphoto-topnav-tab-inactive a[href$='/explore'] {
    background-image: url(https://ssl.gstatic.com/s2/oz/images/sprites/common-full-2fd6df831c52ccf15a88279322948103.png);
    background-position: 0 -71px
  }

  div.gphoto-topnav-tab-active a[href$='/explore'],
  div.gphoto-topnav-tab-inactive a[href$='/explore']:hover {
    background-image: url(https://ssl.gstatic.com/s2/oz/images/sprites/common-full-2fd6df831c52ccf15a88279322948103.png);
    background-position: 0 -52px
  }

  .gphoto-topnav-tab a {
    background: transparent;
    border: 0px;
    width: 18px;
    height: 18px;
    text-indent: -999px !important;
    overflow: hidden !important;
    display: block;
    padding: 0px;
    margin: 5px 11px;
  }

  td.gphoto-topnav-tab > div {
    background: #F3F3F3;
    background: -webkit-gradient(linear,left top,left bottom,from(whiteSmoke),to(#F1F1F1));
    background: -moz-linear-gradient(top,whiteSmoke,#F1F1F1);
    background: -o-linear-gradient(top,whiteSmoke,#F1F1F1);
    border: 1px solid #D9D9D9;
    height: 27px;
    margin-right: -1px;
    text-align: center;
    vertical-align: middle;
    width: 44px;
  }

  div.gphoto-topnav-tab-active {
    background-color: #E0E0E0 !important;
    background: -o-linear-gradient(top,#EEE,#E0E0E0) !important;
    background: -webkit-gradient(linear,left top,left bottom,from(#EEE),to(#E0E0E0)) !important;
    background: -moz-linear-gradient(top,#EEE,#E0E0E0) !important;
    background: -o-linear-gradient(top,#EEE,#E0E0E0) !important;
    border-color: #BBB;
    box-shadow: inset 0 1px 5px #ccc;
    -moz-box-shadow: inset 0 1px 5px #ccc;
    -webkit-box-shadow: inset 0 1px 5px #ccc;
    z-index: 2;
  }

  div.gphoto-topnav-tab-inactive:hover {
    background-color: #F8F8F8;
    background: -o-linear-gradient(top,#F8F8F8,#F1F1F1);
    background: -webkit-gradient(linear,left top,left bottom,from(#F8F8F8),to(#F1F1F1));
    background: -moz-linear-gradient(top,#F8F8F8,#F1F1F1);
    background: -o-linear-gradient(top,#F8F8F8,#F1F1F1);
    border-color: silver;
    z-index: 1;
  }

  .gphoto-topnav-tab > div {
    border-radius: 2px 0 0 2px !important;
  }

  .gphoto-topnav-tab {
    padding: 15px 0;
    cursor: pointer;
  }

  .gphoto-topnav-tab .gphoto-notch {
    display: none;
  }

  .gphoto-uploadbutton {
    cursor: default;
    font-size: 11px;
    font-weight: bold;
    text-align: center;
    margin-right: 16px;
    height: 27px;
    line-height: 27px;
    min-width: 54px;
    outline: 0;
    padding: 0 8px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
    border: 1px solid transparent;
    color: white;
    text-transform: uppercase;
    background-color: #D14836;
    background-image: -webkit-linear-gradient(top,#dd4b39,#d14836);
    background-image: -moz-linear-gradient(top,#dd4b39,#d14836);
    background-image: -ms-linear-gradient(top,#dd4b39,#d14836);
    background-image: -o-linear-gradient(top,#dd4b39,#d14836);
    background-image: linear-gradient(top,#dd4b39,#d14836);
    text-shadow: 0 1px rgba(0, 0, 0, 0.1);
  }

  .gphoto-uploadbutton:hover {
    border: 1px solid #B0281A;
    border-color-bottom: #af301f;
    background-color: #C53727;
    background-image: -webkit-linear-gradient(top,#dd4b39,#c53727) !important;
    background-image: -moz-linear-gradient(top,#dd4b39,#c53727) !important;
    background-image: -ms-linear-gradient(top,#dd4b39,#c53727) !important;
    background-image: -o-linear-gradient(top,#dd4b39,#c53727) !important;
    background-image: linear-gradient(top,#dd4b39,#c53727) !important;
    -moz-box-shadow: 0 1px 1px rgba(0,0,0,0.2);
    -ms-box-shadow: 0 1px 1px rgba(0,0,0,0.2);
    -o-box-shadow: 0 1px 1px rgba(0,0,0,0.2);
    -webkit-box-shadow: 0 1px 1px rgba(0,0,0,0.2);
    box-shadow: 0 1px 1px rgba(0,0,0,0.2);
  }

  .gphoto-uploadbutton,
  .gphoto-uploadbutton * {
    cursor: pointer;
  }

  .goog-button-base-inner-box, .goog-button-base-top-shadow,
  .goog-button-base-outer-box {
    background: transparent;
    border: 0px;
  }

  td.gphoto-uploadbutton-box {
    padding: 0 0 13px 20px;
  }

  .gphoto-uploadbutton div.SPRITE_upload-on {
    background-image: url('https://ssl.gstatic.com/s2/oz/images/photos/upload.png');
    background-position: 0 0;
    height: 23px;
    width: 30px;
    top: 7px !important;
    left: -2px !important;
    display: inline-block;
  }

  .gphoto-uploadbutton div.buttonlabel {
    color: white;
    text-transform: uppercase;
    text-shadow: 0 1px rgba(0, 0, 0, 0.1);
    cursor: default;
    font-size: 11px;
    font-weight: bold;
    text-align: center;
    line-height: 27px;
    margin-left: 25px;
  }

  input#lhid_searchbox {
    min-width: 256px;
    max-width: 50%;
    background: white !important;
    border: 1px solid #D9D9D9 !important;
    border-radius: 1px !important;
    border-top: 1px solid silver !important;
    height: 27px !important;
    padding: 0 0 0 8px !important;
    -moz-border-radius: 1px;
    -webkit-border-radius: 1px;
    font-size: 13px;
    margin: 0 10px 0 0;
    border-color: #CCC;
  }

  div.gphoto-searchbox-active,
  div.gphoto-searchbox-inactive {
    padding-bottom: 14px !important;
  }

  input.gphoto-searchphotos-button {
    border: 1px solid #3079ED;
    color: white;
    background: #4D90FE;
    background-image: -webkit-gradient(linear,left top,left bottom,from(#4d90fe),to(#4787ed));
    background-image: -webkit-linear-gradient(top,#4d90fe,#4787ed);
    background-image: -moz-linear-gradient(top,#4d90fe,#4787ed);
    background-image: -ms-linear-gradient(top,#4d90fe,#4787ed);
    background-image: -o-linear-gradient(top,#4d90fe,#4787ed);
    background-image: linear-gradient(top,#4d90fe,#4787ed);
    margin-right: 6px;
    -webkit-border-radius: 2px;
    -webkit-transition: all 0.218s;
    font-size: 11px;
    font-weight: bold;
    height: 27px;
    line-height: 27px;
    min-width: 54px;
    outline: none;
    padding: 0 8px;
    text-align: center;
    cursor: pointer;
    border-radius: 2px;
    text-transform: uppercase;
  }
}
