css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* http://userstyles.org/styles/50576 */
/* author: hg42 at gmx.net */

#TabsToolbar
  {
  background-image: none !important;
  background-color: #AAAABB !important;
  border-right: 0px !important;
  padding-right: 0px !important;
  /* background-color: #FFFFFF !important; */
  }

#tabbrowser-tabs
  {
  background-image: none !important;
  background-color: #AAAABB !important;
  padding-left: 0px !important;
  }

.tabbrowser-strip
  {
  border-right: 0px !important;
  padding-right: 0px !important;
  background-color: #FFFFFF !important;
  }

tabbrowser splitter
  {
  background: #FFF !important;
  border: 0px !important;
  margin: 0px !important;
  padding: 0px !important;
  width: 5px !important;
  -moz-appearance: none !important;
  }

/*
.tabbrowser-arrowscrollbox
  {
  border-top: 0px !important;
  margin-top: 0px !important;
  padding-top: 0px !important;
  }
*/

#tabbrowser-tabs .scrollbox-innerbox
  {
  background-image: none !important;
  padding-left: 2px !important;
  }

#tabbrowser-tabs .tabs-newtab-button
  {
  /* background: #DDE !important; */
  background: transparent !important;
  opacity: 1 !important;
  /* -moz-opacity: 0.6 !important; */
  border: 1px solid #999 !important;
  /*
  -moz-border-top-colors:    #999 !important;
  -moz-border-bottom-colors: #999 !important;
  -moz-border-left-colors:   #999 !important;
  -moz-border-right-colors:  #999 !important;
  */
  margin: 2px 20px !important;
  height: 12px !important;
  /*
  -moz-border-radius: 6px !important;
  */
  border-radius: 6px !important;
  }

#tabbrowser-tabs .tabbrowser-tab
  {
  border: 1px solid #999 !important;
  /*
  -moz-appearance:           none !important;
  -moz-border-top-colors:    #999 !important;
  -moz-border-bottom-colors: #999 !important;
  -moz-border-left-colors:   #999 !important;
  -moz-border-right-colors:  #999 !important;
  -moz-border-radius: 12px !important;
  */
  border-radius: 12px !important;
  outline: 0px !important;
  margin-top: 2px !important;
  padding: 0px !important;
  max-height: 24px !important;
  }

#tabbrowser-tabs .tabbrowser-tab .tab-content
  {
  padding-right: 0px !important;
  }

#tabbrowser-tabs .tabbrowser-tab[selected="true"] .tab-image-right
  {
  display: -moz-box !important;
  height: 24px !important;
  width: 8px !important;
  margin: 0px !important;
  /* margin-left: -22px !important; */
  margin-bottom: -2px !important; /* for informational tab ??? */
  padding: 0px !important;
  outline: 0px !important;
  /*
  background-image: -moz-linear-gradient(left, rgba(255,255,255,0), rgba(255,255,255,255)) !important;
  */
  background-color: #FFF !important;
  /*
  -moz-border-radius-topleft: 12px !important;
  -moz-border-radius-bottomleft: 12px !important;
  */
  border-top-left-radius: 12px !important;
  border-bottom-left-radius: 12px !important;
  opacity: 1 !important;
  }

#tabbrowser-tabs .tabbrowser-tab .tab-image-left
  {
  width: 0px !important;
  }

#tabbrowser-tabs .tabbrowser-tab .tab-image-middle
  {
  background: none !important;
  height: 24px !important;
  /*
  -moz-border-radius-topleft: 9px !important;
  -moz-border-radius-bottomleft: 9px !important;
  */
  border-top-left-radius: 9px !important;
  border-bottom-left-radius: 9px !important;
  /*
  margin: 0px !important;
  outline: 0px !important;
  padding: 0px !important;
  */
  }

#tabbrowser-tabs .tabbrowser-tab .tab-image-middle .tab-text
  {
  opacity: 1 !important;
  }

#tabbrowser-tabs .tabbrowser-tab .tab-close-button
  {
  z-index: 1000 !important;
  background: none !important;
  height: 24px !important;
  width: 24px !important;
  padding: 0 !important;
  padding-right: 0px !important;
  }

#tabbrowser-tabs .tabbrowser-tab[selected] .tab-close-button
  {
  z-index: 1000 !important;
  background: none !important;
  height: 24px !important;
  width: 24px !important;
  padding: 0 !important;
  padding-right: 6px !important;
  }

#tabbrowser-tabs .tabbrowser-tab
  {
  margin-right: 2px !important;
  /*
  -moz-border-radius-topright: 0px !important;
  -moz-border-radius-bottomright: 0px !important;
  */
  }

#tabbrowser-tabs .tabbrowser-tab[selected]
  {
  background: #FFF !important;
  color: #000 !important;
  }

#tabbrowser-tabs .tabbrowser-tab[selected]:not([pinned])
  {
  padding-right: 0px !important;
  margin-right: 0px !important;
  border-right: 0px !important;
  /*
  -moz-border-radius-topright: 0px !important;
  -moz-border-radius-bottomright: 0px !important;
  */
  border-top-right-radius: 0px !important;
  border-bottom-right-radius: 0px !important;
  }

#tabbrowser-tabs .tabbrowser-tab[pinned]
  {
  padding-right: 5px !important;
  background: #ccc !important;
  }
#tabbrowser-tabs .tabbrowser-tab[selected][pinned]
  {
  /* border: 1px solid red !important; */
  background: #FFF !important;
  }

#tabbrowser-tabs .tabbrowser-tab[selected]:hover
  {
  /* padding-right: 0px !important; */
  }


#tabbrowser-tabs
  {
  padding-left: 2px !important;
  }

.tabbrowser-strip > vbox > hbox
  {
  -moz-box-orient: vertical !important;
  }

/* end */
