@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mininova.org") {

/* Style */

body{
background:#3c3c3c !important;
color:#c8c8c8 !important;
}
#torrentgeneral img {
display:none !important;
}
.maintable th, .maintable td, .maintable tr {
background:#3c3c3c !important;
color:#c8c8c8 !important;
border:0 !important;
}

.maintable tr a {
color:#c8c8c8 !important;
text-decoration:none !important;
}

#featured a {
color:#c8c8c8 !important;
text-decoration:none !important;
}
#featured a:hover {
color:#fff !important;
text-decoration:none !important;
}
#featured img {
display:none !important;
}
.maintable strong a {
color:#c8c8c8 !important;
text-decoration:none !important;
}
.maintable strong a:hover {
color:#fff !important;
text-decoration:none !important;
}

#location {
color:#c8c8c8 !important;
background:#3c3c3c !important;
margin-top:10px !important;
margin-bottom:10px !important;
height:20px !important;
}
#location a {
color:#c8c8c8 !important;
background:#3c3c3c !important;
text-decoration:none !important;
}
#location a:hover {
color:#fff !important;
background:#3c3c3c !important;
text-decoration:none !important;
}

#location .c {
display:none !important;
}
td a {
color:#c8c8c8 !important;
text-decoration:none !important;
}
td a:hover {
color:#fff !important;
text-decoration:none !important;
}
#music, .catheader, .dl, tr, tbody, table, td, th {
border:0 !important;
}
.catheader a {
color:#c8c8c8 !important;
text-decoration:none !important;
}
.catheader a:hover {
color:#fff !important;
text-decoration:none !important;
}
.catheader h2 a {
color:#c8c8c8 !important;
text-decoration:none !important;
}
.catheader h2 a:hover {
color:#fff !important;
text-decoration:none !important;
}
#torrentgeneral p.left {
border:0 !important;
}
#searchform input {
border:1px solid #000 !important;
color:#c8c8c8 !important;
background:#2d2d2d !important;
}
#headerlinks ul, #headerlinks li, .g, .b {
color:#c8c8c8 !important;
}
#navigation {
background:#3c3c3c !important;
}
#navigation li {
border:0 !important;
}
#navigation .c {display:none !important;
}
.maintable td a {
color:#c8c8c8 !important;
text-decoration:none !important;
}
.maintable td a:hover {
color:#fff !important;
text-decoration:none !important;
}
h1 img {
display:none !important;
}
#downloadbox {
background:#3c3c3c !important;
color:#c8c8c8 !important;
border:0 !important;
}
#content a {
color:#c8c8c8 !important;
text-decoration:none !important;
}
#content a:hover {
color:#fff !important;
text-decoration:none !important;
}
.r {
color:#c8c8c8 !important;
text-decoration:none !important;
}
#headerlinks a {
color:#c8c8c8 !important;
text-decoration:none !important;
}
#headerlinks a:hover {
color:#c8c8c8 !important;
text-decoration:none !important;
}
#altdownload .emphasis {
color:#c8c8c8 !important;
}
#downloadbox img {
display:none !important;
}
#tabs, #tabs a {
color:#c8c8c8 !important;
background:#3c3c3c !important;
border:0 !important;
}
#tabs a:hover {
background:#3c3c3c !important;
}
#tabs .current a {
background:#3c3c3c !important;
border:0 !important;
}
#navigation a, #nav-browse a, #nav-upload a, #nav-community a {
background:none !important;
color:#c8c8c8 !important;
text-decoration:none !important;
}
#navigation a:hover, #nav-browse a:hover, #nav-upload a:hover, #nav-community a:hover {
background:none !important;
color:#fff !important;
text-decoration:none !important;
}
#tabs .current a:hover {
background:#3c3c3c !important;
border:0 !important;
}

/* Nav */
li .first a {
color:#c8c8c8 !important;
}

#navigation, #navigation ul, #navigation li {
border:0 !important;
}
#navigation ul ul a {
color:#c8c8c8 !important;
background:#3c3c3c !important;
}
#navigation ul ul a:hover {
color:#fff !important;
background:#3c3c3c !important;
}
/* Ads-B-Gone */

#sidebar, #footer, #adspot-a, #adspot-b, #adspot-c {
display:none !important;
}

#content {
margin:0 !important;
}
#description {
border:0 !important;
}
#adspot-d {
display:none !important;
}
/* Logo messup */
img[src*="/images/logo.png"] {
margin-top:10px !important;
width: 0px !important;
height: 34px !important;
padding-left: 204px !important;
background:url(data:image/png;base64,%3D) no-repeat !important;
}

/* Updated for streaming functionality */
#banner-toolbar, #download img, #bitlet img {
display:none !important;
}
#download, #bitlet {
background:#3c3c3c !important;
color:#c8c8c8 !important;
border:0 !important;
}
}
