css:
/* Colors
 * main: 011340
 * analogous accent: 13275B
 * complementary accent: f4b658
 * near-black: 010612
 * med1: 213d70
 * light1: 5781c1
 * light2: e5f2ff
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.renderosity.com"), url-prefix('http://www.renderosity.com'),domain("market.renderosity.com"), url-prefix('http://market.renderosity.com') {
	body {
		background: #010612 !important;
		color: white;
	}
	a, a:active, a:visited, a.forum_threadname_link {
		color: white !important;
	}
	td.forum_name_cell > div > a {
		color: white: !important;
	}
	div#content.inner_page, .inner_page_background {
		background: black !important;
	}
	#top_navigation {
		background: #13275B !important;
	}
	#top_navigation ul li:hover a {
		color: #5781c1 !important;
	}
	#top_navigation li:hover li a {
		color: #ffffff !important;
	}
	#top_navigation li:hover li a:hover {
		color: #e5f2ff !important;
	}
	table.gallery_comment_table td, table.gallery_comment_table {
		border: none !important;
	}
	.gallery_display_title_cell, .gallery_display_avatar_cell, table.gallery_display_table, .forum_divider_cell {
		background: black !important;
	}
	.gallery_display_details_cell, .gallery_display_notes_cell, .gallery_comment_avatar_cell, .gallery_comment_body, a.gallery_image_action_bar:hover {
		background-color: #13275B !important;
	}
	.gallery_comment_header_cell {
		background-color: #5781c1 !important;
		border-bottom: 2px solid #e5f2ff !important;
	}
	.gallery_comment_divider_cell {
		background: black !important;
	}
	.gallery_images_cell {
		background-color: #010612 !important;
	}
	table[width="138"] td {
		background: transparent !important;
	}		
	table[width="138"] td img {
		display: none !important;
	}
	#footer {
		background-color: #011340 !important;
		border-top: 5px solid #13275B !important;
	}
	#right_navigation > ul > li {
		border-color: #010612 !important;
		background: #011340 !important;
	}
	#right_navigation > ul > li > ul, div.sectionhead {
		background: #13275B !important;
	}
	.subcom_menu_select {
		font-size: 12px !important;
		height: 26px !important;
	}
	#forum_outer_table {
		border-color: black !important;
		background-color: black !important;
	}
	#forum_inner_table th, td.hdr_mailbox_delete, td.hdr_mailbox_from, td.hdr_mailbox_subject, td.hdr_mailbox_time, td.hdr_mailbox_read, td.hdr_mailbox_read_date {
		background: #011340 !important;
	}
	.forum_message_detail_table {
		background: transparent !important;
	}
	.forum_group_row_cell, .gallery_section_cell, .gallery_section_link_cell {
		background: #13275B !important;
		color: #e5f2ff !important;
	}
	.forum_marker_cell, td.forum_name_cell, .forum_threads_cell, .forum_posts_cell, .forum_last_post_cell, .forum_author_cell, .forum_views_cell, tr[bgcolor="#2c3b4e"], tr[bgcolor="#2c3b4e"] table tr, tr[bgcolor="#232323"], tr[bgcolor="#232323"] table tr {
		background: #0C1C3F !important;
	}
	tr[bgcolor="#466583"], tr[bgcolor="#466583"] table tr, tr[bgcolor="#333333"], tr[bgcolor="#333333"] table tr  {
		background: #213D70 !important;
	}
	table.forum_message_detail_table {
		background: transparent !important;
		padding: 0 !important;
	}
	table.forum_message_detail_table td {
		margin: 0 !important;
	}
	.bcs_default_page_featured_heading, .bcs_default_page_whatsnew_heading, .bcs_default_page_whatshot_heading {
		background: #5781c1 !important;
	}
	body#tinymce.mceContentBody, iframe html, iframe html body {
		background: white !important;
	}
	.bcs_adv_dept_sort_table td {
		background: #13275B !important;
		border-color: #010612 !important;
	}
	td#bcs_adv_sort_selected {
		background: #5781c1 !important;
	}
	td#bcs_adv_sort_selected a {
		color: #13275B !important;
	}
	ul.css-tabs a {
		border-color: #010612 !important;
		background: #e5f2ff !important;
		color: #112756 !important;
	}
	ul.css-tabs a:hover {
		background: #f4b658 !important;
	}
	ul.css-tabs a.current {
		background: #112756 !important;
		color: white !important;
	}
	hr {
		background: #f4b658 !important;
		color:  #f4b658 !important;
	}
	#homepage_bottom_left_tabs {
		border: none !important;
		background: #010612 !important;
	}
	.tabcontainer {
		background: #011340 !important;
	}
	.activetab  {
		background: #011340 !important;
	}
	.inactivetab  {
		background: #213d70 !important;
	}
	.tabs > span {
		color: white !important;
	}
	.bwSlideshow .selection ul li {
		background: #13275B !important;
		border-color: white !important;
		color: #e5f2ff !important;
	}

	.bwSlideshow .selection ul li:hover {
		background: #5781c1 !important;
	}
	.bwSlideshow .selection ul li.active {
		background: #213d70 !important;
		color: white !important;
	}
	#homepage_bottom_right_tabs1, #homepage_bottom_right_tabs2  {
		background: #010612 !important;
		border-color: #010612 !important;
	}
	#homepage_footer_right, #homepage_footer_left {
		border-color: #010612 !important;
		background: #010612 !important;
	}
	#homepage_footer_left div, #homepage_footer_right div {
		background: #011340 !important;
		color: #e5f2ff !important;
	}
	#homepage_footer_left div b, #homepage_footer_right div b{
		color: white !important;
	}
	.announcement_alert_wrapper, #homepage_chat {
		border: none !important;
		background: transparent !important;
	}
	.announcement_alert_container, .rrchat_online_table  {
		border: none !important;
		background: #213d70 !important;
		color: white !important;
	}
	h2, .homepage_tab_link {
		color: #f4b658 !important;
	}
	#stats_menu_div, .stats_menu_div {
		background-color: #f4b658 !important;
		color: white !important;
		opacity: 1.0 !important;
	}
	.freestuff_comment_action, .freestuff_comment_comment, .freestuff_comment_user, td.mailbox_item_checkbox, td.mailbox_item_fromto, td.mailbox_item_subject, td.mailbox_item_time, td.mailbox_item_read, td.mailbox_item_read_date {
		background: #213d70 !important;
	}
}
