css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#compose-toolbox,
#mail-toolbox,
#ab-toolbox {
    box-shadow: 0 0 0 1px rgba(255, 255, 255, .3) inset,
                0 0 0 2px rgba(255, 255, 255, .1) inset,
                0 1px 0 rgba(0, 0, 0, .15) !important;
}


#compose-toolbox {
    border-radius: 4.5px 4.5px 0px 0px !important;
    background-color: rgb(207, 219, 236) !important;
    background-image: -moz-linear-gradient(rgba(255, 255, 255, .2) 10px,
                      rgba(255, 255, 255, .5)) !important;
    margin-bottom: -1px !important;
    padding-bottom: 3px !important;
}
#headers-box,
#MsgHeadersToolbar {
    border-top: 0 !important;
    border-radius: 0 !important;
}


#mail-toolbox,
#ab-toolbox {
    background: rgba(151, 152, 153, .05)
                -moz-linear-gradient(rgba(251, 252, 253, .95),
                rgba(246, 247, 248, .47) 49%, rgba(231, 232, 233, .45) 51%,
                rgba(225,226,229,.3)) !important;
    background-color: rgba(240, 240, 240, .7) !important;
    border: 1px solid !important;
    border-color: rgba(0,0,0,.12) rgba(0,0,0,.19) rgba(0,0,0,.38) !important;
}

#mail-toolbox {
    border-radius: 4.5px !important;
    padding: 0 2px 4px !important;
    margin-bottom: 2px !important;
}

#ab-toolbox {
    border-radius: 4.5px 4.5px 0 0 !important;
    padding: 0 2px 4px !important;
}
