css:
body, #bottomshelf {
    background: white;
    color: white;
}

#header {
    background: #2d2d2d;
    border-bottom: 1px solid #e5e5e5;
}

#header:after {
    content: "";
    position: absolute;
    top: 25px;
    left: 0;
    width: 100%;
    background: whiteSmoke;
    border-bottom: 1px solid #e5e5e5;
    display: block;
    height: 55px;
    z-index: -1;
}

#header a {
    text-decoration: none;
    padding: 3px 1px;
}

.menu, #drop {
    position: relative;
    top: -4px;
}

#header a:hover {
    border-top: 2px solid #d14863;
}

.menu a:nth-child(9), .menu a:nth-child(10), .menu a:nth-child(11), .menu a:nth-child(12), .menu a:nth-child(13), .menu a:nth-child(15), #drop a:nth-child(3), #___plusone_0, #___plusone_1,span em, .fb_ltr, .com0, table iframe, .addthis_button {
    display: none;
}

.rating {
    position: absolute;
    width: 99%;
    left: 0;
    top: 50px;
    z-index: 10;
    color: black;
}

.item .fn {
    position: absolute;
    left: 50%;
    width: 500px;
    margin-left: -250px;
    text-align: center;
    top: 33px;
    text-transform: capitalize;
    z-index: 10;
    color: black;
}

.item .photo {
    margin: -35px auto 20px auto;
}
