css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.reddit.com") {
/*Created by Achal Dave, 2011*/
/*Colors used:
#000000 [black] for text
#FFFFFF [white] generally for background
#5C5C5C [darkish gray] for general links
#7D7D7D [lightish gray] for visited general links
For no color: 
#262626 [darker darkish gray] for submitted links
#ABABAB [darker lightish gray] for visited submitted links
For color: 
#d50000 [darkish red] for submitted links
#FFCFB5 [light light orange] for visited submitted links
#d50000 [darkish red] (gradient start, other)
#e65100 [darkish orange] (gradient end, other)
*/

/*
Wait, why are you going through my code?
It's not like I put bad stuff here... I would never do that...
Or maybe this is just a ploy to put a virus in your computer?!
Oh yeah. I just invented a ~CSS VIRUS~. All hail.
*/

/*Everything should be Segoe UI sweetness*/
/*Also, no curvy radius border thingies!*/
*{
font-family: "Segoe UI","Segoe WP","Segoe UI Light","Segoe WP Light",helvetica,arial,sans-serif!important;
border-radius:0px!important;
}

/*Front Page Stuff*/
/*Sidebar*/
/*in subreddit*/
input{
border-width:1px!important;
border-color:#5C5C5C!important;
}	

h1.redditname > a{
font-weight:400!important;
color:#FFFFFF!important;
}

/*MALICIOUS CODE HERE!*/
/*not really, just sexy gradients*/

div.linkinfo *, div.titlebox *, div.trophy-area div.content table *{
background:transparent!important;
}

div.linkinfo, div.titlebox, div.trophy-area div.content{
background: -webkit-gradient(linear, left center, right center, from(#d50000), to(#e65100))!important; /* for webkit browsers */
background: -moz-linear-gradient(left center,  #d50000,  #e65100)!important; /* for firefox 3.6+ */ 
}

div.spacer div.titlebox div.bottom{
border-width:0px!important;
}

/*Links in subreddit description*/
div.titlebox div.usertext-body div.md p a{
color:#FFFFFF!important;
font-weight:600!important;
}

span.user{
font-size:20px!important;
margin-bottom:20px!important;
}

/*RES "+shortcut" button*/
span.RESshortcutside{
position:relative!important;
left:20px!important;
padding:1px 5px!important;
}
span.RESshortcutside:hover{
background:black!important;
color:white!important;
font-weight:500!important;
}
span.RESshortcutside:active{
top:2px!important;
left:22px!important;
}
span.RESshortcutside:not(:hover):active{
top:0px!important;
left:20px!important;
}

/*Frontpage button for subreddits*/
span.fancy-toggle-button{
padding-right:15px!important;
}

span.fancy-toggle-button a, span.RESshortcutside{
background:#FFFFFF!important;
font-family: "Segoe UI","Segoe WP","Segoe UI Light","Segoe WP Light",helvetica,arial,sans-serif!important;
font-weight:200!important;
border-radius:0px!important;
text-transform:uppercase!important;
margin:2px!important;
}

span.fancy-toggle-button:hover a{
background:black!important;
color:white!important;
font-weight:600!important;
margin:0px!important;
}

span.fancy-toggle-button a:active:not(#header-img-a){
position:relative!important;
top:2px!important;
left:2px!important;
}

span.fancy-toggle-button a:not(:hover):not(#header-img-a):active{
top:0px!important;
left:0px!important;
}

/*sidebar on user page*/
div.side div.spacer div.titlebox h1 {
color:#FFFFFF!important;
font-weight:500!important;
}

div.giftgold a{
font-weight:600!important;
color:#FFFFFF!important; 
/* Kept lighter to attract attention, drive people to help out Reddit :) */
}

/*end sidebar stuff... the rest is sort of chaotic*/

#header{
border-width:0px!important;
}
div{
border-radius:0px!important;
}

div.morelink{
border-width:1px!important;
border-color:#5C5C5C!important;
border-right-color:#5C5C5C!important;
border-right-width:1px!important;
background:white!important;
width:290px!important;
}

div.morelink:hover{
background:#000000!important;
}

div.morelink:hover a{
color:#FFFFFF!important;
}

a:active:not(#header-img-a){
position:relative!important;
top:2px!important;
left:2px!important;
}

ul.flat-vert li a{
background:none!important;
padding-left:0px!important;
}

a:not(:hover):not(#header-img-a):active{
position:relative!important;
top:0px!important;
left:0px!important;
}

div.morelink:active{
position:relative!important;
top:2px!important;
left:2px!important;
}

div.nub{
display:none!important;
}

#header-img{
position:absolute!important;
height:50px!important;
margin-top:20px!important;
clip:rect(0px,40px,200px,0px);
}

.pagename{
font-family: "Segoe UI","Segoe WP","Segoe UI Light","Segoe WP Light",helvetica,arial,sans-serif!important;
font-size: 48pt!important;
font-variant: normal!important;
line-height: normal!important;
font-weight:100!important;
background:transparent!important;
margin-left:70px!important;

}


ul.tabmenu{
display:block!important;
font-family:"Segoe UI Light","Segoe WP Light","Segoe UI","Segoe WP",helvetica,arial,sans-serif!important;
margin-left:65px!important;
text-transform:uppercase!important;
padding-bottom:10px!important;
border-width:0px!important;
}

ul.tabmenu li, ul.tabmenu a{
font-family:"Segoe UI Light","Segoe WP Light","Segoe UI","Segoe WP",helvetica,arial,sans-serif!important;
font-weight:500!important;
font-size:16px!important;
background:#FFFFFF!important;
color:#5C5C5C!important;
margin:5px!important;

}

ul.tabmenu li.selected, ul.tabmenu li.selected a{
color:#000000!important;
font-size:20px!important;
border-width:0px!important;
}

body, h1, div,  span, form, input{
background-color:#FFFFFF!important;
font-family: "Segoe UI","Segoe WP","Segoe UI Light","Segoe WP Light",helvetica,arial,sans-serif!important;
color:#000000!important;
}
/*Special colors for links*/
a{
font-family: "Segoe UI","Segoe WP","Segoe UI Light","Segoe WP Light",helvetica,arial,sans-serif!important;
color:#5C5C5C!important;
}

a:visited{
color:#7D7D7D!important;
}

a.title:link{
color:#d50000!important;
font-weight:500!important;
}

a.title:visited{
color:#FFCFB5!important;
}

a.submitter{
background:none!important;
color:blue!important;
font-weight:400!important;
text-transform:uppercase!important;
}

/*end link stuff*/ 

ul.sr-bar{
text-transform:uppercase!important;
font-size:10px!important;
}
/*Up and Down vote buttons*/
div.up{
position:relative!important;
background-size:100%!important;
width:18px!important;
background-repeat: no-repeat!important;
background-position: center center!important;
background-image: url(data:image/png;base64,)!important;
}

div.upmod{
position:relative!important;
background-size:100%!important;
width:18px!important;
background-repeat: no-repeat!important;
background-position: center center!important;
background-image: url(data:image/png;base64,)!important;
}

div.down{
position:relative!important;
width:18px!important;
background-size:100%!important;
background-repeat: no-repeat!important;
background-position: center center!important;
background-image: url(data:image/png;base64,)!important;
}

div.downmod{
position:relative!important;
background-size:100%!important;
width:18px!important;
background-repeat: no-repeat!important;
background-position: center center!important;
background-image:url(data:image/png;base64,)!important;
}

div.comment div.up, div.comment div.down, div.comment div.upmod, div.comment div.downmod{
margin-left:-1.5px!important;
}

/*Submit a Link Page*/
ul.formtab li.selected a{
font-size:20px!important;
}

ul.formtab{
margin-left:-27px!important;
}

ul.formtab li a{
font-size:16px!important;
color:#5C5C5C!important;
border-width:0px!important;
}
}
