css:
@-moz-document domain("mail.google.com"), domain("googleusercontent.com") {

/*remove space to the right of an email, the new element for contact info,
 invite a friend box, webclips etc*/

 .iY .Bu:nth-child(2),.iY .Bu:last-child,.T8uMgc .T0.pp.TZ:last-child, .u5,.mq.nH.oy8Mbf {
	display:none !important
}
.T0.J3tk6b
 {padding-right:0 !important;}

/*spaces around top navigation buttons*/
.VP5otc-pzeoBf,.iI {
	padding:11px 0 4px;
}
.qRauBc {
	margin-top:0;
}


/*spaces around bottom navigation buttons*/
.D.E.VP5otc-U4m8q {
	padding-top:7px;
padding-bottom:7px;
}

/*spaces around the chat windows -at least when it's on the right side 
and around the emails list*/
.T0.pp.TZ,.q0CeU {
margin-right:0;
margin-left: 3px;
}
.date-label {background: #E1E1E1!important;}
.view-container {height: 120px !important;}
.menu-link, .agenda-more {color: #666 !important;}
.T0.pp.TZ {
border-top: 0}
/*resizes navigation buttons if browser window
has a width<1240px. you can change this*/
@media screen and (max-width: 1239px){
.J-Zh-I {
	padding-top:5px;
	padding-bottom:5px;
	padding-left:7px;
	padding-right:7px;
	margin-top:0;
	margin-bottom:0;
}
.J-J5-Ji.J-JN-I {
	position:absolute;
	overflow:none;
	width:200px;
	font-size:12px;
	text-align:right;
	left:-162px;
}

.D.E.VP5otc-pzeoBf .J-J5-Ji.J-JN-I {
	top:-15px;
}
.D.E.VP5otc-U4m8q .J-J5-Ji.J-JN-I {
	top:25px;
}


.vpUyzb.J-J5-Ji {
	margin-right:5px;
}

.J-J5-Ji.J-JN-I span {
	width:200px;
}
.Bu:nth-child(2) .J-Zh-I-JW , .J-Zh-I.J-J5-Ji.J-Pm-I.L3:first:child{
	padding-left:7px !important;
}
.Bu:nth-child(2) .J-Zh-I-Js-Zq.J-Zh-I-JW {
	padding-left: 6px !important;
}
}
/*end of button size part*/


/* scroll bar tricks*/
/* creates a scrollbar just next to the list of emails if necessary
and limits max-height so that you can always see the account activity part.
if your browser has spaces at the bottom, increase the max-height*/
.X0POSb.qRauBc {
	overflow-y:auto;
overflow-x:hidden;
	max-height:360px
}

/*for F11 full screen mode*/

@media screen and (min-height: 765px){
.X0POSb.qRauBc
	{max-height:500px !important; }
}

@media screen and (min-height: 850px){
.X0POSb.qRauBc
	{max-height:620px !important; }
}
@media screen and (min-height: 950px){
.X0POSb.qRauBc
	{max-height:750px !important; }
}


.cQ {
	overflow-y:auto !important;
overflow-x:hidden !important;
}
.l2.ov.nH.oy8Mbf {
	margin-bottom:2px;
}

/* makes the scrollbar stylish */
::-webkit-scrollbar {
	width:5px;
}
::-webkit-scrollbar-track {
	background-color:#F1F1F1;
	border-radius:3px;
}
::-webkit-scrollbar-thumb {
	border-radius:3px;
	background-color:#e1e1e1;

}


}
