css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tud.hicknhack.org") {
body { background: #333; background: -moz-linear-gradient(#333, #232) fixed !important; }
body * { background: #FFF !important; color: #222 !important; }

a { color: #228 !important; text-decoration: none !important; }
a:hover { color: #222 !important; text-decoration: underline !important; }

.site { margin: 20px auto !important; border-radius: 4px !important; border: 3px solid #FFF !important; width: 98% !important; box-shadow: 2px 2px 3px #000; }
.site .title { background: #EEE !important; font-size: 20px !important; }
.site td.content { background: #FFF !important; }
.sidebar { display: none; }

.navigation { border: 1px solid #DDD !important; padding: 3px 7px !important; }

/* topics */
#topics { border: 1px solid #777 !important; }
.foren { background: #EEE !important; border: 1px solid #777 !important; border-collapse: collapse !important; }
.foren tr:nth-child(odd) td, .foren tr:nth-child(odd) td * { background: #F4F4F4 !important; }

/* topic */
.author, .text { padding: 3px 7px !important; line-height: 1.4em; }
.foren td { border: 1px solid #999 !important; }
.foren tr:hover td, .foren tr:hover td * { background: #FFF3A4 !important; }
.foren tr.message:hover td, .foren tr.message:hover td * { background: none !important; }
.head { border-bottom: 1px solid #CCC !important; }
.head:before { content: "Titel: "; color: #777; font-weight: normal; font-size: 10px; }

.foren .cat td, .foren tr.cat:hover td { background: #232 !important; }
.foren .cat .name { background: #232 !important; color: #FFF !important; padding: 0 7px !important; line-height: 1.4em; }

#new_forum_message { display: block !important; margin: 20px auto !important; background: #EEE !important; width: 750px !important; padding: 7px !important; border-radius: 3px; }

#new_forum_message .form td { background: #EEE !important; }

.site .impressum div { text-align: center !important; padding: 7px !important; }
}
