css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.igelmetal.de") {
/* Farben ersetzen */
body, #wrapheader {
  background: #FFF !important;
  color: #000 !important}

#logodesc { border: 0 !important }
#wrapheader { background-image: none !important }

/* war: #400 */
input, textarea, select, #wrapnav, .catdiv,
.spacer, .post, .btnfile, #wrapheader {
  background: #EFEFEF !important}

/* war: #F0F000 */
th  {background: #FFF !important}

/* war: #C00 */
.row1, .codetitle {background: #EFEFEF !important}

/* war: #A00 */
.row2, .helpline {background: #DDD !important}

/* war: #800 */
hr, .row3, .btnbbcode, .btnmain, .btnlite,
.attachcontent, .cat {
  background: #BBB !important}

/* war: #FFF */
h1, input, textarea, select, .btnbbcode,
p.moderators, .postauthor, .postdetails,
.error, .quotetitle, .attachtitle,
.quotecontent a, .codetitle, .codecontent,
.syntaxbg , .syntaxhtml {
  color: #333 !important}
  
/* war: yellow, F0F000 */
.error, .quotecontent, .attachcontent {
  color: #000 !important}

a {color: #0066EF !important}

.quotecontent, .codecontent {
  background: #C0C0C0 !important;
  border: 1px solid #666 !important;
  opacity: 0.9 !important}


/* Schickere Schriftarten */
html {
  font-family: "DejaVu Sans", Calibri, Helvetia, sans-serif !important}
code, pre {
  font-family: 'DejaVu Mono', Monaco, 'Courier New', monospace !important}


/* Bilder verstecken */
img {
  opacity: 0.05 !important;
  border: 2px solid #000 !important;
} 

/* Ausnahme: Avatare, Smileys und Forum-Bildchen */
img[src*="subsilver2"], img[src*="images/smilies"], img[src*="images/spacer.gif"] {
  border: 0 !important;
  opacity: 1 !important}
  
img[alt*="Benutzeravatar"] {
  display: none !important}
  
/* Default-Logo: site_logo.gif; schickes Logo: forum_header.jpg */
img[src*="site_logo.gif"], img[src*="forum_header.jpg"] {
  opacity: 0 !important} 

/* Bilder beim Hover wieder sichtbar machen */
img:hover {
  opacity: 1.0 !important;
}
img[src*="site_logo.gif"]:hover, img[src*="forum_header.jpg"]:hover {
  opacity: 0.6 !important;
}
  

/* Signaturen verstecken */
span.postbody {
  display: none !important;}

/* Spoiler */
.quotecontent[style*="color"] {
  color: #C0C0C0 !important}

}
