css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.com"), 
               domain("io9.com"), 
               domain("gizmodo.com") {

#navigation, 
#rightcontainer .cn_pager, 
#switch_blogview, 
.gnc_external, 
#rightbar_pin_container, 
#postTransitionOverlay, 
#rightcontainer .inner, 
#date-nav .ui-dropdown, 
#date-nav .search, 
#date-nav .date { 
    display: none !important; }

#rightwrapper {
    position: absolute !important; 
/*    left: 340px !important; */
    right: 0 !important;
    top: -85px !important; }

#rightcontainer { 
    position: relative !important; }

#rightcontainer .wrap { 
    overflow: visible !important; }

#rightbar_scroller { 
    height: 0 !important; }

#date-nav { 
    background-color: transparent !important; 
    background-image: none !important; 
    border: none !important; }

#main-container { 
    width: 90% !important; }

#main-container > .inner, 
#container, 
.gmgrid, 
.gmgrid .grid-full, 
#comments, 
#comments .thread, 
#comments .comment, 
#postTransitionOverlay, 
div.mainContent {
    width: 100% !important; }

#comments .replies {
    width: 90% !important; 
    margin-left: auto !important;
    margin-right: auto !important; }

.gmgrid .gmg-1 { 
    width: 45% !important; }

.gmgrid .gmg-q { 
    width: 20% !important; 
    margin-right: 2% !important; }
}
