css:
@-moz-document 
domain("advancedaquarist.com") {
#visual-portal-wrapper {width: 480px !important;margin-left:auto !important;margin-right: auto !important;}
#portaltab-bookstore, #portaltab-sponsors, #portaltab-advertise, #portaltab-moreinfo, #portaltab-contact, #portaltab-pdf {display:none !important;}
.tileFooter .twitter, .tileFooter .facebook {float:left !important;clear:both !important;margin-top:10px !important;margin-left: 0px !important;padding-left: 0px !important;}
.tileFooter .facebook {margin-bottom: 10px !important;}
#portal-column-two {clear:both;}
#leaderboard {display: none;}
#latest-issue-dropdowns2 {display:none;}
#slider-wrapper-nivo {display:none;}
.ad-support {display:none;}
.advertisement-banner {width:480px;height:60px;}
.tocTxt {width:300px !important;float:left !important;}
#content, #portal-column-content {width:480px !important;}
#portal-column-two {display:none;}
#portal-searchbox {display: inline !important;margin-top:0 !important;}
.searchSection {display:none;}
#facebook-likes {clear: both !important;position:relative !important;top:0;padding-bottom:10px;}
#facebook-likes, #id-comments-number, .twitter {width:480px !important;float:left !important;clear: both !important;margin-left:0 !important;}
.twitter {padding-top:10px;}
#my-timeline {display:none;}
#my-googlemap {display:none;}
iframe, object {width:480px !important;}
table {width:480px !important;}
.photo-wrapper img, .multi-photo-wrapper img {width:480px;margin:0 !important;}
.photo-wrapper .caption {text-align: left !important;}
.section-blog #portal-column-content .blogImageLarge, .section-blog #portal-column-content .tileImage-large {width:480px !important;height: auto !important;}
.fbt {margin-left:0px !important;}
.stumbleupon, .twitter {display:block !important;width:480px !important;clear:both !important;padding-bottom:10px !important;}
.permalink-disqus {display:block !important;width:480px !important;clear:both !important;margin-bottom:10px !important;}
#id-comments-number {width:480px !important;display:block !important;clear:both !important;margin-top:10px !important;}
#id-comments-number .twitter-share-button {display:block !important;margin-top:10px !important;}
}
