#visual-portal-wrapper {width: 100% !important;margin-left:auto !important;margin-right: auto !important;}
#portal-top {margin-left: 0px !important;margin-right: 0px !important;padding-left: 0px !important;padding-right: 0px !important;}
#portal-column-two {clear:both;}
#leaderboard {display: none;}
#latest-issue-dropdowns2 {display:none;}
