css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.com") 
{

#header
{
    width: 980px !important;
    border-left: 2px solid #CAD2A7 !important;
    border-right: 2px solid #CAD2A7 !important;
}

#rightbar-header
{
    margin-left: 0px !important;
    border-left: 2px solid #CAD2A7 !important;
    width: 338px !important;
}

.illustration
{
    margin-right: 10px !important;
}

.post-body p
{
    margin-left: 310px !important;
}

.illustration.top
{
    margin-right: 0px !important;
    border-right: 2px solid #cad2a7 !important;
    margin-bottom: 0px !important;
}

#page
{
    border-left: 2px solid #CAD2A7 !important;
    border-right: 2px solid #CAD2A7 !important;
}

.post .content
{
    margin-top: 0px !important;
    margin-right: 0px !important;
}

.post .info
{
    width: 328px !important;
    margin-top: 2px !important;
    padding-top: 10px !important;
    padding-left: 10px !important;
}

.post.classic
{
    padding-top: 0px !important;
}

.post .marked-head
{
    width: 980px !important;
    text-align: right !important;
    height: 0px !important;
}

.post .marked-head a
{
    background: #EBEEDD !important;
    padding: 11px !important;
    margin: 0px !important;
}

.post-body p
{
    margin-left: 10px !important;
}

.post
{
    display: table !important;
}

.post:nth-child(odd)
{
    background: white !important;
}

.post:nth-child(even)
{
    background: #F8FAF2 !important;
}

#page
{
    background: #F8FAF2 !important;
}

.ad_perma-panorama
{
    margin: 0px !important;
}

.info.top
{
    margin-top: 0px !important;
    width: 338px !important;
    border-top: 2px solid #CAD2A7 !important;
}

.post.top p
{
    margin-left: 10px !important;
}


.post.top .marked
{
    width: 980px !important;
    height: 0px !important;
    margin-right: -340px !important;
    margin-top: 0px !important;
}

.post.top .post-meta
{
    display: none !important;
}

.post.top .taglink
{
    padding: 11px !important;
    margin-left: 818px !important;
    width: 140px !important;
    background: #EBEEDD !important;
}

.post.top
{
    background: white !important;
}

.post.top h1
{
    padding-left: 10px !important;
    margin-top: -30px !important;
}

.page-post .post-body p
{
    margin-left: 10px !important;
}

#rightwrapper, #rightcontainer, #rightcontainer .wrap
{
    width: 342px !important;
}

#rightbar_scroller, .rightBarControl.cn_pager
{
    margin-left: 5000px !important;
}

.gmgrid .grid-full
{
    width: 976px !important;
    margin-left: 0px !important;
}

.topchrome
{
    border-left: 2px solid #CAD2A7 !important;
}

.imgwrap.abovewrap
{
    border-left: 2px solid #CAD2A7 !important;
    margin-left: -2px !important;
}

#date-nav
{
    width: 340px !important;
    border-right: 2px solid #CAD2A7 !important;
}

.gmgrid .grid-full
{
    position: relative !important;
    margin-top: -13px !important;
    padding-top: 13px !important;
}

.gmgrid .grid-full .post-supp
{
    position: absolute !important;
    top: -20px !important;
    right: 0px !important;
    width: 336px !important;
}

.related-heading
{
    position: absolute !important;
    top: 46px !important;
    right: 0px !important;
    background: #F8FAF2 !important;
    width: 316px !important;
    padding: 10px !important;
}

.gmgrid .grid-full .related
{
    position: absolute !important;
    top: 90px !important;
    right: 0px !important;
    width: 316px !important;
    padding: 10px !important;
    background: #F8FAF2 !important;
    height: 248px !important;
    border-bottom: 2px solid #CAD2A7 !important;
}

.gmgrid .grid-full .taglink
{
    background: white !important;
    position: absolute !important;
    top: 320px !important;
    right: 0px !important;
    padding-top: 10px !important;
    padding-bottom: 10px !important;
    width: 200px !important;
    text-align: center !important;
    z-index: 1 !important;
    border-left: 2px solid #CAD2A7 !important;
    border-top: 2px solid #CAD2A7 !important;
}    

#comments
{
    width: 976px !important;
}

.thread
{
    width: 976px !important;
}

.thread .comment
{
    width: 956px !important;
    padding-left: 10px !important;
    padding-right: 10px !important;
}

.replies .comment, .cn_togglereplies
{
    background: #EBEEDD !important;
}

.cn_togglereplies
{
    padding-top: 5px !important;
    padding-right: 10px !important;
}

#postform_widget .form_part, #postform_widget .form_part .header
{
    width: 956px !important;
}

.cn_entry
{
    width: 936px !important;
}

.sitescontainer
{
    width: 980px !important;
    margin-top: 0px !important;
}

.sitescontainer .threed
{
    margin-top: 0px !important;
}

.gmgrid .postContainer
{
    width: 976px !important;
}

#gallery_container, .gallery_image
{
    width: 976px !important;
    height: auto !important;
}

.gallery_img
{
    width: 976px !important;
}

#gallery_container .lyteboxLink a
{
    text-align: left !important;
}

.gallery_image
{
    margin-left: -2px !important;
    border-right: 2px solid #CAD2A7 !important;
    border-left: 2px solid #CAD2A7 !important;
}

#thumb_navigation
{
    width: 956px !important;
    padding-left: 10px !important;
    padding-right: 10px !important;
}

#thumb_list_window
{
    width: 916px !important;
}

.gmgrid .headline
{
    margin-left: 10px !important;
}

.mainContent:not([id="comments"])
{
    margin-top: -13px !important;
    padding-top: 13px !important;
    width: 976px !important;
    border-right: 2px solid #CAD2A7 !important;
    border-left: 2px solid #CAD2A7 !important;
}

.gmgrid
{
    width: 976px !important;
}

.gmgrid .grid-full
{
    z-index: 2 !important;
}

#userMenu
{
    z-index: 3 !important;
}

#sitetags
{
    width: 980px !important;
}

.site
{
    margin-right: 0px !important;
    margin-bottom: 0px !important;
    width: 121px !important;
}

#footer
{
    width: 980px !important;
}

#page
{
    margin-bottom: 0px !important;
}

.classic_frontpage #search_content
{
    width: 960px !important;
    padding-left: 10px !important;
    padding-right: 10px !important;
}

#thumb_navigation
{
    position: relative !important;
    z-index: 2 !important;
}
}
