css:
@-moz-document domain("forums.penny-arcade.com") {

a.PhotoLink img.ProfilePhotoBig {display:none !important;}

div#Frame {
background: none #010E21 !important;
}
div#footer {
background: none #010E21 !important;
}

ul.DataList.CategoryList li.Item table td.Count,
ul.DataList.Discussions li.Item table td.Count
{font-size: 15px !important;}
   
div.ProfileMenu a.SignInPopup, div.ProfileMenu a.Profile {color:#FFFFFF !important;}

div.Breadcrumbs a, div.Breadcrumbs span {color:#FFFFFF !important;}

div.ProfileMenu a.Inbox {background-color:#4080B9 !important;}

div.MiniPager div.MiniPagerArrow {display:none !important;}

div.MiniPager a, div.MiniPager span.Elipsis, div.PageControl a, div.PageControl span.Ellipsis {
background-color:#4080B9 !important;
border-right:1px solid #4060FF !important;
}

div.MiniPager *:last-child {border-right: none !important;}

div.MiniPager a.GoToPageLink, div.MiniPager a.GoToPageLink:hover, div.PageControl a.Next, div.PageControl a.Next:hover, div.PageControl span.Next, div.PageControl span.Next:hover{
background:url("http://i.imgur.com/HYV8Y.png") no-repeat scroll center center #4080B9 !important;
}

.NumberedPager a.Highlight {background-color: #004E9B !important;}

div.PageControl a {
color:#FFFFFF !important;
text-shadow:0 1px 0 #4B5618 !important;
}

div.PageControl div.GoToPage.Hidden {background-color:#4080B9 !important;}

div.Comment blockquote.UserQuote{
border-color:#000000 !important;
border-style:solid !important;
border-width: 1px !important;
background-color:#4080B9 !important;
}

div.QuoteText {border: none !important;}

div.Photo span.Rank {display:none !important;}

td.Author div.Photo {
width:80px !important;
margin-left:auto !important;
margin-right:auto !important;
}

.NumberedPager a:nth-child(2), .NumberedPager span:nth-child(2) {border-radius:3px 0 0 3px !important;}

}
