css:
@-moz-document domain("forums.penny-arcade.com") {
body {
    background: #999999 !important;
    font-family: Verdana !important;
    min-width: 920px;
}
.content {
    width: 990px;
    margin: 0 auto;
}
.nav a:hover {
    background-position: bottom left !important;
}
#bb .brands a {
    margin-right: 30px !important;
    padding-top: 9px !important;
    color: #0063bf !important;
    display: block !important;
    height: 21px !important;
}
#bb .brands a:hover,
#bb .join a:hover {
    color: #ff7600 !important;
}
#Frame {
    background: #999999 !important;
    overflow: hidden;
}
.React.Infraction:hover {
    color: #dddd00 !important;
}
.MenuItems a,
.MenuItems a:link,
.MenuItems a:visited,
.MenuItems a:active {
    color: white !important;
}
.Section-Conversation .PanelInfo li {
    background: #283560 !important;
}
.AddPeople form div {
    background: #283560 !important;
}
.MessageFormWrap {
    background: #092655 !important;
    padding: 8px;
}
.Preview {
    background: #283560 !important;
    color: white !important;
    min-height: 100px;
    padding: 4px;
}
.EditComment a {
    color: white !important;
}
.bbcode_acronym {
    border-bottom: 1px dotted white !important;
    cursor: help;
}
.bbcode_acronym .bbcode_smiley {
    border-bottom: 1px dotted white !important;
    cursor: auto;
}
.Section-Conversation .PanelColumn .Box > h4,
body.Badge .ContentColumn > h1,
body.Badge .ContentColumn > h2,
#SharedFingerprint h4,
.Box.BadgeGrid h4,
.CommentForm > h2,
body.Discussion .PageTitle h1,
body.Discussions .HomepageTitle,
h1.H,
h2.H {
    color: #fff;
    box-shadow: none !important;
    font-size: 13px;
    line-height: 1.7;
    padding: 4px 8px;
    margin: 10px 0 0;
    text-shadow: none !important;
    background: #324580 !important;
    border: none !important;
    border-top: 1px solid black !important;
    border-right: 1px solid black !important;
    border-left: 1px solid black !important;
}
.Box.BadgeGrid .PhotoGrid {
    background: #092655 !important;
    padding: 6px 5px 8px !important;
    margin-left: -1px !important;
    margin-right: 0px !important;
    margin-top: 0px !important;
}
.Item.Read {
    background: none repeat scroll 0 0 #F3F3F3;
    opacity: 1 !important;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: Verdana !important;
    font-weight: bold;
    margin: 5px 0;
}
.DataList.ChildCategoryList h3:after {
    content: ", ";
    color: gray !important;
}
.DataList.ChildCategoryList li,
.DataList.ChildCategoryList .ItemContent,
.DataList.ChildCategoryList h3,
.DataList.ChildCategoryList .Title {
    background: none !important;
    border: none !important;
    display: inline;
    padding: 0;
}
.DiscussionsTable .Unread .HasNew {
    font-weight: bold !important;
    text-shadow: none !important;
}
table.PreferenceGroup tbody tr:hover td {
    background: none !important;
}
.Item .Author a {
    font-size: 13px !important;
    font-weight: bold;
}
.Item .Author a:hover {
    text-decoration: none !important;
}
.Rank {
    display: block;
    position: absolute;
    left: 7px !important;
    top: 22px;
    color: orange !important;
    font-weight: bold;
    text-transform: Capitalize !important;
}
.Item .OptionsTitle {
    visibility: visible !important;
    margin-top: 3px !important;
}
.ReactSprite {
    display: none !important;
}
.RecordReactions + .Reactions,
.Reactions {
    margin-top: 10px !important;
}
.RecordReactions {
    display: none;
}
.Reactions {
    position: absolute !important;
    font-size: 11px;
    line-height: 14px;
    right: 6px !important;
    bottom: 6px !important;
}
.Item .Reactions > * {
    visibility: visible !important;
}
.PopList .Item:nth-of-type(odd) {
    background: #092655 !important;
}
.Head .MeBox .SignInLinks a,
.ForumMenu a {
    font-size: 13px !important;
    line-height: 24px;
}
.ForumSearch .InputBox {
    background: url("http://forums.penny-arcade.com/themes/pennyarcade/design/images/sprites.png") no-repeat scroll 5px -747px #FFFFFF !important;
    color: gray !important;
}
.UserBox .Username,
.MeBox .Username {
    font-size: 13px !important;
    font-weight: bold;
    vertical-align: top;
}
#ConversationForm label {
    color: white !important;
    display: block;
    font-size: 13px !important;
    font-weight: bold;
    text-align: left;
}
.FormWrapper {
    background: #092655 !important;
    margin: 0;
    padding: 13px 20px;
}
.bookmarked #Body .ContentColumn {
    margin: 50px 20px !important;
}
.SearchResults {
    color: white !important;
}
.NumberedPager a.Highlight {
    background: gray !important;
}
.UserReactionWrap .ReactSprite {
    bottom: -2px;
    position: absolute;
    right: -15px !important;
}
.MenuItems {
    background: #283560 !important;
    border: solid 1px black !important;
    border-radius: 0px !important;
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    font-size: 11px;
    line-height: 100%;
    padding: 5px 0;
}
.FlyoutMenu {
    width: 300px;
    background: #283560 !important;
    color: white !important;
    border-radius: 0px !important;
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    -webkit-box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    border: solid 1px black !important;
}
.FlyoutMenu a {
    color: #00419b;
}
.UserSpoiler {
    background: none !important;
    border: 1px dashed gray !important;
}
.SpoilerToggle {
    color: black !important;
    font-weight: normal !important;
    width: 80px;
}
.Reactions a {
    color: gray !important;
    /*line-height: 11px !important;*/
}
.AlertMessage {
    border: none !important;
}
.AuthorTitle {
    color: gray !important;
    display: inline-block !important;
    width: 120px !important;
    padding-top: 4px !important;
    white-space: normal !important;
    line-height: 1em !important;
}
.AuthorLocation {
    color: gray !important;
    display: inline-block !important;
    width: 120px !important;
    padding-top: 4px !important;
    white-space: normal !important;
    line-height: 1em !important;
}
/*.Rank{
Display:none !important;
}*/

pre {
    background: #555 !important;
    border-radius: 0px !important;
}
blockquote.Quote,
blockQuote.UserQuote {
    background: rgba(0, 0, 0, 0.15) !important;
    border-left: 4px solid rgba(0, 0, 0, 0.3) !important;
}
.EmoticonContainer {
    position: absolute;
    top: 21px;
    right: 0;
    border: 1px solid #aaa;
    border-bottom: 0;
    border-right: 0;
    width: 391px;
    text-align: left;
    float: right;
    box-shadow: none !important;
    z-index: 1000;
}
.EmotifyDropdownActive {
    background-color: #283560 !important;
}
.EmoticonBox {
    background-color: #283560 !important;
    background-position: center center;
    border: 1px solid #aaa;
    border-top: 0;
    border-left: 0;
    display: block;
    float: left;
    height: 22px;
    width: 22px;
    overflow: hidden;
    cursor: pointer;
}
.HasNew {
    background: none !important;
    border-radius: 2px 2px 2px 2px;
    color: white !important;
    font-size: 9px;
    font-weight: bold;
    line-height: 1;
    padding: 3px;
    white-space: nowrap;
}
#footer {
    background: #010d21 !important;
    text-align: center;
    padding: 40px 0;
}
/* Begin Vanilla CSS */
/* Globals */

a,
a:hover {
    color: #FFFFFF !important;
    text-decoration: none;
}
a:hover {
    text-decoration: underline;
    color: #F9A906 !important;
}
/* Width */

.Row {
    width: auto;
}
#Panel {
    display: none;
}
body.Conversations.Messages #Panel,
body.Profile #Panel,
body.Activity #Panel {
    display: block;
    width: 250px;
    margin: 0 0 0 20px;
}
#Body .ContentColumn {
    margin: 0 20px;
}
body.Conversations.Messages #Body .ContentColumn,
body.Profile #Body .ContentColumn,
body.Activity #Body .ContentColumn {
    margin: 0 20px 0 290px;
}
#Head {
    padding: 0 !important;
    background: transparent;
    color: inherit;
    float: right;
    margin: 10px 20px 0 0;
}
/* Forum Menu Search Breadcrumbs */

.ForumSearch,
.ForumSearch div {
    display: inline-block;
}
.ForumSearch .InputBox {
    padding: 2px 0 2px 20px;
    border: 1px solid #888;
    border-radius: 10px;
    -webkit-border-radius: 10px;
    color: #999;
    width: 170px;
    font-size: 11px;
}
.ForumSearch .InputBox:focus {
    color: #000;
}
.ForumSearch .Button {
    display: none;
}
.ForumMenu {
    font-size: 14px;
    display: inline;
}
.ForumMenu li {
    display: inline-block;
    border: none !important;
    border-width: 0 0 0 1px;
    padding: 0 0 0 10px;
    margin: 0 0 0 10px;
}
.ForumMenu li:last-child {
    border-width: 0 1px;
    padding: 0 10px;
    margin: 0 10px;
}
#Head a,
#Head a:hover {
    text-decoration: none;
    color: #032255;
}
#Head .MenuItems a {
    font-weight: normal;
}
.Breadcrumbs {
    font-size: 12px;
    line-height: 24px;
    margin: 4px 0 0 0;
    font-weight: bold;
    color: black;
    width: auto;
}
.Breadcrumbs a {
    color: #0055A2;
    text-decoration: underline;
}
.Breadcrumbs a:hover {
    text-decoration: none;
    color: #F9A906 !important;
}
.BreadcrumbsWrapper {
    margin: 10px 20px;
}
/* Headings */

.HomepageTitle,
.PageDescription {
    display: none;
}
body.Discussions .HomepageTitle {
    display: block;
}
.CommentForm > h2,
body.Discussion .PageTitle h1,
body.Discussions .HomepageTitle,
h1.H,
h2.H {
    color: #fff;
    box-shadow: none !important;
    font-size: 13px;
    line-height: 1.7;
    padding: 4px 8px;
    margin: 10px 0 0;
    text-shadow: none !important;
    background: #324580 !important;
    border: none !important;
    border-top: 1px solid black !important;
    border-right: 1px solid black !important;
    border-left: 1px solid black !important;
}
h1.H a,
h2.H a {
    color: #fff;
    text-decoration: underline;
}
h1.H a:hover,
h2.H a:hover {
    text-decoration: none;
}
.PhotoWrapSmall {
    display: none !important;
}
/* Categories Page */

.DataTable thead td {
    background: black !important;
    border-bottom: none !important;
    color: #FFFFFF;
    font-family: verdana !important;
    letter-spacing: normal !important;
    line-height: normal !important;
    padding: 4px 4px 4px !important;
    text-shadow: none !important;
    text-transform: none !important;
    font-weight: normal !important;
    font-size: 11px !important;
}
.DataTable thead td:first-child {
    padding-left: 15px;
}
.DataTable thead td.CheckBoxColumn {
    padding: 4px;
    vertical-align: middle;
}
.DataTable .Item td {
    background: #092655 !important;
    border-top: none !important;
    border-bottom: 1px solid #000000 !important;
}
.DataTable .Item:nth-of-type(odd) td {
    background-color: #fff;
}
td.CategoryName {
    padding-left: 15px;
}
.CategoryName a:hover {
    text-decoration: none !important;
    color: #F9A906 !important;
}
.CategoryName a {
    text-decoration: underline !important;
}
.Item td.CategoryName {
    background-image: none !important;
    background-position: left center;
    background-repeat: repeat-y;
}
.CategoryName h3 a {
    font-family: Verdana !important;
    color: white !important;
    font-size: 13px !important;
    font-weight: bold !important;
    text-shadow: none !important;
}
.Item.Unread td.CategoryName {
    background: url('http://forums.penny-arcade.com/themes/pennyarcade/design/images/sprites.png') !important;
    background-position: 0 -0px !important;
    background-size: 5px 10000px !important;
    background-repeat: no-repeat !important;
    background-color: #092655 !important;
}
tbody td.BigCount {
    font-size: 13px !important;
    font-weight: normal !important;
    color: #FFFFFF !important;
    font-family: Verdana !important;
    vertical-align: middle;
    text-transform: lowercase !important;
}
.CategoryTable .LatestPost a {
    color: white !important;
}
.CategoryTable .LatestPost a:hover {
    color: #F9A906 !important;
    text-decoration: none !important;
}
.CategoryTable a:hover {
    color: #F9A906 !important;
}
.CategoryTable td {
    vertical-align: middle;
}
/* Discussions Page */

.ChildCategoryList .Item {
    float: none;
    width: auto;
    padding: 8px;
}
.ChildCategories {
    border-top: none !important;
    display: block;
    color: white !important;
}
.ChildCategoryList .ItemContent {
    padding: 0;
}
.HomepageTitle {
    display: none;
}
/* I've read it */

.DiscussionsTable .Item.Read td:first-child {
    background-position: left center;
    background-repeat: repeat-y;
}
/* There are new messages since I last read it */

.DiscussionsTable .Item.Unread td:first-child {
    background: url('http://forums.penny-arcade.com/themes/pennyarcade/design/images/sprites.png') !important;
    background-position: 0 -0px !important;
    background-size: 5px 10000px !important;
    background-repeat: no-repeat !important;
    background-color: #092655 !important;
}
/* I've never read it */

.DiscussionsTable .Item.New td:first-child {
    background: url('http://forums.penny-arcade.com/themes/pennyarcade/design/images/sprites.png') !important;
    background-position: 0 -0px !important;
    background-size: 5px 10000px !important;
    background-repeat: no-repeat !important;
    background-color: #092655 !important;
}
.DiscussionsTable .Item.Read,
.DiscussionsTable .Item.Read td.BigCount,
.DiscussionsTable .Item.Read a {
    color: white !important;
    font-weight: normal !important;
}
.DiscussionsTable .Item.Read a:hover {
    text-decoration: none !important;
    color: #F9A906 !important;
}
.DiscussionsTable .HasNew {
    display: none;
}
.DiscussionsTable .CheckBoxColumn {
    padding: 4px 0 4px 10px !important;
}
.DiscussionName a.Title {
    font-family: Verdana !important;
    color: #FFFFFF !important;
    font-size: 13px !important;
    font-weight: bold !important;
    text-shadow: none !important;
    text-decoration: underline !important;
}
.DiscussionsTable .FirstUser a,
.DiscussionsTable .LastUser a {
    color: white !important;
    font-weight: normal !important;
}
.DiscussionsTable a:hover {
    text-decoration: none !important;
    color: #F9A906 !important;
}
.DataTable .BlockColumn-User {
    width: 150px !important;
}
.DiscussionsTable td.BigCount {}
.MenuItems a:hover {
    text-decoration: none !important;
}
.DiscussionsTable {
    position: relative;
}
a.Bookmark,
a.Bookmarked,
a.Bookmarking {
    margin: -9px 10px 0 0;
    height: 22px;
}
.DiscussionsTable a.Bookmark,
.DiscussionsTable a.Bookmarked,
.DiscussionsTable a.Bookmarking {
    right: 0;
    position: absolute;
    margin-top: -8px;
}
a.Bookmark,
a.Bookmark:hover {
    background-position: top center;
}
a.Bookmarked,
a.Bookmarked:hover {
    background-position: center center;
}
a.Bookmarking,
a.Bookmarking:hover {
    background-position: bottom center;
}
.PageTitle .Options {
    margin: 7px;
}
.OptionsTitle {
    width: 18px;
}
.DiscussionsTable .OptionsTitle {
    width: 21px;
}
.MessageList.Discussion .OptionsTitle {}
.DiscussionsTable td.DiscussionName {
    padding-left: 15px;
}
/* Hide the started by column if the window is narrow. */
/* If browser is less than 1280px wide. */

@media screen and (max-width: 1200px) {
    /* Hide the started by column of the discussions table. */
    
    .FirstUser {
        display: none;
    }
}
/* Pagers */

.MiniPager a,
.MiniPager span,
.NumberedPager a,
.NumberedPager span {
    font-family: Verdana !important;
    font-weight: normal !important;
    font-size: 9px !important;
    line-height: 10px !important;
    color: white !important;
    text-shadow: none !important;
    background: #092655 !important;
    border: 1px solid black !important;
    margin: 0 !important;
    padding: 4px 8px;
    display: inline-block;
    vertical-align: text-top;
}
.MiniPager a:first-child,
.NumberedPager a:first-child,
.NumberedPager span:first-child {
    border-radius: 0px !important;
}
.MiniPager a:nth-last-child(2),
.NumberedPager a:nth-last-child(2),
.NumberedPager span:nth-last-child(2) {
    border-radius: 0px !important;
    border: none;
}
.NumberedPager a.Next,
.NumberedPager span.Next {
    text-indent: 100px;
    padding: 1px;
    width: 16px;
    height: 16px;
    overflow: hidden;
    background: url("http://forums.penny-arcade.com/applications/dashboard/design/images/buttons.png") no-repeat scroll right -144px #092655 !important;
    -webkit-transform: rotate(-90deg);
    -moz-transform: rotate(-90deg);
}
.NumberedPager a.Previous,
.NumberedPager span.Previous {
    text-indent: 100px;
    padding: 1px;
    width: 16px !important;
    height: 16px;
    overflow: hidden;
    background: url("http://forums.penny-arcade.com/applications/dashboard/design/images/buttons.png") no-repeat scroll right -144px #092655 !important;
    -webkit-transform: rotate(90deg);
    -moz-transform: rotate(90deg);
}
.NumberedPager span.Ellipsis {
    cursor: pointer;
    text-indent: 100px;
    padding: 1px;
    width: 16px;
    height: 16px;
    overflow: hidden;
    background: url("http://forums.penny-arcade.com/applications/dashboard/design/images/buttons.png") no-repeat scroll right -144px #092655 !important;
}
.MiniPager a:hover,
.NumberedPager .Ellipsis:hover,
.NumberedPager a:hover {
    color: #F9A906 !important;
}
.NumberedPager a.Highlight {
    color: #66cde9;
    text-shadow: 0 1px 0 #000;
    background: #032255;
}
.MiniPageControl {
    display: inline;
}
.MiniPager {
    margin: 0 0 0 8px;
    vertical-align: text-top;
    display: inline-block;
    white-space: nowrap;
    position: relative;
    top: -3px;
    border: none !important;
}
.MiniPager a,
.MiniPager span {
    display: inline-block;
    line-height: 10px !important;
    color: #fff !important;
    text-shadow: none !important;
    background: #092655;
    border: none !important;
    padding: 4px;
    vertical-align: middle;
}
.MiniPager span.Elipsis {
    cursor: pointer;
    text-indent: 100px;
    padding: 1px;
    width: 16px;
    height: 16px;
    overflow: hidden;
    background: url("http://forums.penny-arcade.com/applications/dashboard/design/images/buttons.png") no-repeat scroll right -144px #092655 !important;
}
.MiniPager span.Elipsis:hover,
.MiniPager a:hover {
    background-color: #B3CE3A;
}
.PageControl {
    position: relative;
    display: inline-block;
    text-align: right;
}
#Content .BoxNewDiscussion {
    display: block;
}
.PagerWrap {
    overflow: visible;
}
.CommentsWrap .PagerWrap:last-child {
    margin: 15px 0;
    text-align: right;
}
.CommentsWrap .PagerWrap:last-child .Pager {
    float: none;
}
.GoToPage {
    border: 1px solid black !important;
    z-index: 300;
    color: white !important;
    text-shadow: none !important;
    background: #324580 !important;
    font-size: 10px;
    font-weight: normal;
    border-radius: 0px !important;
    display: inline-block;
    position: absolute;
    margin: -1px 2px 0 0;
    right: 0;
    padding: 1px 3px 2px;
}
.Hidden {
    display: none;
}
.MiniPageControl .GoToPage {
    background: #96ac31;
    border-top: 1px solid #82952a;
    margin-top: -1px;
    color: #fff !important;
    text-shadow: 0 1px 0 #4b5618;
}
.GoToPage input {
    width: 20px;
    font-size: 10px;
    line-height: 14px !important;
    text-align: center;
    border: none !important;
    box-shadow: none !important;
}
.NumViewing {
    font-size: 13px !important;
    text-transform: capitalize !important;
    font-weight: normal !important;
    letter-spacing: normal !important;
    color: gray !important;
    text-shadow: none !important;
    background: none !important;
    border-radius: 0px !important;
    padding: 0px !important;
    position: relative;
    margin-left: 15px !important;
    font-family: Verdana !important;
}
.NumViewing:before {
    content: "";
    border: none !important;
    display: none !important;
}
/* Big New Discussion Button, Button Groups */

.NavButton,
.NavButton:hover,
.NavButton:active,
.NavButton:focus,
.Button,
input.Button,
.BigButton,
.ButtonGroup.Open .Button.Handle,
.ButtonGroup.Open .NavButton.Handle {
    position: relative;
    background: #E67B00;
    border: none;
    font-family: Rockwell, Helvetica, Arial;
    color: #fff;
    text-shadow: 0 1px 1px #622e00;
    padding: 6px 8px 4px;
    line-height: 1;
    font-size: 14px;
    font-weight: normal;
    border-radius: 2px;
    -webkit-border-radius: 2px;
    top: -1px;
    box-shadow: inset 0 -1px 0 #fbddb5, 0 2px 0 #ba5700, 1px 3px 0 #0060b5, -1px 3px 0 #0060b5;
    -webkit-box-shadow: inset 0 -1px 0 #fbddb5, 0 2px 0 #ba5700, 1px 3px 0 #0060b5, -1px 3px 0 #0060b5;
}
.NavButton.Handle {
    border-radius: 0 2px 2px 0 !important;
    -webkit-border-radius: 0 2px 2px 0 !important;
}
.Button:hover,
.NavButton:hover,
input.Button:hover,
.BigButton:hover {
    color: #fff;
