css:
@-moz-document url-prefix('http://www.tumblr.com/dashboard'),
 url-prefix('http://www.tumblr.com/blog/'), url('http://www.tumblr.com/inbox'), 
 url-prefix('http://www.tumblr.com/tagged/'), url('http://www.tumblr.com/likes'), 
 url('http://www.tumblr.com/explore'), url-prefix('http://www.tumblr.com/liked/'), 
 url-prefix('http://www.tumblr.com/show') , url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/blog/"), url("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/tagged/"), url("https://www.tumblr.com/likes"), url("https://www.tumblr.com/explore"), url-prefix("https://www.tumblr.com/liked/"), url-prefix("https://www.tumblr.com/show") {

body{background:#2A2D30 repeat-x 50% 0% !important;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-image: url('http://media.tumblr.com/tumblr_lg1pg3hFD01qe461o.png')!important; 
background-color: #222 !important;}

#left_column .dashboard_options_form {
background: url('http://assets.tumblr.com/images/white_20.png') #000000;}

ol#posts li.notification{background-color: #000000 !important;
border-bottom: 1px solid #2F465C !important;
color: #A8BCCB !important;}

#right_column a#dashboard_switch_blog_menu_current:hover {background: transparent;}

#right_column .radar .content{background:transparent!important}

#right_column .radar .content .mask{background:transparent!important;}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(30,30,30, 1.0)  !important;}

#nav .nav_item.active{background-color:#353738;}

#left_column #posts .post .permalink{background-color:#2A2D30;}

#nav .nav_item.active, #nav .nav_item .nav_menu, 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel{
background-color:#454748;}

#footer, #footer a {
color:#dddddd !important;
background-image:none !important;}


.post.same_user_as_last:hover, .post.text_post:hover, 
.post.video:hover, .post.audio:hover, .post.photo:hover, 
.post.link:hover, .post.quote:hover, .post.conversation:hover, 
.post.is_reblog:hover, .post.submission:hover, .post.regular:hover, 
.post.question_post:hover, .post.note:hover {
  border-top-right-radius: 24px !important; 
  opacity: 1.00 !important;
}
.permalink {
  background: none !important;
  width: 16px !important;
  height: 16px !important;
  background: rgba(20,20,20, .85) !important;
  border-bottom-left-radius: 10px !important;
  border-top-right-radius: 24px !important;
  margin-top: 6px !important;
  margin-right: 6px !important;
}
.permalink:hover {background: rgba(127,139,152, 1.0 ) !important;}
}

@-moz-document url('http://www.tumblr.com/help'), 
url('http://www.tumblr.com/preferences'), url('https://www.tumblr.com/preferences'),
url-prefix('http://www.tumblr.com/following'), url-prefix('http://www.tumblr.com/spotlight'), 
url-prefix('https://www.tumblr.com/lookup'), url-prefix('http://www.tumblr.com/docs'),
url('http://www.tumblr.com/goodies'), url-prefix('http://www.tumblr.com/follow') , url("https://www.tumblr.com/help"), url-prefix("https://www.tumblr.com/following"), url-prefix("https://www.tumblr.com/spotlight"), url-prefix("https://www.tumblr.com/docs"), url-prefix("https://www.tumblr.com/follow") {
body{background: #2A2D30 repeat-x 50% 0% !important;}
} 

@-moz-document url-prefix('http://www.tumblr.com/dashboard/iframe?10'), url-prefix('https://www.tumblr.com/dashboard/iframe?10')  {
body{ background-color:transparent !important; }
}
