css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.penny-arcade.com") {

body                         { position: absolute !important; width: 80em !important; left: 50% !important; margin-left: -40em !important; }
pre                          { margin-top: 1em !important; }
.UserQuote                   { background-color: rgba(226, 226, 226, 0.2) !important; border: 1px solid #999 !important; margin-bottom: 1em !important; }
.UserSpoiler                 { background-color: transparent !important; border: 1px solid #999 !important; }
.SpoilerToggle               { background-color: transparent !important; border: 1px solid #999 !important; }
.PageControl                 { float: right !important; margin-right: 10px !important; }
.CategoryDescription         { display: none !important; }
.LatestPost                  { width: 400px !important; }
.OptionsMenu                 { left: -540px !important; }
.Rank                        { display: none !important; }
.CommentTable                { line-height: 1em !important; }
.ProfileUser div             { border-bottom: none !important; }
.Author div                  { border-bottom: none !important; }
.Message                     { line-height: 1.25em !important; }
.SpoilerText                 { border-left: 0 !important; }
.QuoteText                   { border: none !important; }
.Comment                     { margin-bottom: 0 !important; padding-bottom: 0 !important; }
.UserSignature               { margin: 0 0 1em 1em !important; padding: 0 !important; }

.CommentTable td.Author div.Photo a.ProfileLink img, .CommentTable td.Author div.Photo, .CommentTable td.Author div.Photo a.ProfileLink {
  height: 80px !important; 
  width: 80px !important; 
  }

ul.CategoryList li.Item a.PhotoLink img, ul.DataList.Discussions li.Item table td.User a.PhotoLink img { 
  display: none !important;
  }

}
