css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.blocket.se") {



#blocket {
  margin: 0 auto;
}

#index .footer_country,
#blocket #banner_index_top,
.box,
.searchbox_mini,
.trafikfonden_top,
#li #right_bar,
#blocket .top_items,
#vi .right_bar {
  display: none;
}

#li .list_mode_thumb,
#li .list_mode_list,
#vi .view .view_content,
#vi .view .header_bar,
#vi .single_image#media,
#li .list_footer_line,
#vi .view_footer,
#footer_gallery,
#footer_gallery #slider,
#li #pagination,
#vi .body,
#vi #view_menu,
#blocket #ehandel_iframe,
#puff_eframe .container {
  width: 100% !important;
}

#vi #view_menu .menu_list {
  width: 32%;
}

#puff_eframe .footer {
  width: 925px;
  padding: 5px 10px 0 0;
}

#vi .body {
  -moz-column-count: 2;
  -moz-column-gap: 1em;
  -webkit-column-count: 2;
  -webkit-column-gap: 1em;
}

#li .list_mode_thumb .item_row {
  margin: 0;
  padding: 5px 10px;
  width: 445px;
}
#li .list_mode_list .item_row {
  width: 445px;
  margin: 0;
  padding: 5px 10px !important;
}
#li .list_mode_thumb .item_row:hover {
  background: -moz-linear-gradient(100% 100% 90deg, #eee, #fff);
  background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#eee), to(#fff));
  background: -webkit-linear-gradient(#eee, #fff);
  background: -o-linear-gradient(#eee, #fff);
  border-radius: 5px;
}
#vi .view h2.subject_medium,
#li .list_mode_thumb .item_row .item_link {
  text-shadow: 0 1px 1px rgba(0, 0, 0, .15);
}
#li .image_content {
  border-radius: 5px;
}


#li .list_mode_thumb .item_row .desc {
  width: 240px;
}

#li .list_mode_list,
#li .list_mode_thumb {
  -moz-column-count: 2;
  -moz-column-gap: 1em;
  -moz-column-rule: 1px solid #C1BDAF;
  -webkit-column-count: 2;
  -webkit-column-gap: 1em;
  -webkit-column-rule: 1px solid #C1BDAF;
}


#li .list_mode_list .cat_geo {
  width: 250px;
  padding: 0 10px 0 0;
}

#vi .options_box {
  position: absolute;
  right: 15px;
  width: 200px;
  top: 106px;
}
#vi .options_box .store_box {
  width: 200px;
}
#all_pages a {
  padding: 0 5px;
}

#footer_gallery .item {
  width: 160px;
}



#index .background {
  height: 480px;
}

#index .regions {
  -moz-column-count: 2;
  -moz-column-gap: 1em;
  -moz-column-rule: 1px solid black;
  -webkit-column-count: 2;
  -webkit-column-gap: 1em;
  -webkit-column-rule: 1px solid black;
  width: 300px;
  position: absolute;
  top: 130px;
}



}
