css:
@-moz-document domain('neopets.com'){

#main #ban {border: none !important; background-image: url(http://i1222.photobucket.com/albums/dd491/membot/Themes/header_a-1.png) !important;}
#main #header {border: none !important; background-image: url(http://img825.imageshack.us/img825/8065/headerbld.png) !important;}
#main #footer {background-image: url(http://i1222.photobucket.com/albums/dd491/membot/Themes/footer-1.png) !important;}
a:link, a:visited, a:active {color: #1AA584 !important;}
a:hover {color: #137761 !important;}
.eventIcon {color: #D9F0EE !important;}
#header td.user.medText {color: #D9F0EE !important; padding-right: 190px !important;}
.eventIcon a:link, #header td.user.medText a:link {color: #9BD739 !important;}
.eventIcon a:visited, #header td.user.medText a:visited {color: #9BD739 !important;}
.eventIcon a:active, #header td.user.medText a:active {color: #9BD739 !important;}
.eventIcon a:hover, #header td.user.medText a:hover {color: #D9F0EE !important;}
#nst {color: #06241E !important;}
ul.dropdown {background: #1AA584 !important; border: none !important; opacity: 0.9 !important; filter: alpha(opacity=90) !important;}
li:hover ul.dropdown a, li.over ul.dropdown a {color: #06241E !important;}
li:hover ul.dropdown a:hover, li.over ul.dropdown a:hover {color: #000000 !important;}
.marqueeSlot, .marqueeSlot a:link, .marqueeSlot a:visited, .marqueeSlot a:active, .marqueeSlot a:hover {background-color: #83D6C2 !important; color: #FFFFFF !important;}
.marqueeSlotOn, .marqueeSlotOn a:link, .marqueeSlotOn a:visited, .marqueeSlotOn a:active, .marqueeSlotOn a:hover {background-color: #1AA584 !important; color: #FFFFFF !important;}
.sidebarHeader {background: #53516F !important; color: #FFFFFF !important; border: none !important;}
.sidebarTable {border: #53516F 2px solid !important;}
.contentModuleHeader, .contentModuleHeaderAlt {background: #1AA584 !important; color: #FFFFFF !important; border: none !important;}
.contentModuleTable {border: #1AA584 2px solid !important;}
.eventIcon a img {width: 0px !important; height: 25px !important; padding-right: 25px !important; background: url(http://i1222.photobucket.com/albums/dd491/membot/Themes/icons_e.png) no-repeat !important;}
#footer .sf, #footer .sf a:link, #footer .sf a:active, #footer .sf a:visited, #footer .sf a:hover {color: #D9F0EE !important;}
.gamesRoomDarkModule .rcModuleHeader {background: #1AA584 !important; color: #FFFFFF !important;}
.gamesRoomDarkModule .rcModuleContent {border-color: #1AA584 !important;}
.rcModuleHeader {background: #53516F !important; color: #FFFFFF !important;}
.rcModuleContent {border-color: #53516F !important;}

#main #footer td {text-align: center !important;}
.footerNifty {display: none !important;}
.copyright .sf {position: relative !important; left: 100px !important;}
.copyright .sf img {display: none !important;}
#template_nav img {opacity: 0 !important; filter: alpha(opacity=0) !important;}
.sidebarHeader a:link, .contentModuleHeader a:link, .contentModuleHeaderAlt a:link {color: #FFFFFF !important;}
.sidebarHeader a:visited, .contentModuleHeader a:visited, .contentModuleHeaderAlt a:visited {color: #FFFFFF !important;}
.sidebarHeader a:active, .contentModuleHeader a:active, .contentModuleHeaderAlt a:active {color: #FFFFFF !important;}
.sidebarHeader a:hover, .contentModuleHeader a:hover, .contentModuleHeaderAlt a:hover {color: #FFFFFF !important;}
.moreLink, .moreLinkAlt {border: #FFFFFF 1px solid !important;}
.sidebarModule, .contentModule {border: none !important;}
.eventIcon a img[alt~="Neomail"] {background-position: 0px 0px !important;}
.eventIcon a img[alt~="accepted"][alt~="NeoFriend"] {background-position: -25px 0px !important;}
.eventIcon a img[alt~="requesting"] {background-position: -50px 0px !important;}
.eventIcon a img[alt~="Battledome"][alt~="accepted"] {background-position: -75px 0px !important;}
.eventIcon a img[alt~="challenged"] {background-position: -100px 0px !important;}
.eventIcon a img[alt~="given"] {background-position: -125px 0px !important;}
.eventIcon a img[alt~="trade"] {background-position: -150px 0px !important;}
#main #footer a img[width="156"][height="46"] {display: none !important;}
#main #footer div[style="position:absolute;right:257px;top:52px;overflow:hidden;"] {display: none !important;}
#main #footer input, #main #footer select {color: #000000 !important;}
#gamesRoomContentWrap .rcModuleHeader a:link, #gamesRoomContentWrap .rcModuleHeader a:visited, #gamesRoomContentWrap .rcModuleHeader a:active, #gamesRoomContentWrap .rcModuleHeader a:hover {color: #FFFFFF !important;}
#gamesRoomContentWrap .moreLinkAlt a:link, #gamesRoomContentWrap .moreLinkAlt a:visited, #gamesRoomContentWrap .moreLinkAlt a:active, #gamesRoomContentWrap .moreLinkAlt a:hover {color: #FFFFFF !important;}
#gamesRoomContentWrap .btnCenter {color: #FFFFFF !important;}
#gamesRoomContentWrap .rcModuleTopLeft, #gamesRoomContentWrap .rcModuleTopRight, #gamesRoomContentWrap .rcModuleHeaderBg, #gamesRoomContentWrap .rcModuleBottomLeft, #gamesRoomContentWrap .rcModuleBottomRight {background-image: none !important; background: transparent !important;}
.rcModuleContentOuter {border: none !important;}


}
