/* AGENT_SHEET */
/* ▓▓ aBYTE: another Black YouTube Embodiment ▓▓
   ▓▓ v.3 - userstyle for www.youtube.com     ▓▓
   ▓▓ by vetinari - 2009/2010                 ▓▓
   ▓▓ contact: vetinari.userstyles@inode.at   ▓▓
   ▓▓ --------------------------------------- ▓▓
   ▓▓ last modified: 20.03.2011               ▓▓
   ▓▓_________________________________________▓▓ */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com"){
::-moz-selection { color: #B6AA8C!important;  background:#1f1f1f!important }

/*▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓global coloring font / borders▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓*/
#ticker, .yt-alert-promo{opacity: .75!important; color: #ccc!important; background: -moz-linear-gradient( top , #555555,#444444,#555555, #444444) #666 !important;}
.yt-alert-promo {-moz-box-shadow: 1px 1px 2px #222!important}
#ticker>*{ background: -moz-linear-gradient( top , #555555, #444444) #555 !important;}
#ticker *{ color: #ccc!important; }
.feedmodule-single-form-item .watched{opacity: 1!important}
*{border-color: #666!important; outline:none !important }
*:not([class*="button"]), *[class*="button-menu"] *{ color: #aaa!important}
a.focus, a{outline: 0px none !important;}
#watch-info .watch-expander-head:hover, #watch-info .yt-uix-expander-collapsed .watch-expander-head:hover{background: rgba(120,120,120,0.2)!important}
button::-moz-focus-inner{border:0px none!important} 
a:not([class*="button"]), a>span[class*="title"], a:not([class*="button"]) *:not([class*="stat"]), a.title[onclick], .clipper a[dir],.clipper .list-view-clipper a{ color: #A69B7C!important}
a[href*="/user/"], .smallText a *{color: #908880!important}
.smallText.grayText, .smallText.grayText *{color: #555!important}
a[onclick], .video-facets>span{color: #888!important}
*[class*="button-menu"] a:hover{color:#ddd!important}
button * {color: #aaa!important}
button, #quicklist, #quicklist *, select, #user_comments-body input[type="button"]{-moz-appearance: none!important; -moz-box-shadow: 0px 0px 0px #111!important;}
input, textarea, option{-moz-appearance: none!important;}
img.vm-sprite.vm-video-rating,  .main-tabs-promo img, .search-tip-icon{opacity: .6!important}
.main-tabs-promo img:hover{opacity: .9!important}
#masthead-search label, .edu-about-bar, #masthead-search p{border: 0px none #666!important}
#masthead-search>div{border: 0 none!important}
/*_________________________single colors___________________________*/
textarea.comments-textarea *{color: #999 !important}
a[class*="video"]>span.video-time{color: #aaa !important}
.quicklist-help *{color: #555!important}
 a:not([class*="button"])>span.video-in-quicklist, a:not([class*="button"])>span.count *{color: #99a!important}
a.watch-comment-atlink{color: #908880!important; font-style: italic!important}
/*_________________________borders___________________________*/
textarea.comments-textarea{border-width:1px!important; padding:3px 0 0 3px !important; margin:0 0 5px 0 !important;}
 input[type*="text"], input[type="password"], textarea, select, #vm-videos-search-form>input,#user_comments-body input[type="button"] {border:1px solid #666!important; -moz-border-radius:3px!important}
#playnav-chevron, .panel-tab-indicator-arrow,#compose-cell {border-color:transparent!important;}
/*▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓backgrounds▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓*/
/*_________________________transparent01___________________________*/
* {background-color: transparent !important}
body hr{background-color: #555 !important; border-color: #555!important}
#yt-admin, td[id*="heading-"], *[id*="channel"], #yts-article .pretty-box,#watch-channel-discoverbox .yt-uix-slider-body{background: transparent none !important}
/*_________________________colored no img___________________________*/
*[class*="button-menu"], .event-calendar-date, #vm-page-subheader,  textarea, .browse-tab-modifiers.yt-rounded, #watch-embed-code, input[type*="text"], input[type="password"], select, #dialog-body, #vm-videos-search-form>input, input[type="checkbox"], select option, input[type="radio"], .yt-alert-promo, #feed_all.experiment{background-color: #484848 !important}
#masthead-search-term, #completeTable{background-color: #555!important}
div.yt-uix-tooltip-tip-content, .dialog, .yt-alert, .video-in-quicklist{background-color: rgba(88,88,100,0.9)!important;}
#watch-info, #watch-actions-area, .edu-about-bar{background-color: rgba(20,20,20,0.4) !important}
#watch-info.expanded .watch-expander-head{margin:1px 3px 0px 2px!important; padding: 2px 8px!important}
#watch-video-count .watch-expander-head, .action-bar-ratings-stats{background-color: #333 !important}
.watch-view-count strong{ text-shadow: 1px 1px 0px rgba(255,255,255,0.2)!important;color: rgba(0,0,0,0.15)!important}
.watch-expander-head, span.video-time,.video-cluster-videos-container{background-color: #222 !important}
li.comment:nth-child(even){background-color: rgba(90,90,90,.14) !important; -moz-box-shadow:inset 1px 1px 0px rgba(128,128,128,.1)}
.yt-uix-slider-body{background-color: rgba(0,0,0,0.2) !important}
div.playlist-contents{background-color: rgba(0,0,0,0.7) !important}
.music-onebox, #search-section-header, #main-channel-content, #user_playlist_navigator, tr.even, #yt-admin-content>h2, .watch-ppv-vid, .promoted-videos{background-color: rgba(128,128,128,.1) !important}
li.comment.author-viewing{background-color: rgba(196,128,128,.13) !important}
li.comment:hover, .video-translation-links a, .video-list-item:hover, #watch-channel-discoverbox, .scrollbox-body, .vm-video-list .vm-video-position, #feed_all.experiment ul .current a, #feed_all.experiment ul .current a:hover{background-color: rgba(0,0,0,0.3) !important;}
.video-grid .video-list-item-linkselected {padding:5px 4px!important; display:block!important; overflow: hidden!important}
.video-grid .video-list-item-linkselected .video-thumb{-moz-box-shadow: 0px 0px 2px #A69B7C}
.quicklist-item .delete{background: #333 !important; -moz-border-radius: 2px!important; width:17px!important; height: 17px!important; top:4px!important; left: 84px!important}
.quicklist-item .delete:hover{background: #484848 !important}
#quicklist-tray-container .quicklist-item .delete>img{ top:4px!important; left: 3px!important; }

/*_________________________gradient no img___________________________*/
button:not([class*="arrow"]):not([class*="expander"]):not([class*="yt-uix-button-text"]):not([class*="close"]):not([class*="delete"]), #quicklist-bar-container, #user_comments-body input[type="button"]{background:-moz-linear-gradient( top , rgba(255,255,255,.25), rgba(255,255,255,.28), rgba(255,255,255,.07)) #000 !important;}
button.yt-uix-pager-selected{background:-moz-linear-gradient( top , #555555,#444444,#555555, #444444) #666 !important;}
div.likes-bar, .watch-sparkbar-likes{background:-moz-linear-gradient( top , #66bb88,#33aa44, #105511) #33aa44!important; -moz-border-radius: 3px 0px 0px 3px!important; border: 0!important; -moz-box-shadow: inset -1px 0px 0px rgba(0,0,0,0.3)}
div.dislikes-bar, .watch-sparkbar-dislikes{background:-moz-linear-gradient( top , #bb6688,#aa3344, #661011) #aa3344!important; -moz-border-radius: 0px 3px 3px 0px!important; margin-right: 0px!important;  -moz-box-shadow: inset 1px 0px 0px rgba(255,200,200,0.25)}
td>div.likes-bar, td>div.dislikes-bar{-moz-border-radius:3px!important; border: 1px solid #777!important; border-color: #222 #555 #555 #222!important;  -moz-box-shadow: inset 0px -3px 4px rgba(0,0,0,0.4)!important}
input[type="checkbox"]:checked{background:-moz-linear-gradient( top , #404040,#606060, #303030) #aa3344!important}
#headings, #quicklist-bar button, .ab-users-checks,a.video-thumb button:not([class*="arrow"]):not([class*="expander"]):not([class*="yt-uix-button-text"]):not([class*="close"]):not([class*="delete"]){background:-moz-linear-gradient( top , #333333,#444444, #282828) #666 !important;}
/*▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓more custom CSS▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓*/
/*_________________________custom button css___________________________*/
button.yt-uix-button-text{background:transparent !important; border: 0px none #555!important;-moz-border-radius:5px!important}
button.yt-uix-button-text:hover{background:rgba(128,128,128,0.15) !important;  color: #bbb!important}
button.yt-uix-button-text.yt-uix-button-active{background:#484848 !important;  color: #bbb!important; -moz-border-radius-bottomleft: 0px!important;  -moz-border-radius-bottomright: 0px!important;
-moz-box-shadow:inset 1px 1px 1px #222!important;}
.yt-uix-button-menu{-moz-box-shadow: 1px 2px 2px #111!important;}
button:not([class*="arrow"]):not([class*="expander"]):not([class*="yt-uix-button-text"]):hover{-moz-box-shadow: 0px 0px 4px #777!important}
input[type="checkbox"], input[type="radio"]{border: 1px solid #666!important; color: #999!important}
select[id]{-moz-box-shadow: -1px 0px 0px #666, 1px 0px 0px #666, 0px -1px 0px #666,  0px 1px 0px #666!important; border-color: #282828 transparent transparent #282828 !important;}
select[id] *:not(option){border-style: solid!important; border-width: 0px 0px 0px 1px!important; border-color: transparent transparent transparent #333!important;}
select[id] option{-moz-box-shadow:inset 1px 0px 0px #333!important; border: 0 none !important}
#search-btn, #masthead-search .yt-uix-button-group .start{-moz-border-radius:4px!important; border: 1px solid #666!important; padding: 0px 8px !important; height: 22px!important; margin: 0px 2px 2px 1px!important}
/*_________________________custom button menu css___________________________*/
.yt-uix-button-menu table{background-color: #404040!important}
.yt-uix-button-menu table a.yt-uix-button-menu-item:hover, ul.yt-uix-button-menu li span:hover{background-color: #585858!important}
ul.yt-uix-button-menu li:not(:last-child){border-bottom: 1px solid #404040!important}
li.yt-uix-button-menu-item-selected span{-moz-box-shadow:inset 1px 3px 3px #333 !important;}
#masthead-utility .floatL a:hover{text-decoration: underline!important}
#masthead-utility .floatL a.selected{text-shadow: 0px 0px 3px #aaa, 1px 1px 1px #111!important}
.yt-uix-pager .yt-uix-slider-num {color: #aaa!important}
/*_________________________subnav___________________________*/
.browse-tab-modifiers .subcategory.selected {-moz-box-shadow:inset 1px 1px 2px #111, inset 0px -2px 2px #2a2a2a!important; background-color: #404040 !important}
.browse-tab-modifiers .subcategory.first{-moz-border-radius: 0px!important; margin-left: 10px!important}
#masthead-subnav,#vm-playlist-actions a,  button.delete{background:-moz-linear-gradient( top , #333333,#444444, #282828) #666 !important;-moz-box-shadow: 0px 1px 0px #111!important}
/*_________________________browse categories___________________________*/
.browse-categories-side a{color: #aaa!important}
.browse-categories-side li{padding-top: 1px !important; padding-bottom: 1px !important; margin-top: 1px !important; margin-bottom: 1px !important; -moz-border-radius:10px!important}
.browse-categories-side li a{text-decoration: none!important}
.browse-categories-side li:hover{background-color: #585858!important}
.browse-categories-side .category-selected{background-color: #484848!important}
/*_________________________admin-sidebar___________________________*/
#yt-admin-sidebar li a, #yt-admin-sidebar .tab a , .ab-contact{color: #aaa!important; -moz-border-radius:10px!important; padding: 2px 10px !important; margin: 2px 2px 1px 0!important}
#yt-admin-sidebar li a.selected, #yt-admin-sidebar .tab.selected a, .ab-leftpane-sel{background: #484848!important}
#yt-admin-sidebar li a:hover,  #yt-admin-sidebar .tab a:hover, .ab-contact:hover{background: #585858!important; text-decoration: none!important}

/*_________________________click/hovercard___________________________*/
.yt-uix-clickcard-card-body-arrow, .yt-uix-clickcard-card-border-arrow{border-color: transparent !important;}
.yt-uix-hovercard-card-content, .yt-uix-clickcard-card-border{background: #222!important;}
.yt-uix-hovercard-card-content{-moz-border-radius:5px!important; }
.yt-uix-hovercard-card-body-arrow, .yt-uix-hovercard-card-border-arrow{background: transparent!important;border-color: transparent !important; border-width: 12px 0 12px 12px!important}
.yt-uix-hovercard-card-body-arrow, .yt-uix-clickcard-card-body-arrow{border-left-color: #222!important }
.yt-uix-hovercard-card-border-arrow, .yt-uix-clickcard-card-border-arrow{border-left-color: #666!important }
.yt-uix-clickcard-card-border{-moz-box-shadow: 1px 1px 2px #111!important}


/*_________________________video-cluster___________________________*/
.vCluster120WideEntry{background: none repeat scroll 0 0 #484848 !important; border:1px solid #666!important;  border-color: #181818 #666666 #666666 #181818 !important;-moz-border-radius:4px!important; 
    height: 68px!important; padding: 3px!important; width: 116px!important;}
img.dropdown-arrow{height:0px!important; width:0!important; overflow: hidden;border-color: #666 transparent transparent transparent !important; border-width: 5px 5px 0px 5px!important; border-style:solid!important}
.video-cluster-videos-container {-moz-border-radius:4px!important; width:140px !important;margin-left:-8px!important}
/*_________________________playlist/quicklist___________________________*/
#quicklist-bar-container, #quicklist-bar-container *:not(img){border-color: #333!important}
#quicklist-bar-container{border-top: 1px solid #282828!important}
#quicklist-bar button, #quicklist-bar button *{text-shadow: 0px 0px 0px #333!important; color:#888!important}
 .quicklist-item .title span{color:#666!important}
#quicklist button.quicklist-tray-button, #watch-channel-discoverbox .yt-uix-slider-next, #watch-channel-discoverbox .yt-uix-slider-prev{background:-moz-linear-gradient( left , #282828,#353535, #282828) #666 !important;-moz-box-shadow: inset 1px 1px 3px #383838!important}
 button .yt-uix-slider-prev-arrow {border-color:transparent #777 transparent transparent!important;border-width:9px 9px 9px 0!important;}
 button .yt-uix-slider-next-arrow {border-color:transparent transparent transparent #777!important;border-width:9px 0px 9px 9px!important;}
 button:hover .yt-uix-slider-prev-arrow {border-color:transparent #C72626 transparent transparent!important;}
 button:hover .yt-uix-slider-next-arrow {border-color:transparent transparent transparent #C72626!important;}
#quicklist-title-button .yt-uix-button-arrow, .yt-uix-button.yt-uix-button-reverse .yt-uix-button-arrow{border-color:transparent transparent #666 transparent !important;border-width:0px 6px 6px 6px!important;}
div.yt-uix-button-menu li span:hover{background-color: #333!important}
/*_________________________spotlight / trends___________________________*/
.main-tabs-spotlight-inner, .hot-trends-cloud-area{height: 1.3em!important; line-height:1.3em!important; overflow:hidden!important; width: 5.8em!important; background-color:#555!important;}
.main-tabs-spotlight-inner .featured-title, .hot-trends-cloud-area .hot-trends-title{margin:-2px 0 0 0!important; cursor:default}
.main-tabs-spotlight-inner:hover, .hot-trends-cloud-area:hover{height: auto!important; padding-bottom:9px!important; padding-right:10px!important; width: auto!important}
.hot-trends-cloud-area{margin-bottom: 10px!important}
.main-tabs-spotlight .video-entry, .hot-trends-cloud-inner-area {background-color:#444!important}
.hot-trends-cloud-inner-area a{color:#aaa!important}
.feedmodule-single-form-item .feeditem-grid .grid-item .x-box{top:auto!important; right: auto!important; margin: 0 0 0 140px!important}
/*_________________________music & categories___________________________*/
.artist-image{background-color: #484848 !important; padding: 4px!important; -moz-border-radius: 3px!important;border:1px solid #666!important; border-color: #181818 #666 #666 #181818 !important}
.shmoovies-list-container #popular-column .artist-cell{height: 160px!important; overflow:hidden!important}
.shmoovies-list-container #popular-column  .artist-image{max-height: 90px!important}
.shmoovies-list-container .artist-cell a span{margin:0 !important}
.shmoovies-list-container .artist-cell .artist-video{margin:0 !important; font-size: .9em!important}
.artist-image[src*="no_videos_140"]{opacity: .4!important}
#artist-videos .album-row{border: 1px solid transparent!important}
#artist-videos li:nth-child(odd){background-color:rgba(128,128,128,.1) !important;}
#artist-videos{width: 634px!important; margin-left: 2px!important; }
#artist-videos .artist-album-module{ -moz-box-shadow: 0px 2px 1px rgba(0,0,0,.6), -1px 0px 1px rgba(255,255,255,.2)  !important; -moz-border-radius: 3px!important;padding: 1px 2px 2px 3px!important}
/*_________________________channel-fixes___________________________*/
a.view-button-selected, a.view-button:hover {background-color:#666!important;}
a.view-button-selected .contents div, a.view-button .contents div{background-color:#111!important;border-color:#888!important;}
a.view-button-selected .contents div.tri, a.view-button .contents div.tri{background-color:#777!important;border-top-color:#111!important;border-bottom-color:#111!important;}
/*_________________________complete tabel___________________________*/
#completeTable{ -moz-box-shadow: 1px 2px 2px #111!important;  -moz-border-radius-bottomright: 5px!important;}
#completeTable .yt-suggest-selected{background:#888!important}
#completeTable .yt-suggest-selected *{color: #333!important}

#watch-info{padding: 2px 0px 0px 4px!important; overflow:hidden!important}
#watch-description{overflow:visible!important}
#watch-description .yt-uix-expander-head{ margin: 0 0px 0 -4px!important; }

#watch-description-extra-info{margin-left:467px!important}
/*_________________________various___________________________*/
#watch-video-container{background:#222!important;-moz-box-shadow:inset 0px -2px 2px #484848,inset  0px 2px 3px #111!important; padding: 3px 0px!important}
.playlist-badge, .yt-badge-std{background: #555 none !important; color: #aaa!important; -moz-box-shadow: 1px 1px 2px #111}
.video-thumb{background: #484848 none !important;border-color: #181818 #666 #666 #181818 !important}
.user-thumb-xlarge, .user-thumb-large, .user-thumb-medium, .user-thumb-semismall,.music-onebox-channel-thumbnail{background: #484848 none !important;border-color: #181818 #666 #666 #181818 !important;-moz-border-radius:3px!important; border-style: solid!important; border-width: 1px!important; padding: 3px!important }
.user-thumb-large>div{width: 60px!important; height:60px!important; overflow:hidden!important; margin:0px!important; float: none!important}

.yt-uix-expander-collapsed #watch-description-rounded-top {-moz-border-radius-bottomleft:5px!important;-moz-border-radius-bottomright:5px!important;border-bottom:1px solid #555!important;}
#masthead-search-term{-moz-border-radius:5px!important; border: 1px solid #606060!important; width: 95%!important}
#masthead-subnav li a {color:#A69B7C !important; text-shadow: 1px 1px 2px #181818!important}
#masthead-subnav li.selected a {color:#aaa !important; text-shadow: 2px 1px 2px #111!important}
#yt-admin-content{-moz-box-shadow:inset 1px 0px 0px #666}
#watch-actions-area, textarea.comments-textarea, #watch-info.expanded, .comment.current, .music-onebox, button.yt-uix-pager-selected, #masthead-search-term, #watch-embed-code, input[type*="text"], input[type="password"], #watch-channel-discoverbox, textarea, .edu-about-bar, #vm-videos-search-form>input, input[type="checkbox"], input[type="radio"]{-moz-box-shadow:inset 1px 1px 2px #111!important}
#watch-info.expanded{ border-bottom: 1px solid #555!important ; border-right: 1px solid #555!important}
div.yt-uix-tooltip-tip-content, .dialog { -moz-box-shadow: 1px 1px 2px #111!important}
.browse-tab-modifiers.yt-rounded{-moz-box-shadow:inset 0px -2px 2px #282828!important}

.dialog, .ab-subscribe{-moz-border-radius:5px!important;}

#edu-search-header {-moz-border-radius:8px!important;-moz-box-shadow: -1px -1px 2px #111, 1px 1px 2px #454545!important; }
.main-tabs-promo img{ padding: 5px!important; background: #484848!important; border: 2px solid #666!important; -moz-border-radius:5px!important; border-color: #181818 #666 #666 #181818!important}
#insightBox img, #view-toggle img{opacity:.6!important;}
.yt-uix-tooltip-tip-arrow, .yt-uix-tooltip-tip-reverse .yt-uix-tooltip-tip-arrow{vertical-align: top!important;	border: 1px solid transparent!important; border-width: 5px 5px 0 5px !important; border-top-color: rgba(80,80,92,0.9)!important;}
.yt-uix-button-arrow{vertical-align: middle!important; border: 1px solid transparent!important; border-width: 5px 5px 0 5px !important; border-top-color: #666!important;}
button[data-button-menu-id="shared-addto-menu"] img.yt-uix-button-arrow, #comments-actions img.yt-uix-button-arrow{vertical-align: middle!important; border: 1px solid transparent!important; border-width: 5px 5px 0 5px !important; border-top-color: #888!important;}
/*▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓display none▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓*/
#homepage-side-content, .img-autoshare-logo, #songkick-attribution, body>.goog-scrollfloater, .rss-link-container, #watch-userbanner, #user_banner, .yt-uix-slider-shade-left, .yt-uix-slider-shade-right, .watch-extra-info *, .watch-extra-info, #watch-branded-actions, *[class*="signup-promo"], #user_fullwidth_gadget, #watch-description-fadeout
{display:none!important}

#quicklist-help .empty img, img.yt-uix-button-icon-transcript{opacity: .2!important}
.ux-thumb-26, .ux-thumb-26 .clip, .ux-thumb-26 img{opacity: .75!important}
/*▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓size/position/ floats etc▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓▓*/
body {height:auto!important; min-height: 100%!important}
#homepage-main-content{width: auto!important}
.video-cell[style*="width: 24.5%"]{width: 153px!important}
.feeditem-compressed{width: 32.9%!important}
.ab-subscribe{margin-top: 12px!important}
#composeform>table{width: 66%!important}

#quicklist{left:0px!important}
.video-list-item{padding: .5em 0px!important; margin: 0px!important}
.video-list-item.watch-ppv-vid{padding: 0px!important; margin: 0px!important}
.video-entry img.img-php-close-button, .video-entry img.img-php-always-active-close-button{margin:2px 0px 0px -6px!important; border:1px solid rgba(255,0,0,0)!important}
/*_________________________video player___________________________*/
#content{background: transparent none!important;}
#content #watch-player{width:960px!important; height: 570px!important}
#content #watch-sidebar{margin-top:0px!important; padding-top:4px!important}

/*_________________________v.browseAdditional - EDUcation etc___________________________*/
.browseAdditional {margin: 0px 0 10px 0!important;}
.browseAdditional .yt-uix-slider-body{background: transparent!important; margin: 20px 0 0px 45px!important}
