img.video-logo[src*="badge_cc"]{background-position:-49px -683px!important; padding-left: 18px!important;width: 0!important;}
.vm-drag-handle {background: rgba(0,0,0,.4)!important; overflow:hidden!important}
.vm-drag-handle:before{content: "↕"; line-height: 88px; padding-left: 2px; color: #585858}
.playlist-cell .playlist-contents li{background: transparent!important; list-style: disc!important; padding-left:0px!important; margin-left: 16px!important; color: #666!important}
/*_________________________custum background image positions___________________________*/
img.master-sprite.icon-RAT, img.master-sprite.icon-REC, img.master-sprite.icon-FAV, img.master-sprite.icon-PRO, img.master-sprite.icon-POP, img.master-sprite.icon-UPL{width: 20px!important; height: 20px!important}
img.master-sprite.icon-RAT{background-position: 0px -40px!important}
img.master-sprite.icon-REC{background-position: -20px -40px!important}
img.master-sprite.icon-FAV{background-position: -40px -40px!important}
img.master-sprite.icon-PRO, img.master-sprite.icon-POP, img.master-sprite.icon-UPL{background-position: -60px -40px!important}
.section.selected .bullet {border-color:#888 transparent transparent transparent!important}
.section .bullet {border-color: transparent transparent transparent #888!important}
/*_________________________mainback___________________________*/
body, #page{background:url('http://i.imgur.com/7Prf6.jpg') #333  top!important;}
/*_________________________mastersprite___________________________*/
button img:not([class*="arrow"]), img.master-sprite, button[class*="arrow"],img[alt="Search options"], img.close-button, img.watch-check-grn-circle, .search-option-expander-link img, .close.master-sprite{opacity: .3!important;}
.yt-uix-clickcard-close, .yt-help-icon, .tiny-video-icon,.vm-video-badge{opacity: .45!important;}
button img.yt-uix-button-icon-addto{opacity: 1!important;}
