button img.yt-uix-button-icon-quicklist-toggle{opacity: 1!important;}
.yt-uix-button-content img[alt*="Show"]{background-color: rgba(255,255,255,1)!important; -moz-box-shadow: -1px -1px 1px rgba(0,0,0,.7), 1px 1px 0px rgba(255,255,255,.6)!important}

/*_________________________loader gif___________________________*/
