css:
@namespace url(http://www.w3.org/1999/xhtml);      @-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') 


 {#dashboard_index{background: url('http://i56.tinypic.com/2e363hj.jpg') center right #ffffff repeat fixed !important;}
body {background: url('http://i56.tinypic.com/2e363hj.jpg') center right #ffffff repeat fixed !important; opacity: .9; }  *{text-shadow:1px !important; letter-spacing: -1px;}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#F7F2CF !important; opacity:.9;}
ol#posts li.notification{background-color: #F7F2CF !important; border-bottom: 1px solid #ffd88a !important; color: #000000 !important; letter-spacing: -1px;}
#right_column a#dashboard_switch_blog_menu_current:hover {background:#150c18;}
#nav .nav_item.active{background-color:#6d2f0d;}
#left_column #posts .post .permalink{background-color:#150c18; letter-spacing: -1px;}
#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#6d2f0d !important;
background-image:none !important;
border:0px !important}
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{background-image:none !important}
.notification_type_icon{display:none !important}
#left_column #posts .notification {
background-color:#F7F2CF !important;
color:#5A6155 !important;
border-bottom-color: #c0c0c0  !important}
#new_post_notice_container{
color:#2B3226 !important}
#new_post_notice_container a{
color:#2B3226 !important}
ol#posts li.post.new_post {
background: #FF8F8F url('http://i56.tinypic.com/n1cebo.png') center center no-repeat;}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:70px;
    padding-right:250px;
    opacity: 0;
}
ol#posts .post.new_post .new_post_label {
display:none !important;
}


}
