css:
@-moz-document 
domain("news.ifeng.com"),
domain("finance.ifeng.com"),
domain("ent.ifeng.com"),
domain("sports.ifeng.com"),
domain("fashion.ifeng.com"),
domain("auto.ifeng.com"),
domain("house.ifeng.com"),
domain("home.ifeng.com"),
domain("tech.ifeng.com"),
domain("edu.ifeng.com"),
domain("culture.ifeng.com")
{

/*去除右侧边栏以及广告等*/
.right,
.searchDiv02,
.links,
#Comment,
.pic600,
.adarea2,
.label_2,
.scrollPic,
.chaFotNav,.chaFotNav02,.chaFooter,
#artical_sth,#artical_sth2,#artical_sth3,
#comment2,
.games_right_side,
.ad08, /*新闻频道顶部广告栏*/
.ad1000, /*健康频道顶部广告栏*/
div[class="w1000 tg top-tg"], /*汽车*/
div[style=" width:1000px; height:90px; margin:0 auto; clear:both;"] /*家居*/
,.blockL/*网罗天下*/
,.ss_none/*评论栏下方区域*/

{display:none !important;}

/*设定页面主体颜色*/
body {background-color:#f0f0f0 !important;}

/*调整阅读区域*/
.left,#artical{width:1000px !important;}
#artical_topic {
  width: 1000px !important;
  font: bold 32px 文泉驿微米黑,黑体 !important;
  text-align: center;}
#artical_real {width:960px !important;padding:16px !important;}
#artical_real p,p[align="left"] span{
  text-indent: 2em !important;
  font-size: 26px !important; 
  line-height: 1.5em !important;
  font-family: 文泉驿微米黑,黑体 !important;}
#artical_real .pic_box{
  margin-left:180px !important;}
#artical_real .picIntro span {
  font-size: 20px !important;}
p strong{
  font-size: 26px !important; 
  line-height: 1.5em !important;
  font-family: 方正宋黑_GBK,黑体 !important;
  color:#A14242;}
p span { /*引文*/
  line-height: 1.4em !important;
  font-family: 仿宋 !important;}

/*统一翻页背景与主体背景*/
.next{background:none !important;}
.an {background: none no-repeat scroll center bottom #f0f0f0 !important;}

/*使网页中的视频居中*/
.video_box{margin-left:200px !important;}

}

@-moz-document domain("sports.ifeng.com") {
/*单独修复体育栏目的一些细节*/
.boxR/*独家评论*/
,.dbox01 /*微信二维码*/
{display:none !important;}
.boxL { width: 100% !important; }
#slidedesc2 p { background-color: none !important; }
}
