css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

.fbChatOrderedList li:not(.active):not(.idle) {
 opacity: .4;
}

.fbChatOrderedList li:not(.active):not(.idle) .clearfix {
 padding-left: 43px !important;
 height: 20px;
}

.fbChatOrderedList li:not(.active):not(.idle) .name {
 line-height: 20px !important;
 }

.fbChatOrderedList li:not(.active):not(.idle) .pic {
 display: none;
 }

}
