@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


/*
By banthaz
Version 1.0.1 (Jul 27 2011)
*/


/***************************** THE BUTTON *****************************/


/*********************** MAIN ***********************/


/* Main */
#main-window #appmenu-button
{
width: 105px !important;
background: bottom !important;
box-shadow: none !important;
border: none !important;
border-radius: 0 !important;
}


/* Maximized window */
#main-window[sizemode="maximized"] #appmenu-button
{
margin-left: 2px !important;
}





/********************** IMAGES **********************/


/* Normal  */
#main-window #appmenu-button
{
background-image: url("data:image/png;base64,") !important;
}


/* Hover */
#main-window #appmenu-button:hover:not(:active):not([open])
{
background-image: url("data:image/png;base64,") !important;
}


/* Active */
#main-window #appmenu-button:hover:active,
#main-window #appmenu-button[open]
{
background-image: url("data:image/png;base64,") !important;
}


/* Inactive window */
#main-window:not([override]) #appmenu-button:-moz-window-inactive
{
background-image: url("data:image/png;base64,") !important;
}






/****************************** THE LABEL *****************************/


/* Old label - Remove */
#appmenu-button .box-inherit.button-box > *
{
display: none !important;
}


/* Create new label */
#main-window #appmenu-button
{
color: white !important;
font-size: 16px !important;
font-weight: 600 !important;
font-family: "Segoe UI" !important;
letter-spacing: 1.5px !important;
text-shadow: 1px  1px 0 rgb(83,86,102),
             1px -1px 0 rgb(83,86,102),
            -1px  1px 0 rgb(83,86,102),
            -1px -1px 0 rgb(83,86,102) !important;
}
#appmenu-button .box-inherit.button-box::before
{
position: fixed !important;
top: -2px !important;
left: 23px !important;
}
#main-window[sizemode="maximized"] #appmenu-button .box-inherit.button-box::before
{
top: 4px !important;
left: 26px !important;
}


/* New label text - Normal */
#appmenu-button .box-inherit.button-box::before
{
content: "Firefox" !important;
}


/* New label text - Private browsing */
#main-window[privatebrowsingmode=temporary] #appmenu-button .box-inherit.button-box::before
{
content: "Private" !important;
}
