css:
html, body {
	background:#EEEEEE;
	color:#000000;
	width: 100%;
}
* {
	font-family: "Trebuchet MS", Tahoma, Verdana, Arial, sans-serif;
	font-size: 10pt;
}
input, textarea, select {
	background-color: #DCDDDE;
	border: 1px solid #919191;
}

button {
	background-color: #DCDDDE;
	border: 1px solid #919191;
}

a {
	color: #FF6600;
}
a:hover {
	color: #0066FF;
}

.logo {
	clear:both;
	text-align:center;
	background:inherit;
	font-size:24pt;
	color:#000000;
	width:100%;
}
.replymode {
	background:#E04000;
	text-align:center;
	padding:2px;
	color:#FFFFFF;
	width:100%;
}
.postarea {
	background:inherit;
}
.postblock {
	background: #919191;
	border: 1px solid #000000;
	color: black;
	font-weight: bold;
	padding: 2px 5px 2px 5px;
}
.footer {
	text-align:center;
	font-size:10px;
	font-family:sans-serif;
}
.footer a { 
	font-size:10px;
}

.unkfunc {
	color:#789922;
}

.filesize {
	text-decoration:none;
}
.filetitle {
	background:inherit;
	color:#404040;
	font-weight:800;
}
.postername {
	display: none;
}
.postertrip {
	display: none;
}
.oldpost {
	background:inherit;
	color:#404040;
	font-weight:800;
}
.omittedposts {
	background:inherit;
	color:#070707;
}
.reply {
	background:#DCDDDC;
	color:#000000;
	border:1px solid #919191;
	padding-right: 5px;
	padding-bottom: 3px;
}
.replyhl {
	background:#BFBFBF;
	color:#000000;
}
.doubledash {
	vertical-align:top;
	clear:both;
	float:left;
}
.replytitle {
	background:inherit;
	font-size:18px;
	color:#FFFFFF;
	font-weight:800;
}
.commentpostername {
	background:inherit;
	font-size:11pt;
	color:#737373;
	font-weight: bold;
}
a.quotejs {
	color:#000000;
	text-decoration: none;
}
a.quotejs:hover {
	font-weight:bold;
}

.adminbarx {
	text-align:right;
	background:inherit;
	clear:both;
	float:right;
}

.adminbar {
	color: #999;
	text-align:right;
	clear:both;
	float:right;
	padding: 3px 5px 5px 5px;
	margin: 5px 5px 10px 5px;
	font-size: 12px;
}

.navbar {
	border: none;
	background: #DCDDDC;
	border-right: 1px solid #B7C5D9;
	border-bottom: 1px solid #B7C5D9;
	padding: 5px;
	margin-top: 3px;
	font-size: 12px;
	color: #999;
}

.footerbar {
	background: none;
	border: none !important;
	white-space: nowrap;
}

hr {
	border: none;
	border-top: 1px solid #999;
	height: 0;
	clear: both;
}

.highlight {
	background:#EEDACB;
	color:#333;
	border:2px dashed #E60;
}

#watchedthreads {
	background-color: #DCDDDC !important;
	border: 1px solid #919191 !important;
	border-top: 0px none !important;
}

.reflinkpreview {
	background-color: #DCDDDC !important;
}

.pages {
	border: none;
	background: #DCDDDC;
	border-right: 1px solid #B7C5D9;
	border-bottom: 1px solid #B7C5D9;
}

.pages td {
	border: none;
	padding: 1px 5px;
}

.pages td {
	color: #999;
}

.pages td b {
	color: #000;
}

.userdelete {
	margin-bottom: 10px;
}
