css:
@-moz-document regexp("^(https?://)?(zerkalo-|alt.)?rutracker.*") {
a[href="groupcp.php"],
a[href="info.php?show=copyright_holders"],
a[href="viewtopic.php?t=1045"],
a[href*="http://post.rutracker.org/forum/posting.php?mode=quote"],
a[href*="http://post.rutracker.org/forum/posting.php?mode=reply"],
#main-nav a[href="http://wiki.rutracker.org/"],
.thx-container,
form[action="thx.php"],
#tor-reged .row3.tCenter,
#latest_news,
.bottom_info,
#board_stats,
img.smile,
.poster_info .from,
.poster_info .avatar,
.poster_info .flag,
.signature,
#sidebar1,
#logo,
#page_footer,
td[style="width: 240px; vertical-align: top; padding-left: 2px;"] {
    display: none!important;
}
a.dl-stub {
    display: block!important;
}
#tor-reged table {
    border-collapse: separate!important;
    width: 100%!important;
}
.torTopic .tor-icon {
    overflow: hidden!important;
    display: block!important;
    float: left!important;
    margin: 0 6px 12px 0!important;
}
#page_header .topmenu {
    margin: 0!important;
}
.post_wrap {
    margin: 6px!important;
}
#forums_top_links {
    margin: 12px 12px 0!important;
}
.forumline.forum td,
#forums_wrap td {
    padding: 6px!important;
    line-height: 1.4!important;
}
#page_content {
    padding: 0!important;
}
#main-nav td.nowrap {
    color: transparent!important;
}
.forumline col.row1[width] {
    width: 85%!important;
}
.forumline col.row1[width="25%"] {
    width: 15%!important;
}
.forumline col.row4[width="75%"] {
    width: 85%!important;
}
.forumline .row2 {
    width: 5%!important;
}
}
