css:
/*
 * Darker NationStates Theme
 *
 * Author      : Northrop-Grumman
 * Updated     : September 24th, 2011
 * Website     : http://www.nationstates.net/
 * Description : Changes the colors on the forums to make it darker
 * Usage       : Use with the Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 * Contact me  : http://www.nationstates.net/northrop-grumman
 */
 
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nationstates.net") {

/*
-----------------------------------------------
Main
-----------------------------------------------
*/

/* Some extra background color at the bottom of every page */
body {
    color:white !important;
    background-color:black !important;
}

/* Some more extra left column extension color */
#main {
    background-color:#191919 !important;
}

/* Background and text */
#content {
    background-color:black !important;
    color:white !important;
}

/* Top banner */
#banner {
    background:#191919 none !important;
}

/* Conservative Theme Banner */
img[src*="/images/banner_af2010_con.png"]{
    width:0 !important;
    height:70px !important;
    padding-right: 572px !important;
    padding-bottom: 85px !important;
    background:transparent url(http://www.nationstates.net/images/banner3green.png) top left no-repeat !important;
}

/* Liberal Theme Banner */
img[src*="/images/banner_af2010_lib.png"]{
    width:0 !important;
    height:70px !important;
    padding-right: 572px !important;
    padding-bottom: 85px !important;
    background:transparent url(http://www.nationstates.net/images/banner3green.png) top left no-repeat !important;
}

/* Picture above left nav bar */
#createdby {
    display: none !important;
}

/* Left navigation bar */
#panel {
    background-color:#191919 !important;
	color:white !important; /* "Latest Forum Topics" */
}
#panel .menu a {
    color:white !important;
}

/* Bottom footer for Scroll to Top of Page */
#foot #toplink {
    background-color:#191919 !important;
}
#foot #toplink2 {
    background-color:#191919 !important;
    margin-left:194px;
}

/* Page footer */
#page-footer {
	background-color: black !important;
}

/* === Settings === */
/* Basic Settings */
table.settings {
	background-image:none !important;
	box-shadow:none !important;
	border-bottom:none !important;
}

/* Official Nation Name */
table.settings h2#officialname {
	font-size: 175% !important;
	font-family: 'Georgia' !important;
	padding-top: 20px !important;
	min-height: 20px !important;
}

/* Body text */
table.settings td {
	font-family: Verdana,Tahoma !important;
}

/* Paperclip Picture on Flag */
table.settings img.paperclip {
	display:none !important;
}

/* Flag on upper right hand corner of page */
table.settings div.clippedflagbox {
	display:none !important;
}

/* Input Boxes */
table.settings input, table.settings select {
	font-family: Verdana,Tahoma !important;
	padding: 0px !important;
}


/* === Region === */

/* World Factbook */
fieldset.wfe {
    background-color: #2A2A2A !important;
    color: white !important;
}
fieldset.wfe legend {
    background-color: #2A2A2A !important;
    color: white !important;
}

/* Embassies Pending and Cancelled */
.embassypending, .embassycancel { 
    background-color: #2A2A2A !important; 
    color: white !important;
}

/* = Regional Message Board = */

/* Older Messages Button */
.rmbolder { 
    border:1px #2A2A2A solid !important; 
    background-color: #191919 !important; 
}

/* Messages and borders */
.rmbrow { border:1px #1A1A1A solid !important;}
.odd { background-color: #191919 !important; }
.even { background-color: #2A2A2A !important; }

/* Date */
.rmbdate a, .rmbdate a:hover { color: white !important;}

/* Preview */
fieldset.rmbpreview {
    color: white !important;
    background-color: #2A2A2A !important;
}
fieldset.rmbpreview legend {
    color: white !important;
    background-color: #2A2A2A !important;
}

/* Submit Button */
input[type="submit"] {
    color:black !important;
    background-color:white !important;
	font-size: 100% !important;
}

	
/* === Tables === */

/* Table brackets */
table.shiny {
    background-color:#191919 !important;	
	-moz-box-shadow: 3px 3px 12px black !important;
    -webkit-box-shadow: 3px 3px 12px black !important;
}	

/* Table interior */
table.shiny td {
    background-color: #2A2A2A !important;
}


/* === News Page === */

/* Author */
#page_news .author {
    border: 2px solid #CCCCCC !important;
	background-color: #2A2A2A !important;
    color: white !important;
}

/* Block Quote */
#page_news blockquote {
    border: 2px solid #CCCCCC !important;
    background-color:#191919 !important;
    border-radius:8px;
}	


/* === The World === */

/* Featured Region */
legend {
    background-color: #2A2A2A !important; 
    border: 1px solid #CCCCCC !important;
    color: white !important;	
}
fieldset {
    border: 1px solid #CCCCCC !important; 
    background-color:#2A2A2A !important; 
    color: white !important;
}
.frdata { 
background-color: #191919 !important;
}
	

/* === World Assembly === */

/* WA Background Image */	
.UN {
    background-image:none !important;
}

/* WA Logo on Nation Page */
#panel .wamenu {
    background:none !important; 
}


/* === Miscellaneous === */	
	
/* Minor Infoboxes */
.minorinfo {
    color: white !important;
    border: solid #CCCCCC 1px !important;
    background-color: #2A2A2A !important;
}

/* Minor Infoboxes 2 */
.info {
    color: white !important;
    border: solid 2px #CCCCCC !important;
    background-color:#2A2A2A !important;
}

/* Bottom banner */
#footbar {
    background-color:#191919 !important;
}

/* 
-----------------------------------------------
Forums
-----------------------------------------------
*/

/* Top banner */
#nsbanner {
    background:#191919 none 100% 0% no-repeat !important;
}

/* Forum header titles */
h2 {
	color: white !important;
}

/* Sub-headers */
h3 {
	color: white !important;
}


/* === Forums === */
/* List/Row/Post/CP Colors */
.bg1	{ background-color: #191919 !important; } /* 2nd, 4th, 6th post colors and 1st, 3rd, 5th thread list colors */
.bg2	{ background-color: #1A1A1A !important; } /* 1st, 3rd, 5th post colors and 2nd, 4th, 6th thread list colors */
.bg3	{ background-color: #191919 !important; } /* Control panel color */

/* Main Forum Page color */
ul.forums { 
	background: #000000 !important;
} 

/* Text descriptions for Main Forum Page */
ul.topiclist li { 
	color: #FFFFFF !important; 
}

/* Column dividers for Main Forum Page */
ul.topiclist dd { 
	border-left-color: #FFFFFF !important;
} 

/* Highlighted Rows on forum page */
li.row:hover {
	background-color: #404040 !important;
}

/* Pages for each Thread in Thread Listing */
.pagination span a, .pagination span a:link, .pagination span a:visited, .pagination span a:active {
background-color: black !important;
color: grey !important;
}
	
	
/* === Threads/Posts === */

/* Post Profile */
.postprofile {
	color: white !important;
}
.postprofile strong {
	color: white !important;
}

/* Signature colors */
.postbody {
	color: #FFFFFF !important;
}

/* Post colors */
.postbody .content {
	color: #FFFFFF !important;
}

/* Quote colors */
blockquote {
	background: #1A1A1A none 6px 8px no-repeat !important;
	border: 1px solid #CCCCCC !important;
}

/* Top navigation bar colors */
.navbar {
	background-color: #191919 !important;
}

/* Horizontal line for top navigation bar */
ul.navlinks {
	border-bottom-color: #FFFFFF !important;
}

/* Lower border of main forum list */
.forabg {
	background-color: #191919 !important;
	background-image: none !important;
}

/* Lower border of subforum thread list */
.forumbg {
	background-color: #191919 !important;
	background-image: none !important;
}

/* Precode BBcode */
pre {
background-color: #2A2A2A !important;
}

/* Code box BBcode */
dl.codebox {
    background: #2A2A2A !important;
}

/* Polls */
fieldset.polls dl {
	color: white !important;
}
fieldset.polls dl.voted {
	color: white !important;
}
fieldset.polls dd div {
	color: #E6E6E6 !important;
}


/* === Corners === */
span.corners-top {
	background-image: none !important;
}
span.corners-top span {
	background-image: none!important;
}
span.corners-bottom {
	background-image: none!important;
}
span.corners-bottom span {
	background-image: none!important;
}


/* === Control Panel === */

/* Control panel colors */
.panel {
	background-color: #333333 !important;
	color: #FFFFFF !important;
}
#cp-main .panel {
	background-color: #2A2A2A !important;
}
#cp-main h2 {
	color: white !important;
}

/* All Tabs */
#tabs a {
	padding: 0 0 0 0px !important;
}

/* Inactive tabs */
#tabs a span {
	background: none no-repeat 100% 0px !important;
        background-color: #101010 !important;
	color: white !important;
}

/* Active tab */
#tabs .activetab a {
        background-color: #333333 !important;
	background-position: 0 0 !important;
	border-bottom: 1px solid #333333 !important;
}

#tabs .activetab a span {
        background-color: #333333 !important;
	color: white !important;
}

/* Friends List */
.cp-mini {
	background-color: #2A2A2A !important;
}
dl.mini dt {
	color: white !important; /* "Friends" */
}

/* Active Subsection */
#navigation #active-subsection a {
	color: white !important;
	background-color: #2A2A2A !important;
	background-image: none;
}

/* Non-active Subsection */
#navigation a {
	color: white !important;
	background-color: #303030 !important;
	background-image: none !important;
	background-repeat: no-repeat;
}


/* === Miscellaneous ===*/

/* Miscellaneous layout styles */
dl.details dt {
	color: white !important;
}
dl.details dd {
	color: white !important;
}
dt label {
	color: white !important;
}
label {
    color:white !important;
}

/* Allows Font to be Same Color as Background */
font[color="#F9F9F9"] {
color:#2A2A2A !important;
} 

/* Random jumpbox */
fieldset.jumpbox {
        background: black !important;
        border: black !important;
}

/* White space at bottom of certain pages */
html, body {
	background-color: black !important;
}

}
