css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("torrentz.eu") {
    /* -------------------- HTML -------------------- */
    html {
        background: fixed center top -moz-radial-gradient(center bottom, circle cover, #616161 0%, #000000 100%) !important;
    }
    
    /* -------------------- HEADER -------------------- */
    div.top {
        background: none !important;
    }

    div.top ul {
        background: none !important;
    }

    ul li a {
        background: none !important;
        border-bottom: none !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    ul li {
        border-left: none !important;
    }

    div.top h1 {
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.top h1 a:hover {
        color: #0099E0 !important;
    }

    div.top ul li a:hover {
        color: #0099E0 !important;
    }

    /* -------------------- REMOVING PUBS -------------------- */
    body > div > iframe {
        display: none !important;
    }

    /* -------------------- SEARCH DIV -------------------- */
    form.search {
        background: #4c4c4c !important;
        -moz-box-shadow: 10px 10px 5px #000000 !important;
    }

    form.search > fieldset {
        background: none !important;
    }

    form.search > fieldset > div {
        border-color: #4c4c4c !important;
    }

    form.search #thesearchbox {
        padding: 5px !important;
        color: #FFFFFF !important;
        background: #333333 !important;
        text-shadow: 4px 4px 4px #000000 !important;
        -moz-box-shadow:inset 6px 6px 10px #111111 !important;
    }
    
    form.search #thesearchbutton {
        color: #FFFFFF !important;
        text-shadow: 4px 4px 4px #000000 !important;
        background-image: -moz-radial-gradient(center, ellipse contain, #616161 0%, #000000 200%) !important;
        -moz-box-shadow: 4px 4px 5px #000000 !important;
    }

    form.search #thesearchbutton:hover {
        color: #0099E0 !important;
    }

    ul.autocomplete {
        border: none !important;
        color: #FFFFFF !important;
        background: #333333 !important;
        text-shadow: 4px 4px 4px #000000 !important;
        -moz-box-shadow: 10px 10px 5px #000000 !important;
    }

    ul.autocomplete > li {
        border-top: none !important;
        -moz-box-shadow:inset 0px 0px 10px #111111 !important;
    }

    ul.autocomplete > li.selected {
        background: #0099E0 !important;
        -moz-box-shadow:inset 6px 6px 10px #111111 !important;
    }

    /* -------------------- MIDDLE DIV -------------------- */
    body > div > h2 {
        margin-bottom: 10px !important;
        background: #616161 !important;
        color: #FFFFFF !important;
        text-shadow: 4px 4px 4px #000000 !important;
        -moz-box-shadow: 10px 10px 5px #000000 !important;
        background: -moz-radial-gradient(center bottom, circle cover, #616161 0%, #000000 100%) !important;
    }

    body > div > dl {
        background: none !important;
        border-bottom: none !important;
    }

    body > div > dl > dt {
       color: #0099E0 !important;
       text-shadow: 4px 4px 4px #000000 !important;
    }

    body > div > dl > dd {
        color: #FFFFFF !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    body > div > dl > dd > span.u {
        color: #aeff00 !important;
    }

    body > div > dl > dd > span.d {
        color: #0099E0 !important;
    }
    
    /* -------------------- CLOUD -------------------- */
    div.cloud > a {
        color: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    /* -------------------- GENERIC -------------------- */
    p.generic {
        color: #FFFFFF !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    p.generic > a {
        color: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    /* -------------------- RESULTS -------------------- */
    div.results {
        background: none !important;
    }

    div.results > div {
        color: #999999 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.results > div > b {
        color: #999999 !important;
    }

    div.results > div > a {
        color: #336699 !important;
    }

    div.results > div > a:hover {
         color: #0099E0 !important;
    }

    div.results > h2 {
        color: #FFFFFF !important;
        background: none !important;
        margin-bottom: 10px !important;
        border-bottom: none !important;
        text-shadow: 4px 4px 4px #000000 !important;
        -moz-box-shadow: 10px 10px 5px #000000 !important;
        background: -moz-radial-gradient(center bottom, circle cover, #616161 0%, #000000 100%) !important;
        overflow: visible !important;
    }

    div.results > h2 > a {
        color: #0099E0 !important;
    }

    div.results > h3 {
        color: #FFFFFF !important;
        background: none !important;
        margin-top: 10px !important;
        border-bottom: none !important;
        margin-bottom: 10px !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.results > h3 > a {
        color: #336699 !important;
    }

    div.results > h3 > a:hover {
        color: #0099E0 !important;
    }

    div.results > h3 > small {
        color: #999999 !important;
    }

    div.results > dl {
        background-color: transparent !important;
        border-bottom: none !important;
        margin-bottom: 1px !important;
    }

    div.results > dl > dd {
        color: #FFFFFF !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.results > dl > dt {
        color: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.results > dl > dt > a {
        color: #FFFFFF !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.results > dl > dt > a > b {
        color: #8a8a8a !important;
    }

    div.results > p {
        margin-top: 5px !important;
        color: #FFFFFF !important;
        border-top: none !important;
        -moz-box-shadow: 10px 10px 5px #000000 !important;
        background: -moz-radial-gradient(center bottom, circle cover, #616161 0%, #000000 100%) !important;
    }

    div.results > p > span > span {
        color: #FFFFFF !important;
        border: 1px solid #616161 !important;
        background: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
        -moz-box-shadow:inset 6px 6px 10px #111111 !important;
    }

    div.results > p > span > a {
        color: #FFFFFF !important;
        border: 1px solid #616161 !important;
        background: #333333 !important;
        text-shadow: 4px 4px 4px #000000 !important;
        -moz-box-shadow:inset 6px 6px 10px #111111 !important;
    }

    div.results > p > span > a:hover {
        color: #0099E0 !important;
    }

    div.results > p > span > strong {
        color: #0099E0 !important;
    }

    div.results > dl > dd > span.u {
        color: #AEFF00 !important;
    }

    div.results > dl > dd > span.d {
        color: #0099E0 !important;
    }

    /* COMMENT */
    div.comments {
        background: none !important;
    }

    div.comments > h2 {
        border-bottom: none !important;
        margin-bottom: 1px !important;
    }

    div.comments > div.comment {
        background-color: transparent !important;
        border-bottom: none !important;
        margin-bottom: 1px !important;
    }

    div.comments > div.comment > div.head {
        color: #999999 !important;
        background: none !important;
        border-left: none !important;
        margin-left: 1px !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.comments > div.comment > div.head > a {
        color: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.comments > div.comment > div.com { 
        color: #FFFFFF !important;
        border-left: none !important;
        margin-left: 1px !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.comments > div.comment > div.votes {
        color: #999999 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.comments > div.comment > div.votes > a.g {
        color: #AEFF00 !important;
    }

    div.comments > div.comment > div.votes > a.b {
        color: #d30000 !important;
    }

    /* -------------------- DOWNLOAD DIV -------------------- */
    div.download {
        background: none !important;
    }

    div.download > div {
        color: #999999 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.download > p {
        background-color: transparent !important;
        border-top: none !important;
        margin-top: 5px !important;
        color: #999999 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.download > p > a {
        color: #0099E0 !important;
    }

    div.download > h2 {
        border-bottom: none !important;
        color: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.download > h2 > span {
        color: #FFFFFF !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.download > dl {
        background-color: transparent !important;
    }

    div.download > dl > dt > a {
        color: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.download > dl > dt > a:hover {
        background: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
        -moz-box-shadow:inset 6px 6px 10px #111111 !important;
    }

    div.download > dl > dt > a > span.n {
        color: #FFFFFF !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.download > dl > dt > a > span.u {
        text-decoration: none !important;
        color: #FFFF00 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    /* -------------------- FEEDBACK -------------------- */
    div.feedback {
        background-color: transparent !important;
    }

    div.feedback > h2 {
        border-bottom: none !important;
    }

    div.feedback > p {
        background-color: transparent !important;
    }

    /* -------------------- TRACKERS -------------------- */
    div.trackers {
        background: none !important;
    }

    div.trackers > div {
        color: #999999 !important;
        text-shadow: 4px 4px 4px #000000 !important; 
    }

    div.trackers > p {
        background-color: transparent !important;
        border-top: none !important;
        margin-top: 5px !important;
        color: #999999 !important;
        text-shadow: 4px 4px 4px #000000 !important;        
    }

    div.trackers > p > a {
        color: #0099E0 !important;
    }

    div.trackers > h2 {
        border-bottom: none !important;
    }

    div.trackers > dl > dt > a {
        color: #FFFFFF !important;
    }

    div.trackers > dl > dd > span.a {
        color: #0099E0 !important;
    }

    /* -------------------- CONTENT FILE -------------------- */
    div.files {
        background: none !important;
    }

    div.files ul {
        background: none !important;
        border-left: none !important;
        margin-left: 1px !important;
    }

    div.files li > a {
        color: #FFFFFF !important;
        
    }

    div.files ul li span {
        color: #999999 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.files li.t {
        color: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.files > div {
        color: #999999 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.files > p {
        color: #999999 !important;
        background-color: transparent !important;
    }

    div.files > h2 {
        border-bottom: none !important;
        margin-bottom: 5px !important;
    }

    /* -------------------- USER -------------------- */
    div.user {
        color: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
        background: none !important;
    }
    
    div.user > ul {
        background-color: transparent !important;
    }


    div.user > h2 {
        margin-bottom: 1px !important;
        border-bottom: none !important;
    }

    div.user > p {
        background-color: transparent !important;
    }

    /* -------------------- INDEX STATS -------------------- */
    div.index-stats {
        background: none !important;
    }

    div.index-stats > p {
        color: #999999 !important;
        background-color: transparent !important;
        border-top: none !important;
        margin-top: 5px !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.index-stats > h2 {
        border-bottom: none !important;
        margin-bottom: 10px !important;
    }

    div.index-stats > div {
        color: #999999 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    /* -------------------- HELP -------------------- */
    div.help {
        color: #FFFFFF !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.help > p {
        color: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.help li > a {
        color: #0099E0 !important;
    }

    div.help li > a:hover {
        text-decoration: underline !important;
        color: #0099E0 !important;
    }

    div.help > a {
        color: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    /* -------------------- PROFILE PAGE -------------------- */
    form.profile {
        margin-bottom: 20px !important;
        -moz-box-shadow: 10px 10px 5px #000000 !important;
    }

    form.profile > fieldset {
        background-color: transparent !important;
        border: none !important;
        -moz-box-shadow:inset 6px 6px 10px #111111 !important;
    }

    form.profile > fieldset > label {
        color: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    form.profile > fieldset > input.i {
        border-color: transparent !important;
        color: #FFFFFF !important;
        background: #333333 !important;
        text-shadow: 4px 4px 4px #000000 !important;
        -moz-box-shadow:inset 6px 6px 10px #111111 !important;
    }

    form.profile > fieldset > legend {
        background-color: transparent !important;
        color: #FFFFFF !important;
        border: none !important;
        text-shadow: 4px 4px 4px #000000 !important;
        -moz-box-shadow: 10px 10px 5px #000000 !important;
        background: -moz-radial-gradient(center bottom, circle cover, #616161 0%, #000000 100%) !important;
    }

    form.profile > fieldset > p {
        color: #FFFFFF !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    form.profile > fieldset > p > a {
        color: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.recent {
        color: #FFFFFF !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.recent > a {
        color: #0099E0 !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    div.footer {
        color: #FFFFFF !important;
        text-shadow: 4px 4px 4px #000000 !important;
    }

    /* --------------------   -------------------- */
}
