css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.studivz.net") {



/*** layout ***/
li {color: #888 !important }

#content {
  border-color: #0a50b4 !important;
}

#topHeader {
 background-image: url(http://farm3.static.flickr.com/2241/1825424208_91bba6001e_o.jpg) !important;
  width:630px !important;
margin-left:-1px !important;
}

.commentMetaData,
.friendsInfo,
.pinboard_Navi,
.microblogInfo {
  background-color: #f5f5f5 !important;
  border-color: #eee !important;
}

#pageFooter {
  background-color: #5485cb !important;
  border-color: #0a50b4 !important;
}

#topNavi a,
#pageFooter a {
  color: #fff !important;
}

#topNavi a:hover,
#pageFooter a:hover {
  color: #0a50b4 !important;
  background-color:#fff !important;
}

#pageHeader {
  background-color: #5485cb !important;
  border-color: #0a50b4 !important;
}



/*** no ads ***/
#ad120x600 { display:none !important; }
#ad125x125 { display:none !important; }
#ad160x600 { display:none !important; }
#ad300x250 { display:none !important; }
#ad500x250 { display:none !important; }
#ad728x90  { display:none !important; }
#ad770x90  { display:none !important; }
#MismatchFooterBannerSnipplet { display:none !important; }



/*** links ***/
a { color: #0a50b4 !important; }

a.btnLikeLink:link,
a.btnLikeLink:visited,
a.btnLikeLink:hover,
a.btnLikeLink:focus,
a.btnLikeLink:active {
  color: #fff !important;
  background-color: #5485cb !important;
  border-color: #0a50b4 !important;
}

input.llbut {
  color: #0a50b4 !important;
}



/*** headlines ***/
h2, .mH {
  color: #fff !important;
  background-color: #5485cb !important;
  border-color: #0a50b4 !important;
}
h3 { color: #000 !important;}
h4 { border-color: #0a50b4 !important; }
.dialogueHeader { color: #000 !important; }


/*** red link list ***/

.linkList li {
  color: #0a50b4 !important;
  border-color: #0a50b4 !important;
}

.linkList a:hover {
  color: #fff !important;
  border-color: #0a50b4 !important;
  background-color: #5485cb !important;
}



}
