@-moz-document domain('tppcrpg.net'), url-prefix('http://tppcrpg.net') {
html,body{margin:0;padding:0; min-width:1024px;}

div#body{float:left;width:100%}
div#inner{margin: 0 200px 0 200px}
div#left{float:left;width:200px;margin-left:-100%}
div#right{float:left;width:178px;margin-left:-178px}
div#footer{clear:left;width:100%}

body {
	margin: 0px;
	font-family: Tahoma, Verdana;
	/*background-color: #EBE7DB;*/
	background-color: #FFF;
	min-width: 1024px !important;
}
#header {
	position: static;
	height: 100px;
	background-color: #2b2b2b !important;
	border-left: 1px solid #333333;
	border-right: 1px solid #333333;
	background-image:url('http://i55.tinypic.com/i6ku0z.png') !important;
	background-repeat: no-repeat;	
}
#tAoDp {
	position: static;
	height: 90px;
	text-align: center;
	background-color: #EBE7DB;
	border-top: 1px solid #F7F7EF;
	border-bottom: 1px solid #333333;
	border-left: 1px solid #333333;
	border-right: 1px solid #333333;
}
#tAoDp iframe {
	width: 728px;
	height: 90px;
}
#inner {
	padding: 3px;
	font-size: 0.8em;
}
#left ul, #right ul {
	list-style-type: none;
	padding: 0px;
	font-size: 0.8em;	
	line-height: 23px;
	background-color: #F3F0E9;
	margin: 0px;
}
#left ul {
	border: 1px solid #333333;
	border-top: 0px;
	margin-right: 20px;
}
li.h2 {
	background-color: #333333;
	text-align: center;
	font-weight: bold;
	color: #FFFFFF;
	clear: both;
}
li.h {
	background-color: #2b2b2b !important;
	height: 23px;
	line-height: 23px;
	color: white; 
	font-weight: bold;
	text-align: center;
	clear: both;
}
li.r1, li.r0 {
	border-top: 1px solid #F7F7EF;
	border-bottom: 1px solid #E5E2D9;
	height: 23px;
	padding-left: 20px;
	margin: 0px;
}
li.r1 a, li.r0 a {
	border-left: 1px solid #F7F7EF;
	display: block;
	height: 100%;
	margin: 0px;
	padding: 0px;
	padding-left: 3px;
}
li.r0 {
	background-color: #EBE7DB;
}
li.r0 a {
	background-color: #F3F0E9;
}
li.r1 {
	background-color: #E4E0D4;
}
li.r1 a {
	background-color: #ECEADE;
}
li.p0 {
	padding: 0px;
	text-align: center;
}
li.pb {
	background-color: #F3F0E9;
	text-align: center;
	line-height: 18px;
	padding-bottom: 13px;
}
li.hpSide {
	background-color: #80C1DD;
	border-bottom: 1px solid #333333;
	border-top: 1px solid #333333;
	height: 23px;
	margin: 0px;
	padding: 0px;
}
li.hpSide img {
	height: 23px;
	margin: 0px;
	padding: 0px;
}
li.pb img {
	padding: 10px;
}
li.rs {
	padding-top: 5px;	
	padding-bottom: 5px;	
}
li.rs div {
	padding-top: 5px;	
	padding-bottom: 5px;	
}
li.rs div img {
	padding: 4px;
	margin-left: 7px;
	margin-right: 6px;
	background-color: #EEFFBB;
	border: 1px solid #EBE7DB;
	cursor: pointer;
}
#right ul {
	border: 1px solid #333333;
	border-top: 0px;
}
#footer_clear {
	background-color: transparent;
	position: static;
	clear: both;
}
#footer {
	position: static;
	border-top: 1px solid #333333;
	font-size: 0.8em;
	color: #000000;
	background-color: #EBE7DB;
	height: 60px;
}
#tAoDp2 {
	float: right;
	width: 468px;
	height: 60px;
}
#footer p {
	margin: 5px;
}
#footer #sM {
	float: left;
	width: 60px;
}
#footer #sM div {
	background-color:#333333;
	color:white;
	float:left;
	text-align:center;
	width:60px;
	height:60px;
	font-size: 10px;
	border-right: 1px solid black;
}
#footer #sM div a {
	font-size:2.5em;
	display: block;
	line-height: 48px;
	height: 48px;
	color: #FFFFFF;
}
#header ul {
	position: absolute;
	background-color: #333333;
	top: 0px;
	right: 0px;
	list-style-type: none;
	margin: 0px;
	padding: 0px;
	padding-left: 15px;
	font-size: 0.8em;
	font-weight: bold;
	border-bottom: 1px solid #EBE7DB;
	border-left: 1px solid #EBE7DB;
}
#header a {
	font-weight: bold;
	color: white;
	text-decoration: none;
}
#header a:hover {
	text-decoration: underline;
}
#header ul li {
	float: left;
	margin: 0px;
	margin-right: 15px;
	padding: 3px;
	color: white;
}
#switchSidebar {
	position: absolute;
	border-bottom: 1px solid #333333;
	/* z-index: 3; */
	width: 176px;
	background-color: #333333;
	color: white;
	background-repeat: no-repeat;
	background-position: 100px 25px;
	opacity: 0;
}
#switchSidebar div {
	color: white;
	background-color: #2b2b2b !important;
	font-weight: bold;
	text-align: center;
}
#switchSidebar p {
	margin: 0px;
	padding: 3px;
	line-height: 22px;
}
#switchSidebar p a {
	color: white;
	text-decoration: none;
	cursor: pointer;
}
#switchSidebar p.c {
	text-align: center;
}
h2, h3 {
	text-align: center;
}
.center {
	text-align: center;
}
label {
	font-weight: bold;
}
label.nb {
	font-weight: normal;
}
.notice, #cBox {
	background-color: #EEFFBB;
	border: 3px solid #333333;
	padding: 5px;
	margin: auto;
	text-align: center;
	width: 400px;
}
.notice {
	width: 500px;
}
.notice img.p, .error img.p, .success img.p {
	float: right;
}
.submit {
	background-color: #333333;
	border: 0px;
	color: white;
	padding: 5px 21px;
}
.error {
	background-color: #EEFFBB;
	border: 3px solid #2b2b2b !important;
	padding: 5px;
	margin: auto;
	text-align: center;
	width: 500px;
}
.success {
	background-color: #EEFFBB;
	border: 3px solid #44AA00;
	padding: 5px;
	margin: auto;
	text-align: center;
	width: 500px;
}
.account_note {
	background-image: url('http://graphics.tppcrpg.net/hgss/normal/144M.gif');
	background-repeat: no-repeat;
	background-position: right center;
	background-color: #F3F0E9;
	border: 3px solid #80C1DD;
	padding: 5px;
	margin: 5px auto;
	margin-bottom: 20px;
	max-width: 750px;
	min-width: 620px;
}
.account_note div {
	position: absolute;
	margin-top: -20px;
	background-color: #80C1DD;
	color: #FFF;
	padding: 5px;
}
.account_note p, .maintenance p {
	text-align: center;
	margin: 5px 120px 0px 120px;
}
.maintenance {
	background-image: url('http://graphics.tppcrpg.net/hgss/normal/068M.gif');
	background-repeat: no-repeat;
	background-position: right center;
	background-color: #F3F0E9;
	border: 3px solid #2b2b2b !important;
	padding: 5px;
	margin: 5px auto;
	margin-bottom: 20px;
	max-width: 750px;
	min-width: 620px;
}
.maintenance div {
	position: absolute;
	margin-top: -20px;
	background-color: #2b2b2b !important;
	color: #FFF;
	padding: 5px;
}
#inner .r0 {
	background-color: white;
}
#inner .r1 {
	background-color: #F3F0E9;
}
.b {
	font-weight: bold;
}
#blackout {
	position: fixed;
	top: 0px;
	left: 0px;
	height: 100%;
	width: 100%;
	background-color: black;
	z-index: 5000;
}
#cBox {
	z-index: 5001;
	position: fixed;
	left: 50%;
	top: 50%;
	margin-left: -200px;
	font-size: 0.8em;
	text-align: left;
}
#inner table {
	width: 100%;
}
.w50 {
	width: 50%;
}
img {
	border: 0px;
}
.cb {
	clear: both;
}
.pages select, .Pages select {
	float: right;
}
.green {
	color: #44AA00;
}
.red {
	color: #2b2b2b !important;
}
.disabled {
	color: #C0C0C0;
}
.silph {
	background-image:url('http://graphics.tppcrpg.net/buildings/mini-silphco.gif');
	background-repeat: no-repeat;
	background-position: right center;
}
.nolog {
	background-color: #EFEFEF;
	text-align: center;
	font-weight: bold;
	font-size: 1.2em;
	margin-top: -3px;
	border: 1px solid black;
	border-top: 0px;
	color: white;
	padding: 3px;
}
.nolog a {
	color: #333333;
	text-decoration: none;
	float: left;
	width: 49%;
}
.nolog a:hover {
	text-decoration: underline;
}

.ranks {
	width: 100%;
}
.ranks th, .ranks td {
	text-align: center;
}
.ranks th {
	background-color:#2b2b2b !important;
	border: 1px solid #333333;
	color:white;
	font-weight:bold;
	line-height: 23px;
}
.ranks th a {
	color: white;
}
.facrew td {
	line-height: 23px;
}
div.page {
	margin-top: 5px;	
	margin-bottom: 5px;	
}
div.page select {
	float: right;	
}
.saleType {
	width: 400px;
	margin: auto;
}
.saleType .fl {
	float: left;
}
.dark {
	background-color: #333333 !important;
}
.ranks tr.small {
	font-size: 0.8em;
}
.ranks tr.note {
	background-color: #333333 !important;
	color: white;
	font-weight: bold;
}
}

