css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('www.linux.org.ru') 

 {

html{
background-color: rgb(36,37,37) !important;
}

div[class=sign]{
position:relative; left: 0; text-align:justify  !important  }

div[class=sign] a, a:visited{
text-decoration: none !important}

div[class=sign] a{
color:#8ae234 !important}

body{box-shadow: 0px -10px 0px 10px black !important;
border-top-left-radius: 10px !important;
border-top-right-radius: 10px !important;
background-color: rgb(36,37,37) !important;
background: -moz-radial-gradient(center top , circle farthest-side, rgb(92, 92, 92) 0%, rgb(36,37,37) 370px) ,rgb(36,37,37) !important;
}

a{
color:#729fcf !important;
text-shadow: 0px -1px 0px rgba(0,0,0,0.6) !important;
transition: 0.3s ease-in-out !important;
text-decoration: none !important}

a:hover{
text-shadow: 0px 0px 2px #29d !important;
transition: 0.3s ease-in-out !important}

/*a:visited{
color: #729fcf !important;}*/

.msg {
color: rgba(255,255,255,0.5) !important;
text-shadow: 0px -1px 0px rgba(0,0,0,0.6) !important;
padding: 10px !important;
background: url("http://i.imgur.com/QEHf4.png") !important;
background-color: rgba(36,37,37, 0.8) !important;
box-shadow: 0px 2px 5px -1px black inset !important;
border: solid #111 0px !important
}

.msg .userpic {
opacity: 0.1 !important;
transition: 0.5s linear !important;
}

.msg .userpic:hover {
opacity: 1 !important;
transition: 0.5s linear !important;
}

.userpic img {
margin-left: 3px;
border: 1px solid black !important;
border-radius: 5px !important;
}

.msg:target {box-shadow: 0px 0px 2px 2px #088 inset !important;
box-shadow: 0px 0px 6px cyan !important;
border: 0px !important;}

.infoblock {background-color:rgba(68,67,67,0.4) !important;
box-shadow: 0px 0px 3px 0px black inset !important;}

.infoblock.mini-news {box-shadow: 0px 0px 3px 0px black inset, 0px 1px 1px 0px blue inset !important;}

.boxlet {background-color:rgba(68,67,67,0.4) !important;
box-shadow: 0px 0px 3px 0px black inset !important;}

#bd{
text-shadow: 0px -1px 0px rgba(0,0,0,0.4) !important;
}

#bd .forum table tbody tr:hover {background-color: transparent !important;}

#bd .forum table thead {background-color:transparent !important;
border: none !important;
border-radius: 5px !important;}

#bd .forum table tbody {background-color:transparent !important;
border: none !important;
}

#bd .forum table th {background-color:rgba(0,0,0,0) !important;
box-shadow: 0px 4px 6px -4px black inset !important;
border: none !important;
border-color:transparent !important;
border-radius: 6px !important;
}

#bd .forum table td{background-color:rgba(0,0,0,0) !important;
border-color:rgba(0,0,0,0) !important;
border: none !important;
box-shadow: 0px 0px 6px -4px black inset !important;
border-radius: 3px !important;}

#dw {
box-shadow: 0px 0px 0px 0px black !important;
border-top-left-radius: 0px !important;
border-top-right-radius: 0px !important;
background-color: transparent !important;
background: transparent !important;
color:black !important;
} 

}
