css:
@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix('https://www.tumblr.com/dashboard')   {

#dashboard_index{background: url(http://i.imgur.com/vbKOp.png) top left !important}
#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content { background:#ab100b !important; border:5px #f2ce38 solid}
#dashboard_index #posts {background:transparent !important}
#dashboard_index #logo {width:0px !important; height:0px !important; padding-left:330px !important; padding-top:70px !important; background-image:url(http://i.imgur.com/hk3Xf.png) !important}
#dashboard_index .nipple {display:none}
#dashboard_index .controls_section.selected.following {background:transparent !important}
#dashboard_index .notification {background:#f2ce38 !important}
#dashboard_index #right_column li {background:#f2ce38 !important; opacity:0.7}
#dashboard_index #right_column li.selected {opacity:1.0}
#dashboard_index #right_column li:hover {opacity:0.9}
#dashboard_index #right_column li.forever_alone {opacity:1.0}

/*SELECTION ARROW begin - points to dashboard selection*/
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px #f2ce38 !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}
/*SELECTION ARROW end*/

/*RADAR begin*/
.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
  border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}

/*RADAR end*/

/*NOTIFICATION ARROW begin*/
.nipple {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px #f2ce38 !important;
  border-bottom: 6px solid transparent !important;
  border-left: 0px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 0px !important;
  margin-left: 0px !important;
}
.nipple.border {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px rgba(104,79,74, 1.0) !important;
  border-bottom: 6px solid transparent !important;
  border-left: 0px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 1px !important;
  margin-left: 0px !important;
}
/*NOTIFICATION ARROW begin*/

/*HEART begin*/
a[class^="like_button"], .notification_type_icon.like_icon {background-image: none !important;}
a[class^="like_button"]:before,
a[class^="like_button"]:after,
.notification_type_icon.like_icon:before,
.notification_type_icon.like_icon:after {
  position: absolute !important;
  content: "" !important;
   left: 10px !important;
  top: 0 !important;
  width: 10px !important;
  height: 15px !important;
  background: #dd952b !important;
  -moz-border-radius: 50px 50px 0 0 !important;
  border-radius: 50px 50px 0 0 !important;
  -moz-transform: rotate(-45deg) !important;
  -moz-transform-origin: 0 100% !important;
}
a[class^="like_button"]:before,
a[class^="like_button"]:after {
  margin-top: 16px !important;
  margin-left: 503px !important;
}
.notification_type_icon.like_icon:before,
.notification_type_icon.like_icon:after {
  margin-top: -1px !important;
  margin-left: -5px !important;
}
a[class^="like_button"]:after,
.notification_type_icon.like_icon:after {
  left: 0 !important;
  background: #dd952b !important;
  -moz-transform: rotate(45deg) !important;
  -moz-transform-origin: 100% 100% !important;
}
/*HEART end*/

/*FOLLOWING HEAD begin*/
.notification_type_icon.follow_icon {
  background-image: none !important;
}
.notification_type_icon.follow_icon:before {
    position: absolute !important;
    content:"" !important;
    background: #dd952b !important;
    left:4px !important;
    width:14px !important;
    height: 5px !important;
    margin-top: 8px !important;
    margin-left: -4px !important;
    border-top-right-radius: 4px !important;
    border-top-left-radius: 4px !important;      
}
.notification_type_icon.follow_icon:after {
    position: absolute !important;
    content:"" !important;
    background: #dd952b !important;
    left:9px !important;
    width:6px !important;
    height:10px !important;
     margin-top: 0px !important;
    margin-left: -5px !important;
    -moz-border-radius:5px 5px 5px 5px / 5px 5px 10px 10px !important;
    border-radius:5px 5px 5px 5px / 5px 5px 10px 10px;
}
/*FOLOWING HEAD end*/}

/*Special thanks to ohnemitleid.tumblr.com for the selectors! */
